import React, { useState, useEffect } from 'react';
import { 
  Row, 
  Col, 
  Card, 
  Input, 
  Button, 
  List, 
  Typography, 
  message, 
  Modal,
  Form,
  InputNumber,
  Space,
  Tag,
  Divider
} from 'antd';
import { 
  PlusOutlined, 
  MinusOutlined, 
  DeleteOutlined,
  ShoppingCartOutlined,
  PrinterOutlined,
  SearchOutlined
} from '@ant-design/icons';

import { productsAPI, ordersAPI } from '../services/api';
import { formatCurrency } from '../utils/format';

const { Title, Text } = Typography;
const { Search } = Input;

function POSPage() {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [cart, setCart] = useState([]);
  const [loading, setLoading] = useState(false);
  const [orderModalVisible, setOrderModalVisible] = useState(false);
  const [customerForm] = Form.useForm();

  useEffect(() => {
    loadProducts();
  }, []);

  const loadProducts = async () => {
    try {
      setLoading(true);
      const response = await productsAPI.getAll();
      if (response.data.success) {
        setProducts(response.data.data);
        setFilteredProducts(response.data.data);
      }
    } catch (error) {
      message.error('Lỗi khi tải danh sách sản phẩm');
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = (value) => {
    const filtered = products.filter(product =>
      product.name.toLowerCase().includes(value.toLowerCase()) ||
      product.sku?.toLowerCase().includes(value.toLowerCase()) ||
      product.barcode?.toLowerCase().includes(value.toLowerCase())
    );
    setFilteredProducts(filtered);
  };

  const addToCart = (product) => {
    if (product.quantity <= 0) {
      message.warning('Sản phẩm đã hết hàng');
      return;
    }

    const existingItem = cart.find(item => item.product_id === product.id);
    
    if (existingItem) {
      if (existingItem.quantity >= product.quantity) {
        message.warning('Không thể thêm, vượt quá số lượng tồn kho');
        return;
      }
      setCart(cart.map(item =>
        item.product_id === product.id
          ? { ...item, quantity: item.quantity + 1 }
          : item
      ));
    } else {
      setCart([...cart, {
        product_id: product.id,
        name: product.name,
        price: product.price,
        quantity: 1,
        max_quantity: product.quantity
      }]);
    }
  };

  const updateCartQuantity = (product_id, newQuantity) => {
    if (newQuantity <= 0) {
      removeFromCart(product_id);
      return;
    }

    const product = products.find(p => p.id === product_id);
    if (newQuantity > product.quantity) {
      message.warning('Vượt quá số lượng tồn kho');
      return;
    }

    setCart(cart.map(item =>
      item.product_id === product_id
        ? { ...item, quantity: newQuantity }
        : item
    ));
  };

  const removeFromCart = (product_id) => {
    setCart(cart.filter(item => item.product_id !== product_id));
  };

  const clearCart = () => {
    setCart([]);
  };

  const getTotalAmount = () => {
    return cart.reduce((total, item) => total + (item.price * item.quantity), 0);
  };

  const getTotalItems = () => {
    return cart.reduce((total, item) => total + item.quantity, 0);
  };

  const handleCheckout = () => {
    if (cart.length === 0) {
      message.warning('Giỏ hàng trống');
      return;
    }
    setOrderModalVisible(true);
  };

  const submitOrder = async (values) => {
    try {
      setLoading(true);
      
      const orderData = {
        customer_name: values.customer_name || 'Khách hàng',
        customer_phone: values.customer_phone,
        notes: values.notes,
        items: cart.map(item => ({
          product_id: item.product_id,
          quantity: item.quantity
        }))
      };

      const response = await ordersAPI.create(orderData);
      
      if (response.data.success) {
        message.success('Tạo đơn hàng thành công!');
        
        // Reset form và giỏ hàng
        customerForm.resetFields();
        clearCart();
        setOrderModalVisible(false);
        
        // Reload products để cập nhật tồn kho
        loadProducts();
        
        // Mở modal in hóa đơn
        showPrintModal(response.data.data);
      }
    } catch (error) {
      message.error(error.response?.data?.message || 'Lỗi khi tạo đơn hàng');
    } finally {
      setLoading(false);
    }
  };

  const showPrintModal = (order) => {
    Modal.info({
      title: 'Hóa đơn bán hàng',
      width: 500,
      content: (
        <div style={{ padding: '20px 0' }}>
          <div style={{ textAlign: 'center', marginBottom: '20px' }}>
            <Title level={4}>HÓA ĐƠN BÁN HÀNG</Title>
            <Text>Số hóa đơn: {order.order_number}</Text><br/>
            <Text type="secondary">
              {new Date(order.created_at).toLocaleString('vi-VN')}
            </Text>
          </div>
          
          <Divider />
          
          <div style={{ marginBottom: '16px' }}>
            <Text strong>Khách hàng: </Text>
            <Text>{order.customer_name}</Text><br/>
            {order.customer_phone && (
              <>
                <Text strong>Số điện thoại: </Text>
                <Text>{order.customer_phone}</Text>
              </>
            )}
          </div>
          
          <Divider />
          
          <List
            size="small"
            dataSource={order.items}
            renderItem={item => (
              <List.Item style={{ padding: '8px 0' }}>
                <div style={{ width: '100%', display: 'flex', justifyContent: 'space-between' }}>
                  <div>
                    <Text>{item.product_name}</Text><br/>
                    <Text type="secondary">{item.quantity} x {formatCurrency(item.price)}</Text>
                  </div>
                  <Text strong>{formatCurrency(item.subtotal)}</Text>
                </div>
              </List.Item>
            )}
          />
          
          <Divider />
          
          <div style={{ textAlign: 'right' }}>
            <Title level={4} style={{ margin: 0 }}>
              Tổng cộng: {formatCurrency(order.total_amount)}
            </Title>
          </div>
        </div>
      ),
      footer: (
        <Button 
          type="primary" 
          icon={<PrinterOutlined />}
          onClick={() => window.print()}
        >
          In hóa đơn
        </Button>
      )
    });
  };

  return (
    <Row gutter={24}>
      {/* Danh sách sản phẩm */}
      <Col xs={24} lg={16}>
        <Card title="Danh sách sản phẩm" style={{ height: '700px' }}>
          <Search
            placeholder="Tìm kiếm sản phẩm (tên, mã, barcode)"
            allowClear
            size="large"
            style={{ marginBottom: 16 }}
            prefix={<SearchOutlined />}
            onSearch={handleSearch}
            onChange={e => handleSearch(e.target.value)}
          />
          
          <div className="pos-product-grid">
            {filteredProducts.map(product => (
              <Card
                key={product.id}
                className="pos-product-card"
                size="small"
                hoverable
                onClick={() => addToCart(product)}
                title={product.name}
                extra={
                  <Tag color={product.quantity > 0 ? 'green' : 'red'}>
                    {product.quantity > 0 ? `Còn ${product.quantity}` : 'Hết hàng'}
                  </Tag>
                }
              >
                <div style={{ textAlign: 'center' }}>
                  <Title level={4} style={{ margin: '8px 0', color: '#1890ff' }}>
                    {formatCurrency(product.price)}
                  </Title>
                  <Text type="secondary">{product.category}</Text><br/>
                  {product.sku && <Text type="secondary">Mã: {product.sku}</Text>}
                </div>
              </Card>
            ))}
          </div>
        </Card>
      </Col>

      {/* Giỏ hàng */}
      <Col xs={24} lg={8}>
        <Card 
          title={`Giỏ hàng (${getTotalItems()} món)`}
          style={{ height: '700px' }}
          extra={
            <Button 
              danger 
              size="small" 
              onClick={clearCart}
              disabled={cart.length === 0}
            >
              Xóa tất cả
            </Button>
          }
        >
          <div className="pos-cart">
            {cart.length === 0 ? (
              <div style={{ 
                textAlign: 'center', 
                padding: '40px 0',
                color: '#999' 
              }}>
                <ShoppingCartOutlined style={{ fontSize: '48px', marginBottom: '16px' }} />
                <div>Giỏ hàng trống</div>
                <div>Chọn sản phẩm để thêm vào giỏ hàng</div>
              </div>
            ) : (
              <List
                dataSource={cart}
                renderItem={item => (
                  <List.Item style={{ padding: '12px 0' }}>
                    <div style={{ width: '100%' }}>
                      <div style={{ 
                        display: 'flex', 
                        justifyContent: 'space-between',
                        alignItems: 'flex-start',
                        marginBottom: '8px'
                      }}>
                        <Text strong>{item.name}</Text>
                        <Button
                          danger
                          size="small"
                          type="text"
                          icon={<DeleteOutlined />}
                          onClick={() => removeFromCart(item.product_id)}
                        />
                      </div>
                      
                      <div style={{ 
                        display: 'flex', 
                        justifyContent: 'space-between',
                        alignItems: 'center'
                      }}>
                        <Space>
                          <Button
                            size="small"
                            icon={<MinusOutlined />}
                            onClick={() => updateCartQuantity(item.product_id, item.quantity - 1)}
                          />
                          <InputNumber
                            size="small"
                            min={1}
                            max={item.max_quantity}
                            value={item.quantity}
                            onChange={(value) => updateCartQuantity(item.product_id, value)}
                            style={{ width: '60px' }}
                          />
                          <Button
                            size="small"
                            icon={<PlusOutlined />}
                            onClick={() => updateCartQuantity(item.product_id, item.quantity + 1)}
                          />
                        </Space>
                        
                        <div style={{ textAlign: 'right' }}>
                          <Text type="secondary">{formatCurrency(item.price)} x {item.quantity}</Text><br/>
                          <Text strong>{formatCurrency(item.price * item.quantity)}</Text>
                        </div>
                      </div>
                    </div>
                  </List.Item>
                )}
              />
            )}
          </div>

          {cart.length > 0 && (
            <div className="pos-total">
              <div style={{ 
                display: 'flex', 
                justifyContent: 'space-between',
                marginBottom: '16px'
              }}>
                <Title level={4} style={{ margin: 0 }}>Tổng cộng:</Title>
                <Title level={3} style={{ margin: 0, color: '#52c41a' }}>
                  {formatCurrency(getTotalAmount())}
                </Title>
              </div>
              
              <Button
                type="primary"
                size="large"
                block
                onClick={handleCheckout}
                loading={loading}
              >
                Thanh toán
              </Button>
            </div>
          )}
        </Card>
      </Col>

      {/* Modal tạo đơn hàng */}
      <Modal
        title="Thông tin đơn hàng"
        open={orderModalVisible}
        onCancel={() => setOrderModalVisible(false)}
        footer={null}
      >
        <Form
          form={customerForm}
          layout="vertical"
          onFinish={submitOrder}
        >
          <Form.Item
            label="Tên khách hàng"
            name="customer_name"
          >
            <Input placeholder="Nhập tên khách hàng (tùy chọn)" />
          </Form.Item>

          <Form.Item
            label="Số điện thoại"
            name="customer_phone"
          >
            <Input placeholder="Nhập số điện thoại (tùy chọn)" />
          </Form.Item>

          <Form.Item
            label="Ghi chú"
            name="notes"
          >
            <Input.TextArea rows={3} placeholder="Ghi chú đơn hàng (tùy chọn)" />
          </Form.Item>

          <div style={{ 
            background: '#f6ffed', 
            border: '1px solid #b7eb8f',
            borderRadius: '6px',
            padding: '16px',
            marginBottom: '16px'
          }}>
            <div style={{ display: 'flex', justifyContent: 'space-between' }}>
              <Text>Số lượng món:</Text>
              <Text strong>{getTotalItems()}</Text>
            </div>
            <div style={{ display: 'flex', justifyContent: 'space-between' }}>
              <Text>Tổng tiền:</Text>
              <Text strong style={{ fontSize: '18px', color: '#52c41a' }}>
                {formatCurrency(getTotalAmount())}
              </Text>
            </div>
          </div>

          <Form.Item style={{ marginBottom: 0 }}>
            <Space style={{ width: '100%', justifyContent: 'flex-end' }}>
              <Button onClick={() => setOrderModalVisible(false)}>
                Hủy
              </Button>
              <Button type="primary" htmlType="submit" loading={loading}>
                Xác nhận đơn hàng
              </Button>
            </Space>
          </Form.Item>
        </Form>
      </Modal>
    </Row>
  );
}

export default POSPage;
