import React from 'react';
import { Spin, Card, Typography } from 'antd';
import { LoadingOutlined, RobotOutlined } from '@ant-design/icons';

const { Text } = Typography;

const LoadingSpinner = ({ 
  size = 'large', 
  tip = 'Äang táº£i...', 
  variant = 'default',
  spinning = true,
  children,
  style = {}
}) => {
  const getIcon = () => {
    switch (variant) {
      case 'ai':
        return <RobotOutlined spin />;
      default:
        return <LoadingOutlined style={{ fontSize: 24 }} spin />;
    }
  };

  const getWrapperStyle = () => {
    const baseStyle = {
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      minHeight: '200px',
      ...style
    };

    return baseStyle;
  };

  if (children) {
    return (
      <Spin 
        spinning={spinning} 
        size={size} 
        tip={tip} 
        indicator={variant !== 'default' ? getIcon() : undefined}
      >
        {children}
      </Spin>
    );
  }

  return (
    <div style={getWrapperStyle()}>
      <Spin 
        size={size} 
        tip={tip} 
        indicator={variant !== 'default' ? getIcon() : undefined}
      />
    </div>
  );
};

// Specialized loading components
export const PageLoading = ({ message = 'Äang táº£i trang...', style = {} }) => (
  <div style={{ 
    display: 'flex', 
    justifyContent: 'center', 
    alignItems: 'center', 
    minHeight: '60vh',
    flexDirection: 'column',
    ...style
  }}>
    <Spin size="large" />
    <Text style={{ marginTop: 16, color: '#666' }}>{message}</Text>
  </div>
);

export const CardLoading = ({ title = 'Äang táº£i...', height = 300 }) => (
  <Card style={{ height }}>
    <div style={{ 
      display: 'flex', 
      justifyContent: 'center', 
      alignItems: 'center', 
      height: '100%',
      flexDirection: 'column'
    }}>
      <Spin size="large" />
      <Text style={{ marginTop: 16, color: '#666' }}>{title}</Text>
    </div>
  </Card>
);

export const AILoading = ({ message = 'AI Ä‘ang phÃ¢n tÃ­ch...', style = {} }) => (
  <div style={{ 
    display: 'flex', 
    justifyContent: 'center', 
    alignItems: 'center', 
    minHeight: '100px',
    flexDirection: 'column',
    ...style
  }}>
    <Spin 
      size="large" 
      indicator={<RobotOutlined spin style={{ fontSize: 24 }} />}
    />
    <Text style={{ marginTop: 16, color: '#666' }}>
      ðŸ¤– {message}
    </Text>
  </div>
);

export const InlineLoading = ({ size = 'small', tip = 'Äang táº£i...' }) => (
  <span style={{ display: 'inline-flex', alignItems: 'center', gap: '8px' }}>
    <Spin size={size} />
    <Text type="secondary">{tip}</Text>
  </span>
);

export default LoadingSpinner;
