import React, { useState, useEffect } from 'react';
import {
  Card,
  Table,
  Button,
  Input,
  Space,
  Modal,
  Form,
  Select,
  message,
  Row,
  Col,
  Statistic,
  Tag,
  Tooltip,
  DatePicker,
  Tabs
} from 'antd';
import {
  InboxOutlined,
  WarningOutlined,
  AdjustmentOutlined,
  HistoryOutlined,
  ImportOutlined,
  ExportOutlined,
  SearchOutlined,
  BarChartOutlined
} from '@ant-design/icons';
import { inventoryAPI, productsAPI, formatCurrency, formatDate } from '../services/api';

const { Search } = Input;
const { Option } = Select;
const { TabPane } = Tabs;
const { RangePicker } = DatePicker;

const InventoryPage = () => {
  const [products, setProducts] = useState([]);
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [isAdjustModalVisible, setIsAdjustModalVisible] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [searchText, setSearchText] = useState('');
  const [activeTab, setActiveTab] = useState('stock');
  const [stats, setStats] = useState({
    totalProducts: 0,
    lowStockProducts: 0,
    totalValue: 0,
    outOfStock: 0
  });
  const [form] = Form.useForm();

  useEffect(() => {
    if (activeTab === 'stock') {
      fetchProducts();
    } else if (activeTab === 'transactions') {
      fetchTransactions();
    }
  }, [activeTab, searchText]);

  const fetchProducts = async () => {
    try {
      setLoading(true);
      const response = await productsAPI.getAll({ search: searchText });
      
      if (response.data.success) {
        setProducts(response.data.data);
        calculateStats(response.data.data);
      }
    } catch (error) {
      message.error('Lỗi khi tải danh sách sản phẩm');
      console.error('Error fetching products:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchTransactions = async () => {
    try {
      setLoading(true);
      const response = await inventoryAPI.getTransactions({ limit: 100 });
      
      if (response.data.success) {
        setTransactions(response.data.data);
      }
    } catch (error) {
      message.error('Lỗi khi tải lịch sử giao dịch');
      console.error('Error fetching transactions:', error);
    } finally {
      setLoading(false);
    }
  };

  const calculateStats = (productData) => {
    const stats = {
      totalProducts: productData.length,
      lowStockProducts: productData.filter(p => p.quantity <= (p.min_stock || 10)).length,
      totalValue: productData.reduce((sum, p) => sum + (p.quantity * (p.cost_price || p.price)), 0),
      outOfStock: productData.filter(p => p.quantity === 0).length
    };
    setStats(stats);
  };

  const showAdjustModal = (product) => {
    setSelectedProduct(product);
    setIsAdjustModalVisible(true);
    form.setFieldsValue({
      current_quantity: product.quantity,
      new_quantity: product.quantity,
      reason: ''
    });
  };

  const handleAdjustStock = async (values) => {
    try {
      await inventoryAPI.adjustStock({
        product_id: selectedProduct.id,
        new_quantity: values.new_quantity,
        reason: values.reason,
        user_id: 1 // TODO: Get from auth context
      });
      
      message.success('Điều chỉnh tồn kho thành công');
      setIsAdjustModalVisible(false);
      form.resetFields();
      fetchProducts();
    } catch (error) {
      message.error('Lỗi khi điều chỉnh tồn kho');
      console.error('Error adjusting stock:', error);
    }
  };

  const getStockStatus = (quantity, minStock) => {
    if (quantity === 0) {
      return { status: 'Hết hàng', color: 'red' };
    } else if (quantity <= minStock) {
      return { status: 'Sắp hết', color: 'orange' };
    } else {
      return { status: 'Còn hàng', color: 'green' };
    }
  };

  const stockColumns = [
    {
      title: 'Sản phẩm',
      key: 'product_info',
      render: (_, record) => (
        <div>
          <div style={{ fontWeight: 'bold' }}>{record.name}</div>
          <div style={{ fontSize: '12px', color: '#666' }}>
            SKU: {record.sku || 'N/A'} | Barcode: {record.barcode || 'N/A'}
          </div>
        </div>
      )
    },
    {
      title: 'Danh mục',
      dataIndex: 'category_name',
      key: 'category_name',
      width: 120,
      render: (category) => category || 'Chưa phân loại'
    },
    {
      title: 'Tồn kho',
      key: 'stock_info',
      width: 150,
      render: (_, record) => {
        const stockStatus = getStockStatus(record.quantity, record.min_stock || 10);
        return (
          <div>
            <div style={{ fontSize: '16px', fontWeight: 'bold' }}>
              {record.quantity} {record.unit || 'cái'}
            </div>
            <Tag color={stockStatus.color}>{stockStatus.status}</Tag>
          </div>
        );
      },
      sorter: (a, b) => a.quantity - b.quantity
    },
    {
      title: 'Mức tối thiểu',
      dataIndex: 'min_stock',
      key: 'min_stock',
      width: 120,
      render: (minStock) => `${minStock || 10} cái`
    },
    {
      title: 'Giá vốn',
      dataIndex: 'cost_price',
      key: 'cost_price',
      width: 120,
      render: (price) => formatCurrency(price || 0)
    },
    {
      title: 'Giá trị tồn kho',
      key: 'stock_value',
      width: 150,
      render: (_, record) => {
        const value = record.quantity * (record.cost_price || record.price || 0);
        return <span style={{ fontWeight: 'bold', color: '#52c41a' }}>
          {formatCurrency(value)}
        </span>;
      },
      sorter: (a, b) => {
        const valueA = a.quantity * (a.cost_price || a.price || 0);
        const valueB = b.quantity * (b.cost_price || b.price || 0);
        return valueA - valueB;
      }
    },
    {
      title: 'Thao tác',
      key: 'actions',
      width: 120,
      render: (_, record) => (
        <Space>
          <Tooltip title="Điều chỉnh tồn kho">
            <Button
              type="primary"
              size="small"
              icon={<AdjustmentOutlined />}
              onClick={() => showAdjustModal(record)}
            />
          </Tooltip>
        </Space>
      )
    }
  ];

  const transactionColumns = [
    {
      title: 'Thời gian',
      dataIndex: 'created_at',
      key: 'created_at',
      width: 150,
      render: (date) => formatDate(date),
      sorter: (a, b) => new Date(a.created_at) - new Date(b.created_at)
    },
    {
      title: 'Loại giao dịch',
      dataIndex: 'type',
      key: 'type',
      width: 120,
      render: (type) => {
        const typeConfig = {
          import: { label: 'Nhập kho', color: 'green', icon: <ImportOutlined /> },
          export: { label: 'Xuất kho', color: 'red', icon: <ExportOutlined /> },
          adjustment: { label: 'Điều chỉnh', color: 'blue', icon: <AdjustmentOutlined /> },
          return: { label: 'Trả hàng', color: 'orange', icon: <HistoryOutlined /> }
        };
        const config = typeConfig[type] || { label: type, color: 'default' };
        return (
          <Tag color={config.color} icon={config.icon}>
            {config.label}
          </Tag>
        );
      }
    },
    {
      title: 'Sản phẩm',
      dataIndex: 'product_name',
      key: 'product_name'
    },
    {
      title: 'Số lượng trước',
      dataIndex: 'quantity_before',
      key: 'quantity_before',
      width: 120,
      render: (qty) => <span>{qty}</span>
    },
    {
      title: 'Thay đổi',
      dataIndex: 'quantity_change',
      key: 'quantity_change',
      width: 100,
      render: (change) => (
        <span style={{ 
          color: change > 0 ? '#52c41a' : '#ff4d4f',
          fontWeight: 'bold'
        }}>
          {change > 0 ? '+' : ''}{change}
        </span>
      )
    },
    {
      title: 'Số lượng sau',
      dataIndex: 'quantity_after',
      key: 'quantity_after',
      width: 120,
      render: (qty) => <strong>{qty}</strong>
    },
    {
      title: 'Nhân viên',
      dataIndex: 'user_name',
      key: 'user_name',
      width: 120,
      render: (name) => name || 'Hệ thống'
    },
    {
      title: 'Ghi chú',
      dataIndex: 'notes',
      key: 'notes'
    }
  ];

  const lowStockProducts = products.filter(p => p.quantity <= (p.min_stock || 10));

  return (
    <div style={{ padding: '24px' }}>
      {/* Header với thống kê */}
      <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
        <Col span={6}>
          <Card>
            <Statistic
              title="Tổng sản phẩm"
              value={stats.totalProducts}
              prefix={<InboxOutlined />}
              valueStyle={{ color: '#1890ff' }}
            />
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <Statistic
              title="Sắp hết hàng"
              value={stats.lowStockProducts}
              prefix={<WarningOutlined />}
              valueStyle={{ color: '#faad14' }}
            />
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <Statistic
              title="Hết hàng"
              value={stats.outOfStock}
              prefix={<WarningOutlined />}
              valueStyle={{ color: '#ff4d4f' }}
            />
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <Statistic
              title="Giá trị tồn kho"
              value={stats.totalValue}
              formatter={value => formatCurrency(value)}
              prefix={<BarChartOutlined />}
              valueStyle={{ color: '#52c41a' }}
            />
          </Card>
        </Col>
      </Row>

      {/* Main Content */}
      <Card>
        <Tabs activeKey={activeTab} onChange={setActiveTab}>
          <TabPane tab="Tồn kho" key="stock">
            <Row gutter={[16, 16]} style={{ marginBottom: 16 }}>
              <Col span={12}>
                <Search
                  placeholder="Tìm sản phẩm..."
                  value={searchText}
                  onChange={(e) => setSearchText(e.target.value)}
                  onSearch={fetchProducts}
                  enterButton={<SearchOutlined />}
                  allowClear
                />
              </Col>
            </Row>

            <Table
              columns={stockColumns}
              dataSource={products}
              rowKey="id"
              loading={loading}
              pagination={{
                total: products.length,
                pageSize: 15,
                showSizeChanger: true,
                showQuickJumper: true,
                showTotal: (total, range) => 
                  `${range[0]}-${range[1]} của ${total} sản phẩm`
              }}
              scroll={{ x: 1200 }}
            />
          </TabPane>

          <TabPane tab="Lịch sử giao dịch" key="transactions">
            <Row gutter={[16, 16]} style={{ marginBottom: 16 }}>
              <Col span={8}>
                <Search
                  placeholder="Tìm theo sản phẩm..."
                  onSearch={(value) => {
                    setSearchText(value);
                    fetchTransactions();
                  }}
                  enterButton={<SearchOutlined />}
                  allowClear
                />
              </Col>
              <Col span={6}>
                <Select
                  style={{ width: '100%' }}
                  placeholder="Lọc theo loại giao dịch"
                  allowClear
                  onChange={(value) => {
                    // TODO: Filter transactions by type
                  }}
                >
                  <Option value="import">Nhập kho</Option>
                  <Option value="export">Xuất kho</Option>
                  <Option value="adjustment">Điều chỉnh</Option>
                  <Option value="return">Trả hàng</Option>
                </Select>
              </Col>
              <Col span={10}>
                <RangePicker
                  style={{ width: '100%' }}
                  placeholder={['Từ ngày', 'Đến ngày']}
                  onChange={(dates) => {
                    // TODO: Filter transactions by date range
                  }}
                />
              </Col>
            </Row>

            <Table
              columns={transactionColumns}
              dataSource={transactions}
              rowKey="id"
              loading={loading}
              pagination={{
                total: transactions.length,
                pageSize: 15,
                showSizeChanger: true,
                showQuickJumper: true,
                showTotal: (total, range) => 
                  `${range[0]}-${range[1]} của ${total} giao dịch`
              }}
              scroll={{ x: 1200 }}
            />
          </TabPane>

          <TabPane tab="Cảnh báo tồn kho" key="alerts">
            <Card title="Sản phẩm sắp hết hàng" style={{ marginBottom: 16 }}>
              <Table
                columns={stockColumns.filter(col => 
                  ['product_info', 'stock_info', 'min_stock', 'actions'].includes(col.key)
                )}
                dataSource={lowStockProducts}
                rowKey="id"
                pagination={false}
                size="small"
              />
            </Card>
          </TabPane>
        </Tabs>
      </Card>

      {/* Modal điều chỉnh tồn kho */}
      <Modal
        title="Điều chỉnh tồn kho"
        visible={isAdjustModalVisible}
        onCancel={() => setIsAdjustModalVisible(false)}
        footer={null}
        width={500}
      >
        {selectedProduct && (
          <div>
            <Card size="small" style={{ marginBottom: 16, backgroundColor: '#f5f5f5' }}>
              <div><strong>Sản phẩm:</strong> {selectedProduct.name}</div>
              <div><strong>SKU:</strong> {selectedProduct.sku || 'N/A'}</div>
              <div><strong>Tồn kho hiện tại:</strong> {selectedProduct.quantity} {selectedProduct.unit || 'cái'}</div>
            </Card>

            <Form
              form={form}
              layout="vertical"
              onFinish={handleAdjustStock}
            >
              <Row gutter={16}>
                <Col span={12}>
                  <Form.Item
                    label="Số lượng hiện tại"
                    name="current_quantity"
                  >
                    <Input disabled />
                  </Form.Item>
                </Col>
                <Col span={12}>
                  <Form.Item
                    label="Số lượng mới"
                    name="new_quantity"
                    rules={[
                      { required: true, message: 'Vui lòng nhập số lượng mới' },
                      { type: 'number', min: 0, message: 'Số lượng phải >= 0' }
                    ]}
                  >
                    <Input type="number" min={0} placeholder="Nhập số lượng mới" />
                  </Form.Item>
                </Col>
              </Row>

              <Form.Item
                label="Lý do điều chỉnh"
                name="reason"
                rules={[{ required: true, message: 'Vui lòng nhập lý do điều chỉnh' }]}
              >
                <Input.TextArea 
                  rows={3} 
                  placeholder="VD: Kiểm kê, hàng hỏng, nhập sai..."
                />
              </Form.Item>

              <Form.Item style={{ textAlign: 'right', marginBottom: 0 }}>
                <Space>
                  <Button onClick={() => setIsAdjustModalVisible(false)}>
                    Hủy
                  </Button>
                  <Button type="primary" htmlType="submit">
                    Điều chỉnh
                  </Button>
                </Space>
              </Form.Item>
            </Form>
          </div>
        )}
      </Modal>
    </div>
  );
};

export default InventoryPage;
