import React, { useState, useEffect } from 'react';
import {
  Card,
  Table,
  Button,
  Modal,
  Form,
  Input,
  Select,
  Space,
  Tag,
  message,
  Switch,
  Avatar,
  Descriptions,
  Row,
  Col,
  Statistic,
  Alert,
  Popconfirm,
  Badge,
  Typography,
} from 'antd';
import {
  PlusOutlined,
  EditOutlined,
  DeleteOutlined,
  UserOutlined,
  LockOutlined,
  SettingOutlined,
  TeamOutlined,
  CrownOutlined,
  KeyOutlined,
} from '@ant-design/icons';
import { usersAPI } from '../services/api';
import moment from 'moment';

const { Option } = Select;
const { Text } = Typography;

const UsersPage = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [modalVisible, setModalVisible] = useState(false);
  const [editingUser, setEditingUser] = useState(null);
  const [form] = Form.useForm();

  const roles = {
    admin: {
      label: 'Quản trị viên',
      color: 'red',
      permissions: ['all'],
      description: 'Toàn quyền quản lý hệ thống',
    },
    manager: {
      label: 'Quản lý',
      color: 'orange',
      permissions: ['products', 'orders', 'customers', 'reports', 'inventory'],
      description: 'Quản lý sản phẩm, đơn hàng, khách hàng và báo cáo',
    },
    cashier: {
      label: 'Thu ngân',
      color: 'blue',
      permissions: ['pos', 'orders', 'customers'],
      description: 'Bán hàng và quản lý đơn hàng',
    },
    inventory_staff: {
      label: 'Nhân viên kho',
      color: 'green',
      permissions: ['inventory', 'products'],
      description: 'Quản lý kho và sản phẩm',
    },
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    setLoading(true);
    try {
      const response = await usersAPI.getAll();
      if (response.data.success) {
        setUsers(response.data.data);
      }
    } catch (error) {
      message.error('Lỗi khi tải danh sách nhân viên');
      // Mock data for demo
      const mockUsers = [
        {
          id: 1,
          username: 'admin',
          full_name: 'Quản trị viên',
          email: 'admin@posystem.com',
          phone: '0901234567',
          role: 'admin',
          is_active: true,
          last_login: '2024-01-15T10:30:00Z',
          created_at: '2024-01-01T00:00:00Z',
        },
        {
          id: 2,
          username: 'manager01',
          full_name: 'Nguyễn Văn Quản Lý',
          email: 'manager@posystem.com',
          phone: '0902345678',
          role: 'manager',
          is_active: true,
          last_login: '2024-01-15T09:15:00Z',
          created_at: '2024-01-02T00:00:00Z',
        },
        {
          id: 3,
          username: 'cashier01',
          full_name: 'Trần Thị Thu Ngân',
          email: 'cashier@posystem.com',
          phone: '0903456789',
          role: 'cashier',
          is_active: true,
          last_login: '2024-01-15T08:00:00Z',
          created_at: '2024-01-05T00:00:00Z',
        },
        {
          id: 4,
          username: 'warehouse01',
          full_name: 'Lê Văn Kho',
          email: 'warehouse@posystem.com',
          phone: '0904567890',
          role: 'inventory_staff',
          is_active: false,
          last_login: '2024-01-10T16:45:00Z',
          created_at: '2024-01-08T00:00:00Z',
        },
      ];
      setUsers(mockUsers);
    } finally {
      setLoading(false);
    }
  };

  const handleAddUser = () => {
    setEditingUser(null);
    setModalVisible(true);
    form.resetFields();
  };

  const handleEditUser = (user) => {
    setEditingUser(user);
    setModalVisible(true);
    form.setFieldsValue({
      ...user,
      permissions: roles[user.role]?.permissions || [],
    });
  };

  const handleDeleteUser = async (userId) => {
    try {
      await usersAPI.delete(userId);
      message.success('Xóa nhân viên thành công');
      fetchUsers();
    } catch (error) {
      message.error('Lỗi khi xóa nhân viên');
    }
  };

  const handleSubmit = async (values) => {
    try {
      if (editingUser) {
        await usersAPI.update(editingUser.id, values);
        message.success('Cập nhật nhân viên thành công');
      } else {
        await usersAPI.create(values);
        message.success('Thêm nhân viên thành công');
      }
      setModalVisible(false);
      form.resetFields();
      fetchUsers();
    } catch (error) {
      message.error(editingUser ? 'Lỗi khi cập nhật nhân viên' : 'Lỗi khi thêm nhân viên');
    }
  };

  const handleToggleStatus = async (user) => {
    try {
      await usersAPI.update(user.id, { is_active: !user.is_active });
      message.success(`${user.is_active ? 'Vô hiệu hóa' : 'Kích hoạt'} tài khoản thành công`);
      fetchUsers();
    } catch (error) {
      message.error('Lỗi khi thay đổi trạng thái tài khoản');
    }
  };

  const columns = [
    {
      title: 'Nhân viên',
      key: 'user_info',
      render: (_, record) => (
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <Avatar
            size={40}
            icon={<UserOutlined />}
            style={{ 
              backgroundColor: record.is_active ? '#87d068' : '#f5f5f5',
              marginRight: 12 
            }}
          />
          <div>
            <div style={{ fontWeight: 500 }}>{record.full_name}</div>
            <Text type="secondary" style={{ fontSize: 12 }}>
              @{record.username}
            </Text>
          </div>
        </div>
      ),
    },
    {
      title: 'Liên hệ',
      key: 'contact',
      render: (_, record) => (
        <div>
          <div>{record.email}</div>
          <Text type="secondary" style={{ fontSize: 12 }}>
            {record.phone}
          </Text>
        </div>
      ),
    },
    {
      title: 'Vai trò',
      dataIndex: 'role',
      key: 'role',
      render: (role) => {
        const roleInfo = roles[role];
        return (
          <div>
            <Tag color={roleInfo?.color}>
              {roleInfo?.label || role}
            </Tag>
            <div style={{ fontSize: 11, color: '#666', marginTop: 2 }}>
              {roleInfo?.description}
            </div>
          </div>
        );
      },
      filters: Object.keys(roles).map(key => ({
        text: roles[key].label,
        value: key,
      })),
      onFilter: (value, record) => record.role === value,
    },
    {
      title: 'Trạng thái',
      dataIndex: 'is_active',
      key: 'is_active',
      render: (isActive, record) => (
        <div>
          <Badge
            status={isActive ? 'success' : 'default'}
            text={isActive ? 'Hoạt động' : 'Vô hiệu hóa'}
          />
          {record.last_login && (
            <div style={{ fontSize: 11, color: '#666', marginTop: 2 }}>
              Lần cuối: {moment(record.last_login).format('DD/MM/YYYY HH:mm')}
            </div>
          )}
        </div>
      ),
      filters: [
        { text: 'Hoạt động', value: true },
        { text: 'Vô hiệu hóa', value: false },
      ],
      onFilter: (value, record) => record.is_active === value,
    },
    {
      title: 'Ngày tạo',
      dataIndex: 'created_at',
      key: 'created_at',
      render: (date) => moment(date).format('DD/MM/YYYY'),
      sorter: (a, b) => moment(a.created_at).unix() - moment(b.created_at).unix(),
    },
    {
      title: 'Thao tác',
      key: 'action',
      render: (_, record) => (
        <Space>
          <Button
            type="primary"
            size="small"
            icon={<EditOutlined />}
            onClick={() => handleEditUser(record)}
          >
            Sửa
          </Button>
          <Button
            size="small"
            onClick={() => handleToggleStatus(record)}
          >
            {record.is_active ? 'Vô hiệu hóa' : 'Kích hoạt'}
          </Button>
          {record.role !== 'admin' && (
            <Popconfirm
              title="Bạn có chắc muốn xóa nhân viên này?"
              onConfirm={() => handleDeleteUser(record.id)}
              okText="Xóa"
              cancelText="Hủy"
            >
              <Button
                danger
                size="small"
                icon={<DeleteOutlined />}
              >
                Xóa
              </Button>
            </Popconfirm>
          )}
        </Space>
      ),
    },
  ];

  const renderStats = () => {
    const activeUsers = users.filter(u => u.is_active).length;
    const totalUsers = users.length;
    const adminCount = users.filter(u => u.role === 'admin').length;
    const cashierCount = users.filter(u => u.role === 'cashier').length;

    return (
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col span={6}>
          <Card>
            <Statistic
              title="Tổng nhân viên"
              value={totalUsers}
              prefix={<TeamOutlined />}
              valueStyle={{ color: '#1890ff' }}
            />
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <Statistic
              title="Đang hoạt động"
              value={activeUsers}
              prefix={<UserOutlined />}
              valueStyle={{ color: '#52c41a' }}
            />
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <Statistic
              title="Quản trị viên"
              value={adminCount}
              prefix={<CrownOutlined />}
              valueStyle={{ color: '#f5222d' }}
            />
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <Statistic
              title="Thu ngân"
              value={cashierCount}
              prefix={<UserOutlined />}
              valueStyle={{ color: '#722ed1' }}
            />
          </Card>
        </Col>
      </Row>
    );
  };

  return (
    <div style={{ padding: '24px' }}>
      <div style={{ marginBottom: 24 }}>
        <h2>Quản lý nhân viên</h2>
        <p>Quản lý tài khoản nhân viên và phân quyền hệ thống</p>
      </div>

      {renderStats()}

      <Card>
        <div style={{ marginBottom: 16, display: 'flex', justifyContent: 'space-between' }}>
          <Alert
            message="Hướng dẫn phân quyền"
            description="Quản trị viên có toàn quyền, Quản lý có thể quản lý sản phẩm và báo cáo, Thu ngân chỉ có thể bán hàng."
            type="info"
            showIcon
            style={{ flex: 1, marginRight: 16 }}
          />
          <Button
            type="primary"
            icon={<PlusOutlined />}
            onClick={handleAddUser}
          >
            Thêm nhân viên
          </Button>
        </div>
        
        <Table
          columns={columns}
          dataSource={users}
          rowKey="id"
          loading={loading}
          pagination={{
            pageSize: 10,
            showSizeChanger: true,
            showQuickJumper: true,
            showTotal: (total, range) =>
              `${range[0]}-${range[1]} của ${total} nhân viên`,
          }}
        />
      </Card>

      <Modal
        title={editingUser ? 'Chỉnh sửa nhân viên' : 'Thêm nhân viên mới'}
        visible={modalVisible}
        onCancel={() => {
          setModalVisible(false);
          form.resetFields();
        }}
        footer={null}
        width={700}
      >
        <Form
          form={form}
          layout="vertical"
          onFinish={handleSubmit}
          initialValues={{
            is_active: true,
            role: 'cashier',
          }}
        >
          <Row gutter={16}>
            <Col span={12}>
              <Form.Item
                name="username"
                label="Tên đăng nhập"
                rules={[
                  { required: true, message: 'Vui lòng nhập tên đăng nhập' },
                  { min: 3, message: 'Tên đăng nhập phải có ít nhất 3 ký tự' },
                ]}
              >
                <Input
                  prefix={<UserOutlined />}
                  placeholder="Tên đăng nhập"
                  disabled={!!editingUser}
                />
              </Form.Item>
            </Col>
            <Col span={12}>
              <Form.Item
                name="full_name"
                label="Họ và tên"
                rules={[{ required: true, message: 'Vui lòng nhập họ và tên' }]}
              >
                <Input placeholder="Họ và tên đầy đủ" />
              </Form.Item>
            </Col>
          </Row>

          {!editingUser && (
            <Row gutter={16}>
              <Col span={12}>
                <Form.Item
                  name="password"
                  label="Mật khẩu"
                  rules={[
                    { required: true, message: 'Vui lòng nhập mật khẩu' },
                    { min: 6, message: 'Mật khẩu phải có ít nhất 6 ký tự' },
                  ]}
                >
                  <Input.Password
                    prefix={<LockOutlined />}
                    placeholder="Mật khẩu"
                  />
                </Form.Item>
              </Col>
              <Col span={12}>
                <Form.Item
                  name="confirm_password"
                  label="Xác nhận mật khẩu"
                  dependencies={['password']}
                  rules={[
                    { required: true, message: 'Vui lòng xác nhận mật khẩu' },
                    ({ getFieldValue }) => ({
                      validator(_, value) {
                        if (!value || getFieldValue('password') === value) {
                          return Promise.resolve();
                        }
                        return Promise.reject(new Error('Mật khẩu không khớp'));
                      },
                    }),
                  ]}
                >
                  <Input.Password
                    prefix={<KeyOutlined />}
                    placeholder="Xác nhận mật khẩu"
                  />
                </Form.Item>
              </Col>
            </Row>
          )}

          <Row gutter={16}>
            <Col span={12}>
              <Form.Item
                name="email"
                label="Email"
                rules={[
                  { type: 'email', message: 'Email không hợp lệ' },
                  { required: true, message: 'Vui lòng nhập email' },
                ]}
              >
                <Input placeholder="Email liên hệ" />
              </Form.Item>
            </Col>
            <Col span={12}>
              <Form.Item
                name="phone"
                label="Số điện thoại"
                rules={[
                  { pattern: /^[0-9]{10,11}$/, message: 'Số điện thoại không hợp lệ' },
                ]}
              >
                <Input placeholder="Số điện thoại" />
              </Form.Item>
            </Col>
          </Row>

          <Row gutter={16}>
            <Col span={12}>
              <Form.Item
                name="role"
                label="Vai trò"
                rules={[{ required: true, message: 'Vui lòng chọn vai trò' }]}
              >
                <Select placeholder="Chọn vai trò">
                  {Object.entries(roles).map(([key, role]) => (
                    <Option key={key} value={key}>
                      <div>
                        <Tag color={role.color} style={{ marginRight: 8 }}>
                          {role.label}
                        </Tag>
                        <span style={{ fontSize: 12, color: '#666' }}>
                          {role.description}
                        </span>
                      </div>
                    </Option>
                  ))}
                </Select>
              </Form.Item>
            </Col>
            <Col span={12}>
              <Form.Item
                name="is_active"
                label="Trạng thái"
                valuePropName="checked"
              >
                <Switch
                  checkedChildren="Hoạt động"
                  unCheckedChildren="Vô hiệu hóa"
                />
              </Form.Item>
            </Col>
          </Row>

          <Form.Item shouldUpdate={(prevValues, currentValues) => prevValues.role !== currentValues.role}>
            {({ getFieldValue }) => {
              const selectedRole = getFieldValue('role');
              const roleInfo = roles[selectedRole];
              return roleInfo ? (
                <Alert
                  message={`Quyền hạn của ${roleInfo.label}`}
                  description={
                    <div>
                      <p>{roleInfo.description}</p>
                      <div>
                        <strong>Quyền truy cập:</strong>
                        <div style={{ marginTop: 4 }}>
                          {roleInfo.permissions.includes('all') ? (
                            <Tag color="red">Toàn quyền hệ thống</Tag>
                          ) : (
                            roleInfo.permissions.map(permission => (
                              <Tag key={permission} color="blue" style={{ marginBottom: 4 }}>
                                {permission}
                              </Tag>
                            ))
                          )}
                        </div>
                      </div>
                    </div>
                  }
                  type="info"
                  style={{ marginBottom: 16 }}
                />
              ) : null;
            }}
          </Form.Item>

          <div style={{ textAlign: 'right' }}>
            <Space>
              <Button onClick={() => setModalVisible(false)}>
                Hủy
              </Button>
              <Button type="primary" htmlType="submit">
                {editingUser ? 'Cập nhật' : 'Thêm mới'}
              </Button>
            </Space>
          </div>
        </Form>
      </Modal>
    </div>
  );
};

export default UsersPage;
