import React, { useState, useEffect } from 'react';
import {
  Card,
  Table,
  Button,
  Input,
  Space,
  Modal,
  Form,
  Select,
  message,
  Row,
  Col,
  Statistic,
  Tag,
  Tooltip,
  DatePicker,
  Popconfirm,
  Drawer,
  Tabs,
  Empty,
  Typography,
  Descriptions,
  Timeline
} from 'antd';
import {
  PlusOutlined,
  SearchOutlined,
  EditOutlined,
  DeleteOutlined,
  UserOutlined,
  PhoneOutlined,
  MailOutlined,
  HomeOutlined,
  CrownOutlined,
  ShoppingCartOutlined,
  CalendarOutlined,
  EyeOutlined
} from '@ant-design/icons';
import api, { customersAPI, ordersAPI, formatCurrency, formatDate } from '../services/api';

const { Search } = Input;
const { Option } = Select;
const { TabPane } = Tabs;
const { Text } = Typography;

const CustomersPage = () => {
  const [customers, setCustomers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [isDetailDrawerVisible, setIsDetailDrawerVisible] = useState(false);
  const [editingCustomer, setEditingCustomer] = useState(null);
  const [selectedCustomer, setSelectedCustomer] = useState(null);
  const [searchText, setSearchText] = useState('');
  const [filterType, setFilterType] = useState('all');
  const [stats, setStats] = useState({
    total: 0,
    regular: 0,
    vip: 0,
    wholesale: 0
  });
  const [customerPurchaseHistory, setCustomerPurchaseHistory] = useState([]);
  const [purchaseHistoryLoading, setPurchaseHistoryLoading] = useState(false);
  const [form] = Form.useForm();

  const customerTypes = [
    { value: 'regular', label: 'KhÃ¡ch thÆ°á»ng', color: 'default' },
    { value: 'vip', label: 'VIP', color: 'gold' },
    { value: 'wholesale', label: 'BÃ¡n sá»‰', color: 'purple' }
  ];

  useEffect(() => {
    fetchCustomers();
  }, [searchText, filterType]);

  const fetchCustomers = async () => {
    try {
      setLoading(true);
      const response = await customersAPI.getAll({
        search: searchText,
        type: filterType
      });
      
      if (response.data.success) {
        setCustomers(response.data.data);
        calculateStats(response.data.data);
      }
    } catch (error) {
      message.error('Lá»—i khi táº£i danh sÃ¡ch khÃ¡ch hÃ ng');
      console.error('Error fetching customers:', error);
    } finally {
      setLoading(false);
    }
  };

  const calculateStats = (customerData) => {
    const stats = {
      total: customerData.length,
      regular: customerData.filter(c => c.customer_type === 'regular').length,
      vip: customerData.filter(c => c.customer_type === 'vip').length,
      wholesale: customerData.filter(c => c.customer_type === 'wholesale').length
    };
    setStats(stats);
  };

  const showModal = (customer = null) => {
    setEditingCustomer(customer);
    setIsModalVisible(true);
    if (customer) {
      form.setFieldsValue({
        ...customer,
      });
    } else {
      form.resetFields();
    }
  };

  const handleCancel = () => {
    setIsModalVisible(false);
    setEditingCustomer(null);
    form.resetFields();
  };

  const handleSubmit = async (values) => {
    try {
      // Remove unwanted fields from values before sending
      const { gender, birthday, city, ...customerData } = values; 

      if (editingCustomer) {
        await customersAPI.update(editingCustomer.id, customerData);
        message.success('Cáº­p nháº­t khÃ¡ch hÃ ng thÃ nh cÃ´ng');
      } else {
        await customersAPI.create(customerData);
        message.success('Táº¡o khÃ¡ch hÃ ng thÃ nh cÃ´ng');
      }
      
      handleCancel();
      fetchCustomers();
    } catch (error) {
      message.error(`Lá»—i khi ${editingCustomer ? 'cáº­p nháº­t' : 'táº¡o'} khÃ¡ch hÃ ng: ` + (error.response?.data?.message || error.message));
      console.error('Error saving customer:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await customersAPI.delete(id);
      message.success('XÃ³a khÃ¡ch hÃ ng thÃ nh cÃ´ng');
      fetchCustomers();
    } catch (error) {
      message.error('Lá»—i khi xÃ³a khÃ¡ch hÃ ng');
      console.error('Error deleting customer:', error);
    }
  };

  const showCustomerDetail = async (customer) => {
    try {
      setSelectedCustomer(customer);
      setIsDetailDrawerVisible(true);
      setPurchaseHistoryLoading(true);
      
      // Fetch customer details
      const customerResponse = await customersAPI.getById(customer.id);
      if (customerResponse.data.success) {
        setSelectedCustomer(customerResponse.data.data);
      }
      
      // ðŸ›’ Fetch customer purchase history
      try {
        const ordersResponse = await api.get('/orders', {
          params: {
            customer_name: customer.name, // Search by customer name since orders don't have customer_id
            limit: 100
          }
        });
        
        if (ordersResponse.data.success) {
          const customerOrders = ordersResponse.data.data.orders || ordersResponse.data.data;
          // Filter orders that match this customer (by name and/or phone)
          const filteredOrders = customerOrders.filter(order => 
            (order.customer_name && order.customer_name.toLowerCase() === customer.name.toLowerCase()) ||
            (order.customer_phone && customer.phone && order.customer_phone === customer.phone)
          );
          setCustomerPurchaseHistory(filteredOrders);
        } else {
          setCustomerPurchaseHistory([]);
        }
      } catch (orderError) {
        console.error('Error fetching purchase history:', orderError);
        setCustomerPurchaseHistory([]);
      }
      
    } catch (error) {
      message.error('Lá»—i khi táº£i thÃ´ng tin khÃ¡ch hÃ ng');
    } finally {
      setPurchaseHistoryLoading(false);
    }
  };

  const columns = [
    {
      title: 'MÃ£ KH',
      dataIndex: 'code',
      key: 'code',
      width: 100,
      render: (text) => <strong>{text}</strong>
    },
    {
      title: 'ThÃ´ng tin khÃ¡ch hÃ ng',
      key: 'customer_info',
      render: (_, record) => (
        <div>
          <div style={{ fontWeight: 'bold', marginBottom: 4 }}>
            <UserOutlined style={{ marginRight: 8 }} />
            {record.name}
          </div>
          {record.phone && (
            <div style={{ color: '#666', fontSize: '12px' }}>
              <PhoneOutlined style={{ marginRight: 4 }} />
              {record.phone}
            </div>
          )}
          {record.email && (
            <div style={{ color: '#666', fontSize: '12px' }}>
              <MailOutlined style={{ marginRight: 4 }} />
              {record.email}
            </div>
          )}
        </div>
      )
    },
    {
      title: 'Loáº¡i KH',
      dataIndex: 'customer_type',
      key: 'customer_type',
      width: 120,
      render: (type) => {
        const typeConfig = customerTypes.find(t => t.value === type);
        return (
          <Tag color={typeConfig?.color} icon={type === 'vip' ? <CrownOutlined /> : null}>
            {typeConfig?.label}
          </Tag>
        );
      }
    },
    {
      title: 'Tá»•ng chi tiÃªu',
      dataIndex: 'total_spent',
      key: 'total_spent',
      width: 120,
      render: (amount) => (
        <span style={{ color: '#52c41a', fontWeight: 'bold' }}>
          {formatCurrency(amount || 0)}
        </span>
      ),
      sorter: (a, b) => (a.total_spent || 0) - (b.total_spent || 0)
    },
    {
      title: 'Sá»‘ láº§n mua',
      dataIndex: 'visit_count',
      key: 'visit_count',
      width: 100,
      render: (count) => (
        <span>
          <ShoppingCartOutlined style={{ marginRight: 4 }} />
          {count || 0}
        </span>
      ),
      sorter: (a, b) => (a.visit_count || 0) - (b.visit_count || 0)
    },
    {
      title: 'Chiáº¿t kháº¥u',
      dataIndex: 'discount_rate',
      key: 'discount_rate',
      width: 100,
      render: (rate) => (
        <span style={{ color: '#fa8c16' }}>
          {rate || 0}%
        </span>
      )
    },
    {
      title: 'Láº§n cuá»‘i mua',
      dataIndex: 'last_visit',
      key: 'last_visit',
      width: 130,
      render: (date) => date ? formatDate(date) : 'ChÆ°a mua'
    },
    {
      title: 'Thao tÃ¡c',
      key: 'actions',
      width: 150,
      render: (_, record) => (
        <Space>
          <Tooltip title="Xem chi tiáº¿t">
            <Button
              type="default"
              size="small"
              icon={<EyeOutlined />}
              onClick={() => showCustomerDetail(record)}
            />
          </Tooltip>
          <Tooltip title="Chá»‰nh sá»­a">
            <Button
              type="primary"
              size="small"
              icon={<EditOutlined />}
              onClick={() => showModal(record)}
            />
          </Tooltip>
          <Popconfirm
            title="Báº¡n cÃ³ cháº¯c cháº¯n muá»‘n xÃ³a khÃ¡ch hÃ ng nÃ y?"
            onConfirm={() => handleDelete(record.id)}
            okText="CÃ³"
            cancelText="KhÃ´ng"
          >
            <Tooltip title="XÃ³a">
              <Button
                type="default"
                danger
                size="small"
                icon={<DeleteOutlined />}
              />
            </Tooltip>
          </Popconfirm>
        </Space>
      )
    }
  ];

  return (
    <div style={{ padding: '24px' }}>
      {/* Header vá»›i thá»‘ng kÃª */}
      <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
        <Col span={6}>
          <Card>
            <Statistic
              title="Tá»•ng khÃ¡ch hÃ ng"
              value={stats.total}
              prefix={<UserOutlined />}
              valueStyle={{ color: '#1890ff' }}
            />
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <Statistic
              title="KhÃ¡ch thÆ°á»ng"
              value={stats.regular}
              valueStyle={{ color: '#52c41a' }}
            />
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <Statistic
              title="KhÃ¡ch VIP"
              value={stats.vip}
              prefix={<CrownOutlined />}
              valueStyle={{ color: '#faad14' }}
            />
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <Statistic
              title="KhÃ¡ch bÃ¡n sá»‰"
              value={stats.wholesale}
              valueStyle={{ color: '#722ed1' }}
            />
          </Card>
        </Col>
      </Row>

      {/* Main Content */}
      <Card title="Quáº£n lÃ½ khÃ¡ch hÃ ng">
        {/* Filters */}
        <Row gutter={[16, 16]} style={{ marginBottom: 16 }}>
          <Col span={8}>
            <Search
              placeholder="TÃ¬m theo tÃªn, SÄT, mÃ£ KH..."
              value={searchText}
              onChange={(e) => setSearchText(e.target.value)}
              onSearch={fetchCustomers}
              enterButton={<SearchOutlined />}
              allowClear
            />
          </Col>
          <Col span={6}>
            <Select
              style={{ width: '100%' }}
              placeholder="Lá»c theo loáº¡i khÃ¡ch hÃ ng"
              value={filterType}
              onChange={setFilterType}
            >
              <Option value="all">Táº¥t cáº£</Option>
              {customerTypes.map(type => (
                <Option key={type.value} value={type.value}>
                  {type.label}
                </Option>
              ))}
            </Select>
          </Col>
          <Col span={10} style={{ textAlign: 'right' }}>
            <Button
              type="primary"
              icon={<PlusOutlined />}
              onClick={() => showModal()}
              size="large"
            >
              ThÃªm khÃ¡ch hÃ ng
            </Button>
          </Col>
        </Row>

        {/* Table */}
        <Table
          columns={columns}
          dataSource={customers}
          rowKey="id"
          loading={loading}
          pagination={{
            total: customers.length,
            pageSize: 10,
            showSizeChanger: true,
            showQuickJumper: true,
            showTotal: (total, range) => 
              `${range[0]}-${range[1]} cá»§a ${total} khÃ¡ch hÃ ng`
          }}
          scroll={{ x: 1200 }}
        />
      </Card>

      {/* Modal thÃªm/sá»­a khÃ¡ch hÃ ng */}
      <Modal
        title={editingCustomer ? 'Cáº­p nháº­t khÃ¡ch hÃ ng' : 'ThÃªm khÃ¡ch hÃ ng má»›i'}
        visible={isModalVisible}
        onCancel={handleCancel}
        footer={null}
        width={600}
      >
        <Form
          form={form}
          layout="vertical"
          onFinish={handleSubmit}
          autoComplete="off"
        >
          <Row gutter={16}>
            <Col span={12}>
              <Form.Item
                label="TÃªn khÃ¡ch hÃ ng"
                name="name"
                rules={[{ required: true, message: 'Vui lÃ²ng nháº­p tÃªn khÃ¡ch hÃ ng' }]}
              >
                <Input prefix={<UserOutlined />} placeholder="Nháº­p tÃªn khÃ¡ch hÃ ng" />
              </Form.Item>
            </Col>
            <Col span={12}>
              <Form.Item
                label="Sá»‘ Ä‘iá»‡n thoáº¡i"
                name="phone"
              >
                <Input prefix={<PhoneOutlined />} placeholder="Nháº­p sá»‘ Ä‘iá»‡n thoáº¡i" />
              </Form.Item>
            </Col>
          </Row>

          <Row gutter={16}>
            <Col span={12}>
              <Form.Item
                label="Email"
                name="email"
                rules={[{ type: 'email', message: 'Email khÃ´ng há»£p lá»‡' }]}
              >
                <Input prefix={<MailOutlined />} placeholder="Nháº­p email" />
              </Form.Item>
            </Col>
            <Col span={12}>
              <Form.Item
                label="Äá»‹a chá»‰"
                name="address"
              >
                <Input.TextArea rows={2} placeholder="Nháº­p Ä‘á»‹a chá»‰" />
              </Form.Item>
            </Col>
          </Row>

          <Row gutter={16}>
            <Col span={12}>
              <Form.Item
                label="Loáº¡i khÃ¡ch hÃ ng"
                name="customer_type"
                initialValue="regular"
              >
                <Select>
                  {customerTypes.map(type => (
                    <Option key={type.value} value={type.value}>
                      {type.label}
                    </Option>
                  ))}
                </Select>
              </Form.Item>
            </Col>
            <Col span={12}>
              <Form.Item
                label="Chiáº¿t kháº¥u (%)"
                name="discount_rate"
                initialValue={0}
              >
                <Input type="number" min={0} max={100} placeholder="0" />
              </Form.Item>
            </Col>
          </Row>

          <Form.Item
            label="Ghi chÃº"
            name="notes"
          >
            <Input.TextArea rows={3} placeholder="Nháº­p ghi chÃº" />
          </Form.Item>

          <Form.Item style={{ textAlign: 'right', marginBottom: 0 }}>
            <Space>
              <Button onClick={handleCancel}>
                Há»§y
              </Button>
              <Button type="primary" htmlType="submit">
                {editingCustomer ? 'Cáº­p nháº­t' : 'ThÃªm má»›i'}
              </Button>
            </Space>
          </Form.Item>
        </Form>
      </Modal>

      {/* ðŸ†• Enhanced Customer Details Drawer */}
      <Drawer
        title={
          <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
            <UserOutlined style={{ color: '#1890ff' }} />
            <span>ThÃ´ng tin chi tiáº¿t khÃ¡ch hÃ ng</span>
            {selectedCustomer && (
              <Tag color={customerTypes.find(t => t.value === selectedCustomer.customer_type)?.color}>
                {customerTypes.find(t => t.value === selectedCustomer.customer_type)?.label}
              </Tag>
            )}
          </div>
        }
        placement="right"
        onClose={() => {
          setIsDetailDrawerVisible(false);
          setCustomerPurchaseHistory([]);
        }}
        visible={isDetailDrawerVisible}
        width={800}
        style={{ zIndex: 1001 }}
      >
        {selectedCustomer && (
          <div>
            {/* ðŸ†• Enhanced Basic Information with Address */}
            <Card 
              title="ðŸ“‹ ThÃ´ng tin cÆ¡ báº£n" 
              style={{ marginBottom: 16 }}
              extra={
                <Button 
                  size="small" 
                  icon={<EditOutlined />}
                  onClick={() => {
                    setIsDetailDrawerVisible(false);
                    showModal(selectedCustomer);
                  }}
                >
                  Chá»‰nh sá»­a
                </Button>
              }
            >
              <Descriptions size="small" column={2}>
                <Descriptions.Item label="MÃ£ khÃ¡ch hÃ ng" span={1}>
                  <Text strong style={{ color: '#1890ff' }}>{selectedCustomer.code}</Text>
                </Descriptions.Item>
                <Descriptions.Item label="TÃªn khÃ¡ch hÃ ng" span={1}>
                  <Text strong>{selectedCustomer.name}</Text>
                </Descriptions.Item>
                <Descriptions.Item label="Äiá»‡n thoáº¡i" span={1}>
                  <Text copyable={!!selectedCustomer.phone}>
                    <PhoneOutlined style={{ marginRight: '4px', color: '#52c41a' }} />
                    {selectedCustomer.phone || 'ChÆ°a cÃ³'}
                  </Text>
                </Descriptions.Item>
                <Descriptions.Item label="Email" span={1}>
                  <Text copyable={!!selectedCustomer.email}>
                    <MailOutlined style={{ marginRight: '4px', color: '#1890ff' }} />
                    {selectedCustomer.email || 'ChÆ°a cÃ³'}
                  </Text>
                </Descriptions.Item>
                <Descriptions.Item label="Loáº¡i khÃ¡ch hÃ ng" span={1}>
                  <Tag 
                    color={customerTypes.find(t => t.value === selectedCustomer.customer_type)?.color}
                    icon={selectedCustomer.customer_type === 'vip' ? <CrownOutlined /> : null}
                  >
                    {customerTypes.find(t => t.value === selectedCustomer.customer_type)?.label}
                  </Tag>
                </Descriptions.Item>
                <Descriptions.Item label="Chiáº¿t kháº¥u" span={1}>
                  <Text style={{ color: '#fa8c16', fontWeight: 'bold' }}>
                    {selectedCustomer.discount_rate || 0}%
                  </Text>
                </Descriptions.Item>
                
                {/* ðŸŽ¯ FIX: Address moved into basic info */}
                <Descriptions.Item label="Äá»‹a chá»‰" span={2}>
                  <div style={{ display: 'flex', alignItems: 'flex-start', gap: '4px' }}>
                    <HomeOutlined style={{ marginTop: '4px', color: '#666' }} />
                    <Text>{selectedCustomer.address || 'ChÆ°a cÃ³ Ä‘á»‹a chá»‰'}</Text>
                  </div>
                </Descriptions.Item>
                
                {selectedCustomer.notes && (
                  <Descriptions.Item label="Ghi chÃº" span={2}>
                    <Text type="secondary">{selectedCustomer.notes}</Text>
                  </Descriptions.Item>
                )}
              </Descriptions>
            </Card>

            {/* ðŸ†• Enhanced Purchase Statistics & History with Tabs */}
            <Card style={{ marginBottom: 16 }}>
              <Tabs defaultActiveKey="stats" type="card">
                <TabPane 
                  tab={
                    <span>
                      <ShoppingCartOutlined />
                      Thá»‘ng kÃª mua hÃ ng
                    </span>
                  } 
                  key="stats"
                >
                  <Row gutter={[16, 16]}>
                    <Col span={8}>
                      <Card size="small" style={{ textAlign: 'center', borderLeft: '4px solid #52c41a' }}>
                        <Statistic
                          title="Tá»•ng chi tiÃªu"
                          value={selectedCustomer.total_spent || 0}
                          formatter={value => formatCurrency(value)}
                          valueStyle={{ color: '#52c41a', fontSize: '18px' }}
                          prefix={<span style={{ fontSize: '14px' }}>ðŸ’°</span>}
                        />
                      </Card>
                    </Col>
                    <Col span={8}>
                      <Card size="small" style={{ textAlign: 'center', borderLeft: '4px solid #1890ff' }}>
                        <Statistic
                          title="Sá»‘ láº§n mua"
                          value={selectedCustomer.visit_count || 0}
                          suffix="láº§n"
                          valueStyle={{ color: '#1890ff', fontSize: '18px' }}
                          prefix={<span style={{ fontSize: '14px' }}>ðŸ›’</span>}
                        />
                      </Card>
                    </Col>
                    <Col span={8}>
                      <Card size="small" style={{ textAlign: 'center', borderLeft: '4px solid #fa8c16' }}>
                        <Statistic
                          title="ÄÆ¡n hÃ ng"
                          value={customerPurchaseHistory.length}
                          suffix="Ä‘Æ¡n"
                          valueStyle={{ color: '#fa8c16', fontSize: '18px' }}
                          prefix={<span style={{ fontSize: '14px' }}>ðŸ“¦</span>}
                        />
                      </Card>
                    </Col>
                  </Row>
                  <div style={{ marginTop: 16, padding: '12px', background: '#f6f8fa', borderRadius: '6px' }}>
                    <Text strong>Láº§n cuá»‘i mua: </Text>
                    <Text style={{ color: '#52c41a' }}>
                      {selectedCustomer.last_visit ? formatDate(selectedCustomer.last_visit) : 'ChÆ°a mua hÃ ng'}
                    </Text>
                  </div>
                </TabPane>

                <TabPane 
                  tab={
                    <span>
                      <CalendarOutlined />
                      Lá»‹ch sá»­ mua hÃ ng
                      <Text style={{ 
                        marginLeft: '4px', 
                        fontSize: '11px', 
                        background: '#1890ff', 
                        color: 'white', 
                        padding: '2px 6px', 
                        borderRadius: '10px' 
                      }}>
                        {customerPurchaseHistory.length}
                      </Text>
                    </span>
                  } 
                  key="history"
                >
                  <div style={{ maxHeight: '400px', overflowY: 'auto' }}>
                    {purchaseHistoryLoading ? (
                      <div style={{ textAlign: 'center', padding: '40px' }}>
                        <span>Äang táº£i lá»‹ch sá»­ mua hÃ ng...</span>
                      </div>
                    ) : customerPurchaseHistory.length > 0 ? (
                      <Timeline>
                        {customerPurchaseHistory.map((order, index) => (
                          <Timeline.Item 
                            key={order.id || index}
                            dot={<ShoppingCartOutlined style={{ color: '#52c41a' }} />}
                            color="green"
                          >
                            <div style={{ padding: '8px 12px', border: '1px solid #e8f4fd', borderRadius: '8px', background: '#f6ffed' }}>
                              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '8px' }}>
                                <div>
                                  <Text strong style={{ color: '#1890ff', fontSize: '14px' }}>
                                    ðŸ“¦ ÄÆ¡n hÃ ng #{order.order_number || order.id}
                                  </Text>
                                  <div style={{ fontSize: '11px', color: '#666' }}>
                                    ðŸ“… {formatDate(order.created_at)}
                                  </div>
                                </div>
                                <div style={{ textAlign: 'right' }}>
                                  <div style={{ fontSize: '16px', fontWeight: 'bold', color: '#52c41a' }}>
                                    {formatCurrency(order.total_amount)}
                                  </div>
                                  <Tag color={order.status === 'completed' ? 'green' : 'orange'} size="small">
                                    {order.status === 'completed' ? 'HoÃ n thÃ nh' : order.status}
                                  </Tag>
                                </div>
                              </div>
                              
                              {/* Order Items */}
                              {order.items && order.items.length > 0 && (
                                <div style={{ marginTop: '8px', padding: '8px', background: '#fff', borderRadius: '4px' }}>
                                  <Text strong style={{ fontSize: '12px', color: '#666' }}>ðŸ“‹ Sáº£n pháº©m Ä‘Ã£ mua:</Text>
                                  {order.items.slice(0, 3).map((item, itemIndex) => (
                                    <div key={itemIndex} style={{ 
                                      fontSize: '11px', 
                                      color: '#333', 
                                      marginTop: '4px',
                                      paddingLeft: '8px'
                                    }}>
