// QRScanner.js (ƒê√£ s·ª≠a ƒë·ªïi v√† c·∫£i ti·∫øn)
import React, { useEffect, useRef, useState, useCallback } from 'react';
import { Modal, Button, Space, message, Alert, Spin, Typography } from 'antd';
import { CameraOutlined, CloseOutlined, ReloadOutlined, CheckCircleOutlined } from '@ant-design/icons';
import { Html5Qrcode } from 'html5-qrcode';

const { Text } = Typography;

const QRScanner = ({
  visible,
  onClose,
  onScan,
  title = "Qu√©t Serial Number",
  placeholder = "ƒê∆∞a camera v√†o m√£ QR ho·∫∑c Barcode"
}) => {
  const scannerRef = useRef(null); // Ref cho instance c·ªßa Html5Qrcode
  const readerRef = useRef(null); // Ref cho DOM element
  const [isScanning, setIsScanning] = useState(false);
  const [error, setError] = useState(null);
  const [permissionState, setPermissionState] = useState('unknown');

  // H√†m d·ªçn d·∫πp an to√†n
  const stopScanner = useCallback(async () => {
    if (scannerRef.current && scannerRef.current.isScanning) {
      try {
        await scannerRef.current.stop();
      } catch (err) {
        console.warn("L·ªói khi d·ª´ng scanner, c√≥ th·ªÉ b·ªè qua:", err);
      }
    }
    scannerRef.current = null;
    setIsScanning(false);
  }, []);

  // H√†m x·ª≠ l√Ω l·ªói camera t·∫≠p trung
  const handleCameraError = useCallback((err) => {
    let errorMessage = `L·ªói kh√¥ng x√°c ƒë·ªãnh: ${err.message || err.name}`;
    if (err.name === 'NotAllowedError' || err.name === 'PermissionDeniedError') {
      errorMessage = 'Quy·ªÅn truy c·∫≠p camera ƒë√£ b·ªã t·ª´ ch·ªëi. Vui l√≤ng c·∫•p quy·ªÅn trong c√†i ƒë·∫∑t tr√¨nh duy·ªát.';
      setPermissionState('denied');
    } else if (err.name === 'NotFoundError' || err.name === 'DevicesNotFoundError') {
      errorMessage = 'Kh√¥ng t√¨m th·∫•y camera n√†o tr√™n thi·∫øt b·ªã c·ªßa b·∫°n.';
    } else if (err.name === 'NotReadableError') {
      errorMessage = 'Camera ƒëang ƒë∆∞·ª£c s·ª≠ d·ª•ng b·ªüi m·ªôt ·ª©ng d·ª•ng kh√°c.';
    }
    setError(errorMessage);
    stopScanner();
  }, [stopScanner]);

  // Lu·ªìng kh·ªüi t·∫°o scanner ch√≠nh
  const startScannerFlow = useCallback(async () => {
    if (isScanning || !readerRef.current) return;
    setError(null);
    setIsScanning(true);

    try {
      // 1. Ki·ªÉm tra quy·ªÅn (kh√¥ng c·∫ßn thi·∫øt v√¨ th∆∞ vi·ªán t·ª± x·ª≠ l√Ω, nh∆∞ng t·ªët ƒë·ªÉ c√≥ ph·∫£n h·ªìi UI)
      if (navigator.permissions) {
        const perm = await navigator.permissions.query({ name: 'camera' });
        setPermissionState(perm.state);
        perm.onchange = () => setPermissionState(perm.state);
        if (perm.state === 'denied') {
            throw new Error('PermissionDeniedError');
        }
      }
      
      // 2. L·∫•y camera sau (n·∫øu c√≥)
      const devices = await Html5Qrcode.getCameras();
      if (!devices || devices.length === 0) {
        throw new Error('NotFoundError');
      }
      
      const backCamera = devices.find(d => d.label.toLowerCase().includes('back')) || devices[0];
      const cameraId = backCamera.id;
      
      // 3. Kh·ªüi t·∫°o v√† b·∫Øt ƒë·∫ßu scanner
      const html5QrCode = new Html5Qrcode(readerRef.current.id);
      scannerRef.current = html5QrCode;

      await html5QrCode.start(
        cameraId, // <-- S·ª≠a l·ªói quan tr·ªçng: S·ª≠ d·ª•ng deviceId thay v√¨ object
        {
          fps: 10,
          qrbox: { width: 250, height: 250 },
        },
        (decodedText, decodedResult) => {
          // Th√†nh c√¥ng
          stopScanner();
          message.success(`üì± ƒê√£ qu√©t th√†nh c√¥ng: ${decodedText}`);
          onScan(decodedText);
          onClose();
        },
        (errorMessage) => {
          // B·ªè qua l·ªói kh√¥ng t√¨m th·∫•y QR
          if (!errorMessage.includes('NotFoundException')) {
            console.warn(`L·ªói qu√©t QR: ${errorMessage}`);
          }
        }
      );
      setPermissionState('granted');

    } catch (err) {
      handleCameraError(err);
      setIsScanning(false);
    }
  }, [isScanning, onScan, onClose, handleCameraError, stopScanner]);

  // Effect ch√≠nh ƒë·ªÉ qu·∫£n l√Ω v√≤ng ƒë·ªùi scanner
  useEffect(() => {
    if (visible) {
      // D√πng setTimeout ƒë·ªÉ ƒë·∫£m b·∫£o Modal ƒë√£ render v√† readerRef.current t·ªìn t·∫°i
      const timer = setTimeout(() => {
        startScannerFlow();
      }, 300);
      return () => clearTimeout(timer);
    } else {
      stopScanner();
    }
  }, [visible, startScannerFlow, stopScanner]);
  
  // H√†m x·ª≠ l√Ω ƒë√≥ng modal
  const handleClose = () => {
    stopScanner();
    onClose();
  };

  // Render UI
  const renderContent = () => {
    if (error) {
      return (
        <Alert
          message="L·ªói Camera"
          description={error}
          type="error"
          showIcon
          action={
            <Button size="small" type="primary" onClick={startScannerFlow}>
              Th·ª≠ l·∫°i
            </Button>
          }
        />
      );
    }

    if (permissionState === 'prompt') {
        return (
            <Alert
              message="Y√™u c·∫ßu quy·ªÅn truy c·∫≠p Camera"
              description="·ª®ng d·ª•ng c·∫ßn quy·ªÅn truy c·∫≠p camera ƒë·ªÉ qu√©t m√£. Vui l√≤ng b·∫•m 'Cho ph√©p' tr√™n tr√¨nh duy·ªát."
              type="info"
              showIcon
            />
        );
    }

    if (!isScanning && permissionState !== 'granted') {
        return (
            <div style={{ textAlign: 'center', padding: '20px' }}>
                <Spin size="large" tip="ƒêang ch·ªù c·∫•p quy·ªÅn camera..." />
            </div>
        );
    }

    return (
      <>
        <Alert
          message="Camera ƒë√£ s·∫µn s√†ng"
          description={placeholder}
          type="success"
          showIcon
          style={{ marginBottom: 16 }}
        />
        {/* Spinner khi ƒëang kh·ªüi t·∫°o */}
        {isScanning && !scannerRef.current?.isScanning && (
            <div style={{ padding: '40px', textAlign: 'center' }}>
                <Spin size="large" tip="ƒêang kh·ªüi t·∫°o camera..." />
            </div>
        )}
      </>
    );
  };

  return (
    <Modal
      title={
        <Space>
          <CameraOutlined />
          <span>{title}</span>
          {permissionState === 'granted' && <CheckCircleOutlined style={{ color: '#52c41a' }} />}
        </Space>
      }
      open={visible}
      onCancel={handleClose}
      width={Math.min(600, window.innerWidth - 32)}
      footer={[
        <Button key="retry" icon={<ReloadOutlined />} onClick={startScannerFlow}>
          Th·ª≠ l·∫°i
        </Button>,
        <Button key="close" icon={<CloseOutlined />} onClick={handleClose}>
          ƒê√≥ng
        </Button>,
      ]}
      centered
      destroyOnClose // Quan tr·ªçng: H·ªßy component khi ƒë√≥ng ƒë·ªÉ d·ªçn d·∫πp tri·ªát ƒë·ªÉ
    >
      <div style={{ padding: '20px 0' }}>
        {renderContent()}
        <div
          id="qr-reader-container"
          ref={readerRef}
          style={{ 
            width: '100%', 
            minHeight: '250px', 
            border: (error) ? '2px dashed #ff4d4f' : '2px dashed #d9d9d9',
            borderRadius: '8px',
            marginTop: '16px'
        }}
        />
        <Text type="secondary" style={{ display: 'block', textAlign: 'center', marginTop: '16px' }}>
            üí° H·ªó tr·ª£ qu√©t: QR Code & Barcode
        </Text>
      </div>
    </Modal>
  );
};

export default QRScanner;
