import React, { useState, useEffect } from 'react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import {
  Card,
  Table,
  Button,
  Modal,
  Input,
  Select,
  DatePicker,
  Space,
  Tag,
  Row,
  Col,
  Statistic,
  Tabs,
  InputNumber,
  Alert,
  Badge,
  Typography,
} from 'antd';
import {
  PlusOutlined,
  UserOutlined,
  ShopOutlined,
  DollarOutlined,
  ExclamationCircleOutlined,
  CheckCircleOutlined,
  CreditCardOutlined,
  WalletOutlined,
} from '@ant-design/icons';
import { customersAPI, suppliersAPI, financialAPI } from '../services/api';
import dayjs from 'dayjs';

const { Option } = Select;
const { TabPane } = Tabs;
const { Text } = Typography;

// Zod schema for debt payment validation
const debtPaymentSchema = z.object({
  entity_id: z.number().min(1, 'Vui l√≤ng ch·ªçn kh√°ch h√†ng/nh√† cung c·∫•p'),
  amount: z.number().min(1, 'S·ªë ti·ªÅn ph·∫£i l·ªõn h∆°n 0'),
  description: z.string().optional(),
  payment_method: z.enum(['cash', 'card', 'transfer', 'ewallet'], {
    errorMap: () => ({ message: 'Vui l√≤ng ch·ªçn ph∆∞∆°ng th·ª©c thanh to√°n' })
  }),
  transaction_date: z.any().optional(),
});

const DebtPage = () => {
  const [customerDebts, setCustomerDebts] = useState([]);
  const [supplierDebts, setSupplierDebts] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [suppliers, setSuppliers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [modalVisible, setModalVisible] = useState(false);
  const [activeTab, setActiveTab] = useState('customers');
  const [modalType, setModalType] = useState('payment'); // 'payment' or 'debt'
  const [debtStats, setDebtStats] = useState({
    totalCustomerDebt: 0,
    totalSupplierDebt: 0,
    overduCustomerDebt: 0,
    overdueSupplierDebt: 0,
  });

  const {
    register,
    handleSubmit,
    reset,
    setValue,
    watch,
    formState: { errors, isSubmitting },
  } = useForm({
    resolver: zodResolver(debtPaymentSchema),
    defaultValues: {
      payment_method: 'cash',
      transaction_date: dayjs(),
    },
  });

  const paymentMethods = [
    { value: 'cash', label: 'Ti·ªÅn m·∫∑t', icon: <DollarOutlined /> },
    { value: 'card', label: 'Th·∫ª', icon: <CreditCardOutlined /> },
    { value: 'transfer', label: 'Chuy·ªÉn kho·∫£n', icon: <WalletOutlined /> },
    { value: 'ewallet', label: 'V√≠ ƒëi·ªán t·ª≠', icon: <WalletOutlined /> },
  ];

  useEffect(() => {
    console.log('üöÄ DebtPage mounted, fetching data...');
    fetchData();
  }, []);

  useEffect(() => {
    console.log('üîÑ Customers state updated:', customers);
  }, [customers]);

  useEffect(() => {
    console.log('üîÑ Suppliers state updated:', suppliers);
  }, [suppliers]);

  // Process debts when customers/suppliers data changes
  useEffect(() => {
    if (customers.length > 0) {
      fetchCustomerDebts();
    }
  }, [customers]); // eslint-disable-line react-hooks/exhaustive-deps

  useEffect(() => {
    if (suppliers.length > 0) {
      fetchSupplierDebts();
    }
  }, [suppliers]); // eslint-disable-line react-hooks/exhaustive-deps

  const fetchData = async () => {
    setLoading(true);
    try {
      // First fetch all customers and suppliers
      await Promise.all([
        fetchCustomers(),
        fetchSuppliers(),
      ]);
      
      // Then process debts (this will be done in useEffect when customers/suppliers change)
    } catch (error) {
      toast.error('L·ªói khi t·∫£i d·ªØ li·ªáu');
    } finally {
      setLoading(false);
    }
  };

  const fetchCustomerDebts = async () => {
    try {
      // Use customers data that's already fetched and enhanced
      const customersWithDebt = customers.filter(c => (c.current_debt || 0) > 0);
      setCustomerDebts(customersWithDebt);
      
      // Calculate debt stats
      const totalDebt = customersWithDebt.reduce((sum, c) => sum + (c.current_debt || 0), 0);
      setDebtStats(prev => ({ ...prev, totalCustomerDebt: totalDebt }));
    } catch (error) {
      console.error('Error processing customer debts:', error);
      setCustomerDebts([]);
    }
  };

  const fetchSupplierDebts = async () => {
    try {
      // Use suppliers data that's already fetched and enhanced
      const suppliersWithDebt = suppliers.filter(s => (s.current_debt || 0) > 0);
      setSupplierDebts(suppliersWithDebt);
      
      // Calculate debt stats
      const totalDebt = suppliersWithDebt.reduce((sum, s) => sum + (s.current_debt || 0), 0);
      setDebtStats(prev => ({ ...prev, totalSupplierDebt: totalDebt }));
    } catch (error) {
      console.error('Error processing supplier debts:', error);
      setSupplierDebts([]);
    }
  };

  const fetchCustomers = async () => {
    try {
      const response = await customersAPI.getAll();
      console.log('üë• Raw customers response:', response.data);
      
      if (response.data.success) {
        // Calculate debt for each customer (mock calculation for now)
        const customersWithDebt = response.data.data.map(customer => ({
          ...customer,
          current_debt: customer.current_debt || Math.floor(Math.random() * 5000000), // Mock debt for demo
          total_spent: customer.total_spent || Math.floor(Math.random() * 20000000)
        }));
        
        console.log('üí∞ Customers with debt:', customersWithDebt);
        console.log('üìä Total customers for dropdown:', customersWithDebt.length);
        setCustomers(customersWithDebt);
      }
    } catch (error) {
      console.error('Error fetching customers:', error);
      // Fallback: Set empty array to avoid crashes
      setCustomers([]);
    }
  };

  const fetchSuppliers = async () => {
    try {
      const response = await suppliersAPI.getAll();
      console.log('üè≠ Raw suppliers response:', response.data);
      
      if (response.data.success) {
        // Calculate debt for each supplier (mock calculation for now)
        const suppliersWithDebt = response.data.data.map(supplier => ({
          ...supplier,
          current_debt: supplier.current_debt || Math.floor(Math.random() * 10000000), // Mock debt for demo
          total_purchases: supplier.total_purchases || Math.floor(Math.random() * 50000000)
        }));
        
        console.log('üí∞ Suppliers with debt:', suppliersWithDebt);
        console.log('üìä Total suppliers for dropdown:', suppliersWithDebt.length);
        setSuppliers(suppliersWithDebt);
      }
    } catch (error) {
      console.error('Error fetching suppliers:', error);
      // Fallback: Set empty array to avoid crashes
      setSuppliers([]);
    }
  };

  const handlePayDebt = async (data) => {
    try {
      const transactionData = {
        type: activeTab === 'customers' ? 'income' : 'expense',
        category: activeTab === 'customers' ? 'Thu n·ª£ kh√°ch h√†ng' : 'Tr·∫£ n·ª£ nh√† cung c·∫•p',
        amount: data.amount,
        description: data.description,
        customer_id: activeTab === 'customers' ? data.entity_id : null,
        supplier_id: activeTab === 'suppliers' ? data.entity_id : null,
        payment_method: data.payment_method,
        transaction_date: data.transaction_date?.toISOString() || new Date().toISOString(),
        user_id: 1,
      };

      const response = await financialAPI.create(transactionData);
      if (response.data.success) {
        toast.success('Ghi nh·∫≠n thanh to√°n th√†nh c√¥ng');
        setModalVisible(false);
        reset();
        fetchData();
      }
    } catch (error) {
      toast.error('L·ªói khi ghi nh·∫≠n thanh to√°n');
    }
  };

  const customerColumns = [
    {
      title: 'M√£ KH',
      dataIndex: 'code',
      key: 'code',
      width: 100,
    },
    {
      title: 'T√™n kh√°ch h√†ng',
      dataIndex: 'name',
      key: 'name',
      render: (name, record) => (
        <div>
          <div>{name}</div>
          {record.phone && (
            <Text type="secondary" style={{ fontSize: 12 }}>
              {record.phone}
            </Text>
          )}
        </div>
      ),
    },
    {
      title: 'Lo·∫°i KH',
      dataIndex: 'customer_type',
      key: 'customer_type',
      render: (type) => {
        const colors = {
          regular: 'default',
          vip: 'gold',
          wholesale: 'blue',
        };
        const labels = {
          regular: 'Th∆∞·ªùng',
          vip: 'VIP',
          wholesale: 'S·ªâ',
        };
        return <Tag color={colors[type]}>{labels[type]}</Tag>;
      },
    },
    {
      title: 'S·ªë n·ª£',
      dataIndex: 'current_debt',
      key: 'current_debt',
      render: (debt) => (
        <Text strong style={{ color: debt > 0 ? '#ff4d4f' : '#52c41a' }}>
          {debt.toLocaleString('vi-VN')} ‚Ç´
        </Text>
      ),
      sorter: (a, b) => a.current_debt - b.current_debt,
    },
    {
      title: 'T·ªïng chi ti√™u',
      dataIndex: 'total_spent',
      key: 'total_spent',
      render: (spent) => (
        <Text type="secondary">
          {spent ? spent.toLocaleString('vi-VN') : 0} ‚Ç´
        </Text>
      ),
    },
    {
      title: 'Tr·∫°ng th√°i',
      key: 'status',
      render: (_, record) => {
        const debt = record.current_debt || 0;
        if (debt === 0) {
          return <Badge status="success" text="ƒê√£ thanh to√°n" />;
        } else if (debt > 1000000) {
          return <Badge status="error" text="N·ª£ l·ªõn" />;
        } else {
          return <Badge status="warning" text="C√≥ n·ª£" />;
        }
      },
    },
    {
      title: 'Thao t√°c',
      key: 'actions',
      render: (_, record) => (
        <Space>
          <Button
            type="primary"
            size="small"
            icon={<DollarOutlined />}
            onClick={() => {
              setModalType('payment');
              setActiveTab('customers');
              setModalVisible(true);
              reset({
                entity_id: record.id,
                payment_method: 'cash',
                transaction_date: dayjs(),
              });
            }}
          >
            Thanh to√°n
          </Button>
        </Space>
      ),
    },
  ];

  const supplierColumns = [
    {
      title: 'M√£ NCC',
      dataIndex: 'code',
      key: 'code',
      width: 100,
    },
    {
      title: 'T√™n nh√† cung c·∫•p',
      dataIndex: 'name',
      key: 'name',
      render: (name, record) => (
        <div>
          <div>{name}</div>
          {record.phone && (
            <Text type="secondary" style={{ fontSize: 12 }}>
              {record.phone}
            </Text>
          )}
        </div>
      ),
    },
    {
      title: 'ƒê·ªãa ch·ªâ',
      dataIndex: 'address',
      key: 'address',
      ellipsis: true,
    },
    {
      title: 'S·ªë n·ª£',
      dataIndex: 'current_debt',
      key: 'current_debt',
      render: (debt) => (
        <Text strong style={{ color: debt > 0 ? '#ff4d4f' : '#52c41a' }}>
          {debt.toLocaleString('vi-VN')} ‚Ç´
        </Text>
      ),
      sorter: (a, b) => a.current_debt - b.current_debt,
    },
    {
      title: 'Tr·∫°ng th√°i',
      key: 'status',
      render: (_, record) => {
        const debt = record.current_debt || 0;
        if (debt === 0) {
          return <Badge status="success" text="ƒê√£ thanh to√°n" />;
        } else if (debt > 5000000) {
          return <Badge status="error" text="N·ª£ l·ªõn" />;
        } else {
          return <Badge status="warning" text="C√≥ n·ª£" />;
        }
      },
    },
    {
      title: 'Thao t√°c',
      key: 'actions',
      render: (_, record) => (
        <Space>
          <Button
            type="primary"
            size="small"
            icon={<DollarOutlined />}
            onClick={() => {
              setModalType('payment');
              setActiveTab('suppliers');
              setModalVisible(true);
              reset({
                entity_id: record.id,
                payment_method: 'cash',
                transaction_date: dayjs(),
              });
            }}
          >
            Thanh to√°n
          </Button>
        </Space>
      ),
    },
  ];

  const renderDebtSummary = () => {
    return (
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col span={6}>
          <Card>
            <Statistic
              title="T·ªïng n·ª£ kh√°ch h√†ng"
              value={debtStats.totalCustomerDebt}
              precision={0}
              valueStyle={{ color: '#cf1322' }}
              prefix={<UserOutlined />}
              suffix="‚Ç´"
            />
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <Statistic
              title="T·ªïng n·ª£ nh√† cung c·∫•p"
              value={debtStats.totalSupplierDebt}
              precision={0}
              valueStyle={{ color: '#cf1322' }}
              prefix={<ShopOutlined />}
              suffix="‚Ç´"
            />
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <Statistic
              title="T·ªïng n·ª£ ph·∫£i thu"
              value={debtStats.totalCustomerDebt + debtStats.totalSupplierDebt}
              precision={0}
              valueStyle={{ color: '#1890ff' }}
              prefix={<DollarOutlined />}
              suffix="‚Ç´"
            />
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <Statistic
              title="S·ªë kh√°ch h√†ng c√≥ n·ª£"
              value={customerDebts.length}
              valueStyle={{ color: '#52c41a' }}
              prefix={<UserOutlined />}
            />
          </Card>
        </Col>
      </Row>
    );
  };

  return (
    <div style={{ padding: 24 }}>
      {renderDebtSummary()}

      <Alert
        message="Qu·∫£n l√Ω n·ª£"
        description="Theo d√µi v√† qu·∫£n l√Ω c√°c kho·∫£n n·ª£ c·ªßa kh√°ch h√†ng v√† nh√† cung c·∫•p. H·ªá th·ªëng s·∫Ω t·ª± ƒë·ªông c·∫≠p nh·∫≠t s·ªë d∆∞ n·ª£ khi c√≥ giao d·ªãch thanh to√°n."
        type="info"
        showIcon
        style={{ marginBottom: 24 }}
      />

      <Tabs activeKey={activeTab} onChange={setActiveTab}>
        <TabPane
          tab={
            <span>
              <UserOutlined />
              N·ª£ kh√°ch h√†ng ({customerDebts.length})
            </span>
          }
          key="customers"
        >
          <Card
            title="Danh s√°ch kh√°ch h√†ng c√≥ n·ª£"
            extra={
              <Button
                type="primary"
                icon={<PlusOutlined />}
                onClick={() => {
                  setModalType('payment');
                  setActiveTab('customers');
                  setModalVisible(true);
                  reset({
                    payment_method: 'cash',
                    transaction_date: dayjs(),
                  });
                }}
              >
                Ghi nh·∫≠n thanh to√°n
              </Button>
            }
          >
            <Table
              columns={customerColumns}
              dataSource={customerDebts}
              loading={loading}
              rowKey="id"
              pagination={{
                showSizeChanger: true,
                showQuickJumper: true,
                showTotal: (total, range) =>
                  `${range[0]}-${range[1]} c·ªßa ${total} kh√°ch h√†ng`,
              }}
            />
          </Card>
        </TabPane>

        <TabPane
          tab={
            <span>
              <ShopOutlined />
              N·ª£ nh√† cung c·∫•p ({supplierDebts.length})
            </span>
          }
          key="suppliers"
        >
          <Card
            title="Danh s√°ch nh√† cung c·∫•p c√≥ n·ª£"
            extra={
              <Button
                type="primary"
                icon={<PlusOutlined />}
                onClick={() => {
                  setModalType('payment');
                  setActiveTab('suppliers');
                  setModalVisible(true);
                  reset({
                    payment_method: 'cash',
                    transaction_date: dayjs(),
                  });
                }}
              >
                Ghi nh·∫≠n thanh to√°n
              </Button>
            }
          >
            <Table
              columns={supplierColumns}
              dataSource={supplierDebts}
              loading={loading}
              rowKey="id"
              pagination={{
                showSizeChanger: true,
                showQuickJumper: true,
                showTotal: (total, range) =>
                  `${range[0]}-${range[1]} c·ªßa ${total} nh√† cung c·∫•p`,
              }}
            />
          </Card>
        </TabPane>
      </Tabs>

      <Modal
        title={`Ghi nh·∫≠n thanh to√°n ${activeTab === 'customers' ? 'n·ª£ kh√°ch h√†ng' : 'n·ª£ nh√† cung c·∫•p'}`}
        open={modalVisible}
        onCancel={() => setModalVisible(false)}
        footer={null}
        width={600}
      >
        <form onSubmit={handleSubmit(handlePayDebt)}>
          <div style={{ marginBottom: 16 }}>
            <label style={{ display: 'block', marginBottom: 4 }}>
              {activeTab === 'customers' ? 'Kh√°ch h√†ng' : 'Nh√† cung c·∫•p'} <span style={{ color: 'red' }}>*</span>
            </label>
            <Select
              placeholder={`Ch·ªçn ${activeTab === 'customers' ? 'kh√°ch h√†ng' : 'nh√† cung c·∫•p'}`}
              style={{ width: '100%' }}
              onChange={(value) => setValue('entity_id', value)}
              value={watch('entity_id')}
              showSearch
              filterOption={(input, option) =>
                option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
              }
            >
              {(() => {
                const entities = activeTab === 'customers' ? customers : suppliers;
                console.log(`üîç Rendering dropdown for ${activeTab}:`, entities);
                console.log(`üìã Total entities to render: ${entities.length}`);
                
                return entities.map((entity) => (
                  <Option key={entity.id} value={entity.id}>
                    <div>
                      <div>{entity.name}</div>
                      {entity.phone && (
                        <Text type="secondary" style={{ fontSize: 12 }}>
                          üìû {entity.phone}
                        </Text>
                      )}
                      <div>
                        <Text type="secondary" style={{ fontSize: 12 }}>
                          N·ª£ hi·ªán t·∫°i: {(entity.current_debt || 0).toLocaleString('vi-VN')} ‚Ç´
                        </Text>
                      </div>
                    </div>
                  </Option>
                ));
              })()}
            </Select>
            {errors.entity_id && (
              <div style={{ color: 'red', fontSize: 12 }}>
                {errors.entity_id.message}
              </div>
            )}
          </div>

          <Row gutter={16}>
            <Col span={12}>
              <div style={{ marginBottom: 16 }}>
                <label style={{ display: 'block', marginBottom: 4 }}>
                  S·ªë ti·ªÅn thanh to√°n <span style={{ color: 'red' }}>*</span>
                </label>
                <InputNumber
                  {...register('amount', { valueAsNumber: true })}
                  placeholder="Nh·∫≠p s·ªë ti·ªÅn"
                  style={{ width: '100%' }}
                  formatter={(value) => `${value}`.replace(/\B(?=(\d{3})+(?!\d))/g, ',')}
                  parser={(value) => value.replace(/\$\s?|(,*)/g, '')}
                  min={0}
                />
                {errors.amount && (
                  <div style={{ color: 'red', fontSize: 12 }}>
                    {errors.amount.message}
                  </div>
                )}
              </div>
            </Col>
            <Col span={12}>
              <div style={{ marginBottom: 16 }}>
                <label style={{ display: 'block', marginBottom: 4 }}>
                  Ph∆∞∆°ng th·ª©c thanh to√°n <span style={{ color: 'red' }}>*</span>
                </label>
                <Select
                  placeholder="Ch·ªçn ph∆∞∆°ng th·ª©c"
                  style={{ width: '100%' }}
                  onChange={(value) => setValue('payment_method', value)}
                  value={watch('payment_method')}
                >
                  {paymentMethods.map((method) => (
                    <Option key={method.value} value={method.value}>
                      <Space>
                        {method.icon}
                        {method.label}
                      </Space>
                    </Option>
                  ))}
                </Select>
                {errors.payment_method && (
                  <div style={{ color: 'red', fontSize: 12 }}>
                    {errors.payment_method.message}
                  </div>
                )}
              </div>
            </Col>
          </Row>

          <div style={{ marginBottom: 16 }}>
            <label style={{ display: 'block', marginBottom: 4 }}>
              M√¥ t·∫£
            </label>
            <Input.TextArea
              {...register('description')}
              placeholder="Nh·∫≠p m√¥ t·∫£ thanh to√°n"
              rows={3}
            />
          </div>

          <div style={{ marginBottom: 16 }}>
            <label style={{ display: 'block', marginBottom: 4 }}>
              Ng√†y thanh to√°n
            </label>
            <DatePicker
              {...register('transaction_date')}
              style={{ width: '100%' }}
              onChange={(date) => setValue('transaction_date', date)}
              value={watch('transaction_date')}
            />
          </div>

          <div style={{ textAlign: 'right', marginTop: 24 }}>
            <Space>
              <Button onClick={() => setModalVisible(false)}>
                H·ªßy
              </Button>
              <Button
                type="primary"
                htmlType="submit"
                loading={isSubmitting}
                icon={<DollarOutlined />}
              >
                Ghi nh·∫≠n thanh to√°n
              </Button>
            </Space>
          </div>
        </form>
      </Modal>
    </div>
  );
};

export default DebtPage;
