// üöÄ SUPER AI WEBSITE ANALYZER - COMPREHENSIVE UPGRADE
// Advanced AI-powered website analysis with auto-fix capabilities
// Ph√¢n t√≠ch to√†n di·ªán website v·ªõi kh·∫£ nƒÉng t·ª± ƒë·ªông s·ª≠a l·ªói

class WebsiteAnalyzer {
  constructor() {
    this.analysisResults = [];
    this.isAnalyzing = false;
    this.performanceMetrics = {};
    this.codeQualityIssues = [];
    this.securityConcerns = [];
    this.uiUxProblems = [];
    
    // NEW: Advanced monitoring capabilities
    this.realTimeMonitoring = false;
    this.autoFixEnabled = true;
    this.monitoringInterval = null;
    this.fixedIssuesCount = 0;
    this.criticalAlertsCount = 0;
    
    // NEW: Pattern recognition database
    this.knownPatterns = new Map();
    this.performanceBaseline = null;
    this.lastAnalysisScore = 0;
    
    // NEW: Auto-fix registry
    this.autoFixers = new Map();
    this.initializeAutoFixers();
    
    console.log('üöÄ Super AI Website Analyzer initialized with auto-fix capabilities!');
  }
  
  // ============================================
  // NEW: AUTO-FIX INITIALIZATION
  // ============================================
  
  initializeAutoFixers() {
    // Register auto-fix handlers for common issues
    this.autoFixers.set('missing_alt_text', this.autoFixMissingAltText.bind(this));
    this.autoFixers.set('external_links_security', this.autoFixExternalLinks.bind(this));
    this.autoFixers.set('inline_styles_cleanup', this.autoFixInlineStyles.bind(this));
    this.autoFixers.set('memory_leak_prevention', this.autoFixMemoryLeaks.bind(this));
    this.autoFixers.set('performance_optimization', this.autoFixPerformance.bind(this));
    this.autoFixers.set('accessibility_improvements', this.autoFixAccessibility.bind(this));
    
    console.log('üõ†Ô∏è Auto-fix capabilities registered:', this.autoFixers.size, 'fixers available');
  }
  
  // ============================================
  // NEW: REAL-TIME MONITORING
  // ============================================
  
  startRealTimeMonitoring(intervalMs = 30000) {
    if (this.realTimeMonitoring) {
      console.log('‚ö†Ô∏è Real-time monitoring already active');
      return;
    }
    
    console.log('üîÑ Starting real-time website monitoring...');
    this.realTimeMonitoring = true;
    
    this.monitoringInterval = setInterval(async () => {
      try {
        const quickAnalysis = await this.performQuickAnalysis();
        await this.handleRealTimeIssues(quickAnalysis);
      } catch (error) {
        console.error('‚ùå Real-time monitoring error:', error);
      }
    }, intervalMs);
    
    // Monitor performance continuously
    this.setupPerformanceObserver();
    this.setupMutationObserver();
    
    console.log('‚úÖ Real-time monitoring started successfully');
  }
  
  stopRealTimeMonitoring() {
    if (this.monitoringInterval) {
      clearInterval(this.monitoringInterval);
      this.monitoringInterval = null;
    }
    this.realTimeMonitoring = false;
    console.log('üõë Real-time monitoring stopped');
  }
  
  // ============================================
  // NEW: QUICK ANALYSIS FOR REAL-TIME
  // ============================================
  
  async performQuickAnalysis() {
    const analysis = {
      timestamp: new Date().toISOString(),
      score: 0,
      issues: [],
      autoFixable: [],
      critical: []
    };
    
    // Quick performance check
    const perfIssues = await this.quickPerformanceCheck();
    analysis.issues.push(...perfIssues);
    
    // Quick security scan
    const securityIssues = await this.quickSecurityScan();
    analysis.issues.push(...securityIssues);
    
    // Quick accessibility check
    const a11yIssues = await this.quickAccessibilityCheck();
    analysis.issues.push(...a11yIssues);
    
    // Calculate score
    analysis.score = this.calculateHealthScore(analysis.issues);
    
    // Categorize issues
    analysis.autoFixable = analysis.issues.filter(issue => issue.autoFixable);
    analysis.critical = analysis.issues.filter(issue => issue.severity === 'critical');
    
    return analysis;
  }
  
  async handleRealTimeIssues(analysis) {
    // Auto-fix if enabled
    if (this.autoFixEnabled && analysis.autoFixable.length > 0) {
      console.log(`üîß Auto-fixing ${analysis.autoFixable.length} issues...`);
      
      for (const issue of analysis.autoFixable) {
        try {
          await this.autoFixIssue(issue);
          this.fixedIssuesCount++;
        } catch (error) {
          console.error('‚ùå Auto-fix failed for:', issue.type, error);
        }
      }
    }
    
    // Alert for critical issues
    if (analysis.critical.length > 0) {
      this.criticalAlertsCount++;
      console.warn('üö® CRITICAL ISSUES DETECTED:', analysis.critical.length);
      this.showCriticalAlert(analysis.critical);
    }
    
    // Performance degradation alert
    if (this.lastAnalysisScore > 0 && analysis.score < this.lastAnalysisScore - 10) {
      console.warn('üìâ Performance degradation detected!');
      this.handlePerformanceDegradation(analysis);
    }
    
    this.lastAnalysisScore = analysis.score;
  }

  // ============================================
  // NEW: AUTO-FIX METHODS
  // ============================================
  
  async autoFixIssue(issue) {
    const fixer = this.autoFixers.get(issue.type);
    if (fixer) {
      console.log('üîß Auto-fixing:', issue.type);
      return await fixer(issue);
    }
    console.warn('‚ö†Ô∏è No auto-fixer available for:', issue.type);
    return false;
  }
  
  async autoFixMissingAltText(issue) {
    const images = document.querySelectorAll('img:not([alt])');
    let fixed = 0;
    
    images.forEach((img, index) => {
      if (img.src.includes('logo')) {
        img.alt = 'Company logo';
      } else if (img.src.includes('icon')) {
        img.alt = 'Icon';
      } else if (img.src.includes('product')) {
        img.alt = 'Product image';
      } else {
        img.alt = `Image ${index + 1}`;
      }
      fixed++;
    });
    
    console.log(`‚úÖ Auto-fixed ${fixed} missing alt texts`);
    return fixed > 0;
  }
  
  async autoFixExternalLinks(issue) {
    const externalLinks = document.querySelectorAll('a[href^="http"]:not([rel*="noopener"])');
    let fixed = 0;
    
    externalLinks.forEach(link => {
      const currentRel = link.getAttribute('rel') || '';
      link.setAttribute('rel', currentRel + ' noopener noreferrer');
      fixed++;
    });
    
    console.log(`‚úÖ Auto-fixed ${fixed} external links security`);
    return fixed > 0;
  }
  
  async autoFixInlineStyles(issue) {
    // Create a style element for common inline styles
    const styleSheet = document.createElement('style');
    styleSheet.id = 'ai-auto-fix-styles';
    
    if (!document.getElementById('ai-auto-fix-styles')) {
      document.head.appendChild(styleSheet);
    }
    
    // Move common inline styles to CSS
    const elementsWithInlineStyle = document.querySelectorAll('[style]');
    let fixed = 0;
    
    elementsWithInlineStyle.forEach((element, index) => {
      const inlineStyle = element.getAttribute('style');
      if (inlineStyle && inlineStyle.length < 50) { // Only simple styles
        const className = `ai-fix-${index}`;
        element.classList.add(className);
        styleSheet.sheet.insertRule(`.${className} { ${inlineStyle} }`);
        element.removeAttribute('style');
        fixed++;
      }
    });
    
    console.log(`‚úÖ Auto-fixed ${fixed} inline styles`);
    return fixed > 0;
  }
  
  async autoFixMemoryLeaks(issue) {
    // Add cleanup for common memory leak patterns
    const cleanup = () => {
      // Clear any abandoned intervals
      for (let i = 1; i < 1000; i++) {
        try {
          clearInterval(i);
          clearTimeout(i);
        } catch (e) {
          // Ignore errors
        }
      }
      
      // Force garbage collection if available
      if (window.gc) {
        window.gc();
      }
    };
    
    // Setup cleanup on page unload
    window.addEventListener('beforeunload', cleanup, { once: true });
    
    console.log('‚úÖ Auto-fixed memory leak prevention setup');
    return true;
  }
  
  async autoFixPerformance(issue) {
    // Lazy load images
    const images = document.querySelectorAll('img:not([loading])');
    images.forEach(img => {
      img.loading = 'lazy';
    });
    
    // Add async to non-critical scripts
    const scripts = document.querySelectorAll('script[src]:not([async]):not([defer])');
    scripts.forEach(script => {
      if (!script.src.includes('critical')) {
        script.defer = true;
      }
    });
    
    console.log('‚úÖ Auto-fixed performance optimizations');
    return true;
  }
  
  async autoFixAccessibility(issue) {
    // Add missing ARIA labels
    const buttons = document.querySelectorAll('button:not([aria-label]):not([aria-labelledby])');
    buttons.forEach((button, index) => {
      if (!button.textContent.trim()) {
        button.setAttribute('aria-label', `Button ${index + 1}`);
      }
    });
    
    // Add focus indicators
    const style = document.createElement('style');
    style.textContent = `
      .ai-focus-fix:focus {
        outline: 2px solid #007bff !important;
        outline-offset: 2px !important;
      }
    `;
    
    if (!document.querySelector('#ai-accessibility-fix')) {
      style.id = 'ai-accessibility-fix';
      document.head.appendChild(style);
    }
    
    const focusableElements = document.querySelectorAll('button, a, input, select, textarea');
    focusableElements.forEach(el => el.classList.add('ai-focus-fix'));
    
    console.log('‚úÖ Auto-fixed accessibility improvements');
    return true;
  }
  
  // ============================================
  // NEW: QUICK ANALYSIS METHODS
  // ============================================
  
  async quickPerformanceCheck() {
    const issues = [];
    
    // Check for render-blocking resources
    const renderBlockingScripts = document.querySelectorAll('script[src]:not([async]):not([defer])');
    if (renderBlockingScripts.length > 3) {
      issues.push({
        type: 'performance_optimization',
        severity: 'medium',
        autoFixable: true,
        issue: 'Render-blocking scripts detected',
        count: renderBlockingScripts.length,
        recommendation: 'Add async/defer attributes to non-critical scripts'
      });
    }
    
    // Check image optimization
    const unoptimizedImages = document.querySelectorAll('img:not([loading="lazy"])');
    if (unoptimizedImages.length > 5) {
      issues.push({
        type: 'performance_optimization',
        severity: 'low',
        autoFixable: true,
        issue: 'Images without lazy loading',
        count: unoptimizedImages.length,
        recommendation: 'Add lazy loading to images'
      });
    }
    
    return issues;
  }
  
  async quickSecurityScan() {
    const issues = [];
    
    // Check for sensitive data exposure
    const scriptTags = document.querySelectorAll('script');
    scriptTags.forEach(script => {
      const content = script.textContent;
      if (content && (content.includes('password') || content.includes('api_key') || content.includes('secret'))) {
        issues.push({
          type: 'security_vulnerability',
          severity: 'critical',
          autoFixable: false,
          issue: 'Potential sensitive data in script',
          recommendation: 'Remove sensitive data from client-side scripts'
        });
      }
    });
    
    return issues;
  }
  
  async quickAccessibilityCheck() {
    const issues = [];
    
    // Quick alt text check
    const imagesWithoutAlt = document.querySelectorAll('img:not([alt])');
    if (imagesWithoutAlt.length > 0) {
      issues.push({
        type: 'missing_alt_text',
        severity: 'medium',
        autoFixable: true,
        issue: 'Images missing alt text',
        count: imagesWithoutAlt.length,
        recommendation: 'Add descriptive alt text to images'
      });
    }
    
    return issues;
  }
  
  // ============================================
  // NEW: PERFORMANCE OBSERVERS
  // ============================================
  
  setupPerformanceObserver() {
    if ('PerformanceObserver' in window) {
      try {
        // Monitor Long Tasks
        const longTaskObserver = new PerformanceObserver((list) => {
          list.getEntries().forEach(entry => {
            if (entry.duration > 50) {
              console.warn('üêå Long task detected:', entry.duration + 'ms');
              this.handleLongTask(entry);
            }
          });
        });
        longTaskObserver.observe({ entryTypes: ['longtask'] });
        
        // Monitor Layout Shifts
        const clsObserver = new PerformanceObserver((list) => {
          list.getEntries().forEach(entry => {
            if (entry.value > 0.1) {
