      let significantChanges = 0;
      
      mutations.forEach(mutation => {
        if (mutation.type === 'childList' && mutation.addedNodes.length > 0) {
          significantChanges++;
        }
      });
      
      if (significantChanges > 10) {
        console.log('üîÑ Significant DOM changes detected, triggering quick analysis...');
        setTimeout(() => this.performQuickAnalysis(), 1000);
      }
    });
    
    observer.observe(document.body, {
      childList: true,
      subtree: true,
      attributes: false
    });
  }
  
