import React, { useState, useEffect } from 'react';
import {
  Card, Table, Button, Input, Space, Modal, Form, Select, message,
  Row, Col, Statistic, Tag, Tooltip, DatePicker, Typography, Alert,
  Tabs, Badge, Descriptions, Divider, Rate, InputNumber
} from 'antd';
import {
  PlusOutlined, SearchOutlined, EditOutlined, EyeOutlined,
  ToolOutlined, ExclamationCircleOutlined, CheckCircleOutlined,
  ClockCircleOutlined, DeleteOutlined, FileTextOutlined
} from '@ant-design/icons';
import { warrantyAPI, customersAPI, productsAPI, usersAPI, formatCurrency, formatDate } from '../services/api';
import LoadingSpinner, { PageLoading, CardLoading } from '../components/LoadingSpinner';
import useAPI from '../hooks/useAPI';
import moment from 'moment';

const { Search } = Input;
const { Option } = Select;
const { TabPane } = Tabs;
const { Text, Title } = Typography;

const WarrantyPage = () => {
  const [claims, setClaims] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [products, setProducts] = useState([]);
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [isDetailModalVisible, setIsDetailModalVisible] = useState(false);
  const [selectedClaim, setSelectedClaim] = useState(null);
  const [searchText, setSearchText] = useState('');
  const [filterStatus, setFilterStatus] = useState('all');
  const [filterPriority, setFilterPriority] = useState('all');
  const [stats, setStats] = useState({});
  const [form] = Form.useForm();

  const claimStatuses = [
    { value: 'pending', label: 'Chờ duyệt', color: 'orange' },
    { value: 'approved', label: 'Đã duyệt', color: 'blue' },
    { value: 'rejected', label: 'Từ chối', color: 'red' },
    { value: 'in_progress', label: 'Đang xử lý', color: 'purple' },
    { value: 'completed', label: 'Hoàn thành', color: 'green' },
    { value: 'cancelled', label: 'Đã hủy', color: 'gray' },
  ];

  const claimTypes = [
    { value: 'repair', label: 'Sửa chữa' },
    { value: 'replace', label: 'Thay thế' },
    { value: 'refund', label: 'Hoàn tiền' },
  ];

  const priorities = [
    { value: 'low', label: 'Thấp', color: 'green' },
    { value: 'normal', label: 'Bình thường', color: 'blue' },
    { value: 'high', label: 'Cao', color: 'orange' },
    { value: 'urgent', label: 'Khẩn cấp', color: 'red' },
  ];

  useEffect(() => {
    loadData();
    loadStats();
  }, []);

  useEffect(() => {
    fetchClaims();
  }, [searchText, filterStatus, filterPriority]);

  const loadData = async () => {
    try {
      const [customersRes, productsRes, usersRes] = await Promise.all([
        customersAPI.getAll(),
        productsAPI.getAll(),
        usersAPI.getAll()
      ]);
      
      if (customersRes.data.success) setCustomers(customersRes.data.data);
      if (productsRes.data.success) setProducts(productsRes.data.data);
      if (usersRes.data.success) setUsers(usersRes.data.data);
    } catch (error) {
      console.error('Error loading data:', error);
    }
  };

  const statsAPI = useAPI({
    showErrorMessage: false, // We'll handle errors gracefully
    onError: (error) => {
      console.warn('Warranty stats failed, using fallback data');
      // Set fallback stats data
      setStats({
        total_claims: 0,
        pending_claims: 0,
        in_progress_claims: 0,
        completed_claims: 0,
        avg_resolution_days: 0
      });
    }
  });

  const loadStats = async () => {
    try {
      await statsAPI.executeAPI(() => warrantyAPI.getStats());
      if (statsAPI.data && statsAPI.data.success) {
        setStats(statsAPI.data.data);
      }
    } catch (error) {
      // Error is already handled by useAPI hook
      console.log('Stats API call completed with fallback data');
    }
  };

  const fetchClaims = async () => {
    try {
      setLoading(true);
      const params = {
        serial_number: searchText,
        status: filterStatus !== 'all' ? filterStatus : undefined,
        priority: filterPriority !== 'all' ? filterPriority : undefined,
      };
      
      const response = await warrantyAPI.getClaims(params);
      if (response.data.success) {
        setClaims(response.data.data);
      }
    } catch (error) {
      console.error('Error fetching claims:', error);
    } finally {
      setLoading(false);
    }
  };

  const showModal = (claim = null) => {
    setSelectedClaim(claim);
    setIsModalVisible(true);
    if (claim) {
      form.setFieldsValue({
        ...claim,
        warranty_start_date: claim.warranty_start_date ? moment(claim.warranty_start_date) : null,
        warranty_end_date: claim.warranty_end_date ? moment(claim.warranty_end_date) : null,
        expected_completion_date: claim.expected_completion_date ? moment(claim.expected_completion_date) : null,
        actual_completion_date: claim.actual_completion_date ? moment(claim.actual_completion_date) : null,
        sent_to_supplier_date: claim.sent_to_supplier_date ? moment(claim.sent_to_supplier_date) : null,
        received_from_supplier_date: claim.received_from_supplier_date ? moment(claim.received_from_supplier_date) : null,
      });
    } else {
      form.resetFields();
    }
  };

  const showDetailModal = async (claim) => {
    try {
      const response = await warrantyAPI.getClaimById(claim.id);
      if (response.data.success) {
        setSelectedClaim(response.data.data);
        setIsDetailModalVisible(true);
      }
    } catch (error) {
      message.error('Lỗi khi lấy chi tiết bảo hành');
    }
  };

  const handleSubmit = async (values) => {
    try {
      const data = {
        ...values,
        warranty_start_date: values.warranty_start_date?.format('YYYY-MM-DD'),
        warranty_end_date: values.warranty_end_date?.format('YYYY-MM-DD'),
        expected_completion_date: values.expected_completion_date?.format('YYYY-MM-DD'),
        actual_completion_date: values.actual_completion_date?.format('YYYY-MM-DD'),
        sent_to_supplier_date: values.sent_to_supplier_date?.format('YYYY-MM-DD'),
        received_from_supplier_date: values.received_from_supplier_date?.format('YYYY-MM-DD'),
      };

      if (selectedClaim) {
        await warrantyAPI.updateClaim(selectedClaim.id, data);
        message.success('Cập nhật yêu cầu bảo hành thành công');
      } else {
        await warrantyAPI.createClaim(data);
        message.success('Tạo yêu cầu bảo hành thành công');
      }
      
      setIsModalVisible(false);
      fetchClaims();
      loadStats();
    } catch (error) {
      message.error('Lỗi khi lưu yêu cầu bảo hành');
    }
  };

  const handleCompleteWarranty = async (claim) => {
    try {
      await warrantyAPI.updateClaim(claim.id, {
        ...claim,
        status: 'completed',
        actual_completion_date: new Date().toISOString(),
      });
      message.success('Đã hoàn thành yêu cầu bảo hành');
      fetchClaims();
      loadStats();
    } catch (error) {
      message.error('Lỗi khi hoàn thành yêu cầu bảo hành');
    }
  };

  const searchBySerial = async (serialNumber) => {
    if (!serialNumber.trim()) return;
    
    try {
      const response = await warrantyAPI.searchBySerial(serialNumber);
      if (response.data.success && response.data.data.length > 0) {
        setClaims(response.data.data);
        message.success(`Tìm thấy ${response.data.data.length} yêu cầu bảo hành`);
      } else {
        message.info('Không tìm thấy thông tin bảo hành cho serial này');
        setClaims([]);
      }
    } catch (error) {
      message.error('Lỗi khi tìm kiếm');
    }
  };

  const columns = [
    {
      title: 'Mã yêu cầu',
      dataIndex: 'claim_number',
      key: 'claim_number',
      width: 130,
      render: (text) => <Text code>{text}</Text>
    },
    {
      title: 'Serial Number',
      dataIndex: 'serial_number',
      key: 'serial_number',
      width: 150,
      render: (text) => <Text copyable>{text}</Text>
    },
    {
      title: 'Sản phẩm',
      key: 'product_info',
      width: 200,
      render: (_, record) => (
        <div>
          <div style={{ fontWeight: 'bold' }}>{record.product_name || 'N/A'}</div>
          <div style={{ fontSize: '12px', color: '#666' }}>{record.sku || ''}</div>
        </div>
      )
    },
    {
      title: 'Khách hàng',
      dataIndex: 'customer_name',
      key: 'customer_name',
      width: 150,
    },
    {
      title: 'Vấn đề',
      dataIndex: 'issue_description',
      key: 'issue_description',
      width: 200,
      render: (text) => (
        <Tooltip title={text}>
          <Text ellipsis style={{ maxWidth: 180 }}>{text}</Text>
        </Tooltip>
      )
    },
    {
      title: 'Trạng thái',
      dataIndex: 'status',
      key: 'status',
      width: 120,
      render: (status) => {
        const statusInfo = claimStatuses.find(s => s.value === status);
        return <Tag color={statusInfo?.color}>{statusInfo?.label}</Tag>;
      }
    },
    {
      title: 'Ưu tiên',
      dataIndex: 'priority',
      key: 'priority',
      width: 100,
      render: (priority) => {
        const priorityInfo = priorities.find(p => p.value === priority);
        return <Tag color={priorityInfo?.color}>{priorityInfo?.label}</Tag>;
      }
    },
    {
      title: 'Ngày tạo',
      dataIndex: 'claim_date',
      key: 'claim_date',
      width: 120,
      render: (date) => moment(date).format('DD/MM/YYYY')
    },
    {
      title: 'Thao tác',
      key: 'actions',
      width: 160,
      render: (_, record) => (
        <Space>
          <Tooltip title="Xem chi tiết">
            <Button size="small" icon={<EyeOutlined />} onClick={() => showDetailModal(record)} />
          </Tooltip>
          <Tooltip title="Chỉnh sửa">
            <Button size="small" icon={<EditOutlined />} onClick={() => showModal(record)} />
          </Tooltip>
          {record.status !== 'completed' && (
            <Tooltip title="Hoàn thành">
              <Button 
                size="small" 
                type="primary"
                icon={<CheckCircleOutlined />} 
                onClick={() => handleCompleteWarranty(record)}
                style={{ backgroundColor: '#52c41a', borderColor: '#52c41a' }}
              />
            </Tooltip>
          )}
        </Space>
      )
    }
  ];

  return (
    <div style={{ padding: '24px' }}>
      <Title level={2}>Quản lý Bảo hành</Title>
      
      {/* Stats Cards */}
      <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
        <Col span={6}>
          <Card>
            <Statistic
              title="Tổng yêu cầu"
              value={stats.total_claims || 0}
              prefix={<FileTextOutlined />}
              valueStyle={{ color: '#1890ff' }}
            />
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <Statistic
              title="Chờ duyệt"
              value={stats.pending_claims || 0}
              prefix={<ClockCircleOutlined />}
              valueStyle={{ color: '#faad14' }}
            />
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <Statistic
              title="Đang xử lý"
              value={stats.in_progress_claims || 0}
              prefix={<ToolOutlined />}
              valueStyle={{ color: '#722ed1' }}
            />
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <Statistic
              title="Hoàn thành"
              value={stats.completed_claims || 0}
              prefix={<CheckCircleOutlined />}
              valueStyle={{ color: '#52c41a' }}
            />
          </Card>
        </Col>
      </Row>

      <Card>
        <Row gutter={[16, 16]} style={{ marginBottom: 16 }}>
          <Col span={8}>
            <Search
              placeholder="Tìm theo Serial Number..."
              value={searchText}
              onChange={(e) => setSearchText(e.target.value)}
              onSearch={searchBySerial}
              enterButton={<SearchOutlined />}
              allowClear
            />
          </Col>
          <Col span={4}>
            <Select
              style={{ width: '100%' }}
              placeholder="Trạng thái"
              value={filterStatus}
              onChange={setFilterStatus}
            >
              <Option value="all">Tất cả trạng thái</Option>
              {claimStatuses.map(status => (
                <Option key={status.value} value={status.value}>{status.label}</Option>
              ))}
            </Select>
          </Col>
          <Col span={4}>
            <Select
              style={{ width: '100%' }}
              placeholder="Ưu tiên"
              value={filterPriority}
              onChange={setFilterPriority}
            >
              <Option value="all">Tất cả ưu tiên</Option>
              {priorities.map(priority => (
                <Option key={priority.value} value={priority.value}>{priority.label}</Option>
              ))}
            </Select>
          </Col>
          <Col span={8}>
            <Space style={{ float: 'right' }}>
              <Button
                type="primary"
                icon={<PlusOutlined />}
                onClick={() => showModal()}
              >
                Tạo yêu cầu bảo hành
              </Button>
            </Space>
          </Col>
        </Row>

        <Table
          columns={columns}
          dataSource={claims}
          rowKey="id"
          loading={loading}
          pagination={{
            total: claims.length,
            pageSize: 15,
            showSizeChanger: true,
            showTotal: (total, range) => `${range[0]}-${range[1]} của ${total} yêu cầu`
          }}
          scroll={{ x: 1400 }}
        />
      </Card>

      {/* Create/Edit Modal */}
      <Modal
        title={selectedClaim ? 'Chỉnh sửa yêu cầu bảo hành' : 'Tạo yêu cầu bảo hành'}
        open={isModalVisible}
        onCancel={() => setIsModalVisible(false)}
        footer={null}
        width={800}
      >
        <Form form={form} layout="vertical" onFinish={handleSubmit}>
          <Row gutter={16}>
            <Col span={12}>
              <Form.Item label="Serial Number" name="serial_number" rules={[{ required: true }]}>
                <Input placeholder="Nhập serial number" />
              </Form.Item>
            </Col>
            <Col span={12}>
              <Form.Item label="Sản phẩm" name="product_id">
                <Select placeholder="Chọn sản phẩm" allowClear>
                  {products.map(product => (
                    <Option key={product.id} value={product.id}>{product.name}</Option>
                  ))}
                </Select>
              </Form.Item>
            </Col>
          </Row>

          <Row gutter={16}>
            <Col span={12}>
              <Form.Item label="Khách hàng" name="customer_id">
                <Select placeholder="Chọn khách hàng" allowClear>
                  {customers.map(customer => (
                    <Option key={customer.id} value={customer.id}>{customer.name}</Option>
                  ))}
                </Select>
              </Form.Item>
            </Col>
            <Col span={12}>
              <Form.Item label="Kỹ thuật viên" name="assigned_technician_id">
                <Select placeholder="Chọn kỹ thuật viên" allowClear>
                  {users.map(user => (
                    <Option key={user.id} value={user.id}>{user.full_name}</Option>
                  ))}
                </Select>
              </Form.Item>
            </Col>
          </Row>

          <Form.Item label="Mô tả vấn đề" name="issue_description" rules={[{ required: true }]}>
            <Input.TextArea rows={3} placeholder="Mô tả chi tiết vấn đề..." />
          </Form.Item>

          <Row gutter={16}>
            <Col span={8}>
              <Form.Item label="Loại yêu cầu" name="claim_type">
                <Select placeholder="Chọn loại">
                  {claimTypes.map(type => (
                    <Option key={type.value} value={type.value}>{type.label}</Option>
                  ))}
                </Select>
              </Form.Item>
            </Col>
            <Col span={8}>
              <Form.Item label="Trạng thái" name="status">
                <Select placeholder="Chọn trạng thái">
                  {claimStatuses.map(status => (
                    <Option key={status.value} value={status.value}>{status.label}</Option>
                  ))}
                </Select>
              </Form.Item>
            </Col>
            <Col span={8}>
              <Form.Item label="Ưu tiên" name="priority">
                <Select placeholder="Chọn ưu tiên">
                  {priorities.map(priority => (
                    <Option key={priority.value} value={priority.value}>{priority.label}</Option>
                  ))}
                </Select>
              </Form.Item>
            </Col>
          </Row>

          <Row gutter={16}>
            <Col span={12}>
              <Form.Item label="Ngày bắt đầu BH" name="warranty_start_date">
                <DatePicker style={{ width: '100%' }} format="DD/MM/YYYY" />
              </Form.Item>
            </Col>
            <Col span={12}>
              <Form.Item label="Ngày kết thúc BH" name="warranty_end_date">
                <DatePicker style={{ width: '100%' }} format="DD/MM/YYYY" />
              </Form.Item>
            </Col>
          </Row>

          <Form.Item label="Ghi chú xử lý" name="handler_notes">
            <Input.TextArea rows={2} placeholder="Ghi chú của kỹ thuật viên..." />
          </Form.Item>

          <div style={{ textAlign: 'right' }}>
            <Space>
              <Button onClick={() => setIsModalVisible(false)}>Hủy</Button>
              <Button type="primary" htmlType="submit">Lưu</Button>
            </Space>
          </div>
        </Form>
      </Modal>

      {/* Detail Modal */}
      <Modal
        title={`Chi tiết bảo hành - ${selectedClaim?.claim_number}`}
        open={isDetailModalVisible}
        onCancel={() => setIsDetailModalVisible(false)}
        footer={[
          <Button key="close" onClick={() => setIsDetailModalVisible(false)}>Đóng</Button>
        ]}
        width={900}
      >
        {selectedClaim && (
          <Descriptions bordered column={2} size="small">
            <Descriptions.Item label="Serial Number">{selectedClaim.serial_number}</Descriptions.Item>
            <Descriptions.Item label="Sản phẩm">{selectedClaim.product_name}</Descriptions.Item>
            <Descriptions.Item label="Khách hàng">{selectedClaim.customer_name}</Descriptions.Item>
            <Descriptions.Item label="Kỹ thuật viên">{selectedClaim.technician_name}</Descriptions.Item>
            <Descriptions.Item label="Trạng thái">
              <Tag color={claimStatuses.find(s => s.value === selectedClaim.status)?.color}>
                {claimStatuses.find(s => s.value === selectedClaim.status)?.label}
              </Tag>
            </Descriptions.Item>
            <Descriptions.Item label="Ưu tiên">
              <Tag color={priorities.find(p => p.value === selectedClaim.priority)?.color}>
                {priorities.find(p => p.value === selectedClaim.priority)?.label}
              </Tag>
            </Descriptions.Item>
            <Descriptions.Item label="Ngày tạo">{formatDate(selectedClaim.claim_date)}</Descriptions.Item>
            <Descriptions.Item label="Ngày hoàn thành">
              {selectedClaim.actual_completion_date ? formatDate(selectedClaim.actual_completion_date) : 'Chưa hoàn thành'}
            </Descriptions.Item>
            <Descriptions.Item label="Vấn đề" span={2}>{selectedClaim.issue_description}</Descriptions.Item>
            <Descriptions.Item label="Ghi chú xử lý" span={2}>{selectedClaim.handler_notes || 'Chưa có'}</Descriptions.Item>
          </Descriptions>
        )}
      </Modal>
    </div>
  );
};

export default WarrantyPage;
