// AI-Powered Error Monitor & Auto-Fix System
// T·ª± ƒë·ªông b·∫Øt v√† ph√¢n t√≠ch l·ªói, ƒë∆∞a ra gi·∫£i ph√°p s·ª≠a ch·ªØa

import api from './api';

class AIErrorMonitor {
  constructor() {
    this.errors = [];
    this.apiErrors = [];
    this.consoleErrors = [];
    this.isMonitoring = false;
    this.aiEndpoint = '/ai/analyze-error';
    this.autoFixEnabled = true;
    
    this.init();
  }

  init() {
    console.log('ü§ñ AI Error Monitor kh·ªüi ƒë·ªông...');
    
    // B·∫Øt t·∫•t c·∫£ l·ªói console
    this.interceptConsoleErrors();
    
    // B·∫Øt t·∫•t c·∫£ l·ªói API
    this.interceptAPIErrors();
    
    // B·∫Øt l·ªói React/JS runtime
    this.interceptRuntimeErrors();
    
    // B·∫Øt l·ªói unhandled promise
    this.interceptPromiseErrors();
    
    // B·∫Øt l·ªói network
    this.interceptNetworkErrors();
    
    this.isMonitoring = true;
    console.log('‚úÖ AI Error Monitor ƒë√£ s·∫µn s√†ng - S·∫Ω t·ª± ƒë·ªông s·ª≠a l·ªói!');
  }

  // B·∫Øt t·∫•t c·∫£ l·ªói console (error, warn, debug)
  interceptConsoleErrors() {
    const originalError = console.error;
    const originalWarn = console.warn;
    
    console.error = (...args) => {
      this.captureConsoleError('error', args);
      originalError.apply(console, args);
    };
    
    console.warn = (...args) => {
      this.captureConsoleError('warn', args);
      originalWarn.apply(console, args);
    };
  }

  // B·∫Øt l·ªói API t·ª´ axios interceptor
  interceptAPIErrors() {
    // ƒê√£ c√≥ s·∫µn trong api.js, ta s·∫Ω hook v√†o ƒë√≥
    if (window.axios) {
      window.axios.interceptors.response.use(
        (response) => response,
        (error) => {
          this.captureAPIError(error);
          return Promise.reject(error);
        }
      );
    }
  }

  // B·∫Øt l·ªói runtime JavaScript
  interceptRuntimeErrors() {
    window.addEventListener('error', (event) => {
      this.captureRuntimeError(event);
    });
  }

  // B·∫Øt l·ªói promise kh√¥ng ƒë∆∞·ª£c handle
  interceptPromiseErrors() {
    window.addEventListener('unhandledrejection', (event) => {
      this.capturePromiseError(event);
    });
  }

  // B·∫Øt l·ªói network
  interceptNetworkErrors() {
    const originalFetch = window.fetch;
    window.fetch = async (...args) => {
      try {
        const response = await originalFetch(...args);
        if (!response.ok) {
          this.captureNetworkError(args[0], response);
        }
        return response;
      } catch (error) {
        this.captureNetworkError(args[0], null, error);
        throw error;
      }
    };
  }

  // Ghi l·∫°i l·ªói console
  captureConsoleError(level, args) {
    const error = {
      id: Date.now() + Math.random(),
      type: 'console',
      level: level,
      message: args.join(' '),
      timestamp: new Date().toISOString(),
      url: window.location.href,
      userAgent: navigator.userAgent,
      stack: new Error().stack
    };
    
    this.consoleErrors.push(error);
    this.processError(error);
  }

  // Ghi l·∫°i l·ªói API
  captureAPIError(axiosError) {
    const error = {
      id: Date.now() + Math.random(),
      type: 'api',
      method: axiosError.config?.method?.toUpperCase(),
      url: axiosError.config?.url,
      baseURL: axiosError.config?.baseURL,
      fullURL: `${axiosError.config?.baseURL}${axiosError.config?.url}`,
      status: axiosError.response?.status,
      statusText: axiosError.response?.statusText,
      data: axiosError.response?.data,
      message: axiosError.message,
      timestamp: new Date().toISOString(),
      requestData: axiosError.config?.data,
      headers: axiosError.config?.headers
    };
    
    this.apiErrors.push(error);
    console.log('üî¥ API Error Captured:', error);
    this.processError(error);
  }

  // Ghi l·∫°i l·ªói runtime
  captureRuntimeError(event) {
    const error = {
      id: Date.now() + Math.random(),
      type: 'runtime',
      message: event.message,
      filename: event.filename,
      lineno: event.lineno,
      colno: event.colno,
      stack: event.error?.stack,
      timestamp: new Date().toISOString(),
      url: window.location.href
    };
    
    this.errors.push(error);
    this.processError(error);
  }

  // Ghi l·∫°i l·ªói promise
  capturePromiseError(event) {
    const error = {
      id: Date.now() + Math.random(),
      type: 'promise',
      message: event.reason?.message || event.reason,
      stack: event.reason?.stack,
      timestamp: new Date().toISOString(),
      url: window.location.href
    };
    
    this.errors.push(error);
    this.processError(error);
  }

  // Ghi l·∫°i l·ªói network
  captureNetworkError(url, response, error) {
    const networkError = {
      id: Date.now() + Math.random(),
      type: 'network',
      url: url,
      status: response?.status,
      statusText: response?.statusText,
      message: error?.message || `Network error: ${response?.status}`,
      timestamp: new Date().toISOString()
    };
    
    this.errors.push(networkError);
    this.processError(networkError);
  }

  // X·ª≠ l√Ω l·ªói th√¥ng qua AI
  async processError(error) {
    console.log(`ü§ñ AI ƒëang ph√¢n t√≠ch l·ªói: ${error.type}`, error);
    
    // ‚ú® ENHANCED: Client-side pattern matching tr∆∞·ªõc khi g·ª≠i v·ªÅ AI backend
    const clientSideAnalysis = this.performClientSideAnalysis(error);
    
    if (clientSideAnalysis.canAutoFix) {
      console.log('üîß AI Client-side pattern match found, auto-fixing immediately...');
      await this.applyAutoFix(error, clientSideAnalysis.solution);
      return;
    }
    
    try {
      // G·ª≠i l·ªói ƒë·∫øn AI ƒë·ªÉ ph√¢n t√≠ch
      const aiResponse = await this.sendToAI(error);
      
      if (aiResponse.canAutoFix && this.autoFixEnabled) {
        console.log('üîß AI ƒë√£ t√¨m ra c√°ch s·ª≠a, ƒëang auto-fix...');
        await this.applyAutoFix(error, aiResponse.solution);
      } else {
        console.log('üìã AI g·ª£i √Ω c√°ch s·ª≠a:', aiResponse.suggestion);
        this.showErrorNotification(error, aiResponse);
      }
    } catch (aiError) {
      console.log('‚ùå AI kh√¥ng th·ªÉ ph√¢n t√≠ch l·ªói:', aiError.message);
      this.showBasicErrorNotification(error);
    }
  }

  // ‚ú® NEW: Client-side pattern matching cho c√°c l·ªói ph·ªï bi·∫øn
  performClientSideAnalysis(error) {
    // üéØ CUSTOMER DELETE 404 ERROR - Immediate Recognition
    if (error.type === 'api' && 
        error.method === 'DELETE' && 
        error.url && error.url.includes('/customers/') && 
        error.status === 404) {
      
      console.log('üéØ AI Client-side match: CUSTOMER_DELETE_404_ERROR');
      
      return {
        canAutoFix: true,
        confidence: 0.98,
        pattern: 'CUSTOMER_DELETE_404_ERROR',
        suggestion: 'Kh√°ch h√†ng ƒë√£ b·ªã x√≥a ho·∫∑c kh√¥ng t·ªìn t·∫°i. AI s·∫Ω c·∫≠p nh·∫≠t danh s√°ch v√† th√¥ng b√°o.',
        solution: {
          type: 'customer_delete_404_fix',
          message: 'Kh√°ch h√†ng kh√¥ng t·ªìn t·∫°i ho·∫∑c ƒë√£ b·ªã x√≥a.',
          actions: [
            { type: 'show_user_message', message: 'Kh√°ch h√†ng kh√¥ng t·ªìn t·∫°i ho·∫∑c ƒë√£ b·ªã x√≥a.' },
            { type: 'refresh_customer_list', force: true },
            { type: 'show_success_notification', message: 'Danh s√°ch kh√°ch h√†ng ƒë√£ ƒë∆∞·ª£c c·∫≠p nh·∫≠t' }
          ]
        }
      };
    }
    
    // üéØ SUPPLIER DELETE 404 ERROR - Immediate Recognition
    if (error.type === 'api' && 
        error.method === 'DELETE' && 
        error.url && error.url.includes('/suppliers/') && 
        error.status === 404) {
      
      console.log('üéØ AI Client-side match: SUPPLIER_DELETE_404_ERROR');
      
      return {
        canAutoFix: true,
        confidence: 0.98,
        pattern: 'SUPPLIER_DELETE_404_ERROR',
        suggestion: 'Nh√† cung c·∫•p ƒë√£ b·ªã x√≥a ho·∫∑c kh√¥ng t·ªìn t·∫°i. AI s·∫Ω c·∫≠p nh·∫≠t danh s√°ch.',
        solution: {
          type: 'supplier_delete_404_fix',
          message: 'Nh√† cung c·∫•p kh√¥ng t·ªìn t·∫°i ho·∫∑c ƒë√£ b·ªã x√≥a.',
          actions: [
            { type: 'show_user_message', message: 'Nh√† cung c·∫•p kh√¥ng t·ªìn t·∫°i ho·∫∑c ƒë√£ b·ªã x√≥a.' },
            { type: 'refresh_supplier_list', force: true },
            { type: 'show_success_notification', message: 'Danh s√°ch nh√† cung c·∫•p ƒë√£ ƒë∆∞·ª£c c·∫≠p nh·∫≠t' }
          ]
        }
      };
    }
    
    // üéØ API 500 SERVER ERROR - Critical Pattern
    if (error.type === 'api' && 
        error.status >= 500 && 
        error.status < 600) {
      
      console.log('üéØ AI Client-side match: API_500_SERVER_ERROR');
      
      return {
        canAutoFix: true,
        confidence: 0.95,
        pattern: 'API_500_SERVER_ERROR',
        suggestion: 'Server ƒëang g·∫∑p v·∫•n ƒë·ªÅ nghi√™m tr·ªçng. AI s·∫Ω th·ª≠ retry v√† backup solutions.',
        solution: {
          type: 'api_500_recovery',
          message: 'Server ƒëang g·∫∑p v·∫•n ƒë·ªÅ. AI ƒëang th·ª≠ c√°c gi·∫£i ph√°p kh√¥i ph·ª•c...',
          actions: [
            { type: 'retry_with_backoff', strategy: 'exponential', maxRetries: 3 },
            { type: 'show_user_message', message: 'Server ƒëang g·∫∑p v·∫•n ƒë·ªÅ. AI ƒëang th·ª≠ kh√¥i ph·ª•c...' },
            { type: 'enable_offline_mode', duration: 30000 }
          ]
        }
      };
    }
    
    // üéØ REACT ILLEGAL INVOCATION - Emergency Pattern
    if (error.type === 'console' && 
        error.message && 
        error.message.toLowerCase().includes('illegal invocation')) {
      
      console.log('üéØ AI Client-side match: REACT_ILLEGAL_INVOCATION_ERROR');
      
      return {
        canAutoFix: true,
        confidence: 0.95,
        pattern: 'REACT_ILLEGAL_INVOCATION_ERROR',
        suggestion: 'CRITICAL React DOM error detected. AI implementing emergency recovery.',
        solution: {
          type: 'emergency_react_recovery',
          message: 'ƒêang kh√¥i ph·ª•c t·ª´ l·ªói React critical...',
          actions: [
            { type: 'emergency_page_reload', delay: 2000, message: 'Fixing critical React error...' },
            { type: 'fix_form_bindings', target: 'all_forms' },
            { type: 'enable_safe_mode', duration: 30000 }
          ]
        }
      };
    }
    
    // üéØ NETWORK CONNECTION ERROR
    if (error.type === 'network' || 
        (error.type === 'api' && error.message && error.message.toLowerCase().includes('network'))) {
      
      console.log('üéØ AI Client-side match: NETWORK_CONNECTION_ERROR');
      
      return {
        canAutoFix: true,
        confidence: 0.8,
        pattern: 'NETWORK_CONNECTION_ERROR',
        suggestion: 'L·ªói k·∫øt n·ªëi m·∫°ng. AI implementing retry strategy v√† offline mode.',
        solution: {
          type: 'network_recovery',
          message: 'K·∫øt n·ªëi m·∫°ng g·∫∑p v·∫•n ƒë·ªÅ. AI ƒëang th·ª≠ kh√¥i ph·ª•c...',
          actions: [
            { type: 'retry_with_backoff', strategy: 'exponential', maxRetries: 5 },
            { type: 'enable_offline_mode', cache_strategy: 'last_known_good' },
            { type: 'show_connection_status', real_time: true }
          ]
        }
      };
    }
    
    // No client-side pattern match found
    return {
      canAutoFix: false,
      confidence: 0,
      pattern: 'UNKNOWN',
      suggestion: 'Ch∆∞a nh·∫≠n di·ªán ƒë∆∞·ª£c pattern. G·ª≠i v·ªÅ AI backend ƒë·ªÉ ph√¢n t√≠ch.',
      solution: null
    };
  }

  // G·ª≠i l·ªói ƒë·∫øn AI backend ƒë·ªÉ ph√¢n t√≠ch
  async sendToAI(error) {
    try {
      const response = await api.post(this.aiEndpoint, {
        error: error,
        context: {
          page: window.location.pathname,
          timestamp: new Date().toISOString(),
          userAgent: navigator.userAgent,
          recentErrors: this.getRecentErrors(5),
          systemInfo: {
            url: window.location.href,
            viewport: `${window.innerWidth}x${window.innerHeight}`,
            online: navigator.onLine
          }
        }
      });
      
      return response.data;
    } catch (error) {
      throw new Error('AI service unavailable');
    }
  }

  // √Åp d·ª•ng auto-fix do AI ƒë·ªÅ xu·∫•t
  async applyAutoFix(error, solution) {
    try {
      console.log('üîÑ ƒêang √°p d·ª•ng auto-fix...', solution);
      
      switch (solution.type) {
        case 'reload_page':
          setTimeout(() => window.location.reload(), 2000);
          break;
          
        case 'retry_api':
          await this.retryFailedAPI(error, solution);
          break;
          
        case 'clear_cache':
          localStorage.clear();
          sessionStorage.clear();
          break;
          
        case 'update_ui_state':
          await this.updateUIState(solution.data);
          break;
          
        case 'fix_code':
          console.log('üí° Code fix suggestion:', solution.code);
          break;

        case 'show_user_message':
          this.showUserMessage(solution.message);
          break;

        case 'redirect_to_list':
          this.redirectToList(solution.page);
          break;

        case 'use_fallback_data':
          this.useFallbackData(solution.data);
          break;

        case 'reload_page_section':
          this.reloadPageSection(solution.section);
          break;
          
        // ADVANCED SOLUTIONS - DEEP ANALYSIS
        case 'retry_with_backoff':
          this.retryWithExponentialBackoff(solution);
          break;
          
        case 'enable_offline_mode':
          this.enableOfflineMode(solution);
          break;
          
        case 'add_null_checks':
          this.suggestNullChecks(solution);
          break;
          
        case 'analyze_hook_dependencies':
          this.analyzeHookDependencies(solution);
          break;
          
        case 'implement_error_boundary':
          this.implementErrorBoundary(solution);
          break;
          
        case 'auto_create_customer':
          this.autoCreateCustomer(solution);
          break;
          
        case 'refresh_inventory_data':
          this.refreshInventoryData(solution);
          break;
          
        case 'suggest_alternative_products':
          this.suggestAlternativeProducts(solution);
          break;
          
        case 'refresh_auth_token':
          this.refreshAuthToken(solution);
          break;
          
        case 'highlight_invalid_fields':
          this.highlightInvalidFields(solution);
          break;
          
        // EMERGENCY SOLUTIONS - CRITICAL ERROR HANDLING
        case 'emergency_page_reload':
          this.emergencyPageReload(solution);
          break;
          
        case 'fix_form_bindings':
          this.fixFormBindings(solution);
          break;
          
        case 'reset_component_state':
          this.resetComponentState(solution);
          break;
          
        case 'clear_form_cache':
          this.clearFormCache(solution);
          break;
          
        case 'enable_safe_mode':
          this.enableSafeMode(solution);
          break;
          
        case 'component_recovery':
          this.componentRecovery(solution);
          break;
          
        case 'show_fallback_ui':
          this.showFallbackUI(solution);
          break;
          
        // ‚ú® NEW CLIENT-SIDE SPECIFIC SOLUTIONS
        case 'customer_delete_404_fix':
          await this.handleCustomerDelete404(solution);
          break;
          
        case 'supplier_delete_404_fix':
          await this.handleSupplierDelete404(solution);
          break;
          
        case 'api_500_recovery':
          await this.handleAPI500Recovery(solution);
          break;
          
        case 'emergency_react_recovery':
          await this.handleEmergencyReactRecovery(solution);
          break;
          
        case 'network_recovery':
          await this.handleNetworkRecovery(solution);
          break;
          
        default:
          console.log('ü§ñ AI solution:', solution.description);
      }
      
      console.log('‚úÖ Auto-fix ho√†n th√†nh!');
      this.showSuccessNotification(`AI ƒë√£ t·ª± ƒë·ªông s·ª≠a l·ªói: ${error.message}`);
      
    } catch (fixError) {
      console.log('‚ùå Auto-fix th·∫•t b·∫°i:', fixError.message);
    }
  }

  // Retry API call theo g·ª£i √Ω c·ªßa AI
  async retryFailedAPI(error, solution) {
    if (error.type !== 'api') return;
    
    try {
      const retryConfig = {
        method: error.method?.toLowerCase() || 'get',
        url: error.url,
        data: solution.modifiedData || JSON.parse(error.requestData || '{}'),
        headers: solution.modifiedHeaders || error.headers
      };
      
      console.log('üîÑ Retry API v·ªõi config m·ªõi:', retryConfig);
      const response = await api(retryConfig);
      console.log('‚úÖ API retry th√†nh c√¥ng:', response.data);
      
      return response;
    } catch (retryError) {
      console.log('‚ùå API retry th·∫•t b·∫°i:', retryError.message);
      throw retryError;
    }
  }

  // C·∫≠p nh·∫≠t UI state theo g·ª£i √Ω AI
  async updateUIState(stateChanges) {
    try {
      // Dispatch Redux actions ho·∫∑c update React state
      if (window.store && stateChanges.redux) {
        window.store.dispatch(stateChanges.redux);
      }
      
      // Update localStorage
      if (stateChanges.localStorage) {
        Object.entries(stateChanges.localStorage).forEach(([key, value]) => {
          localStorage.setItem(key, JSON.stringify(value));
        });
      }
      
      console.log('‚úÖ UI state updated theo AI suggestion');
    } catch (error) {
      console.log('‚ùå Kh√¥ng th·ªÉ update UI state:', error.message);
    }
  }

  // Hi·ªÉn th·ªã th√¥ng b√°o l·ªói v·ªõi AI suggestion
  showErrorNotification(error, aiResponse) {
    const notification = {
      type: 'error',
      title: `ü§ñ AI Ph√°t hi·ªán l·ªói: ${error.type}`,
      message: error.message,
      suggestion: aiResponse.suggestion,
      action: aiResponse.action,
      duration: 10000
    };
    
    this.displayNotification(notification);
  }

  // Hi·ªÉn th·ªã th√¥ng b√°o th√†nh c√¥ng
  showSuccessNotification(message) {
    const notification = {
      type: 'success',
      title: 'ü§ñ AI Auto-Fix',
      message: message,
      duration: 5000
    };
    
    this.displayNotification(notification);
  }

  // Hi·ªÉn th·ªã th√¥ng b√°o c∆° b·∫£n
  showBasicErrorNotification(error) {
    console.log(`üî¥ L·ªói ${error.type}: ${error.message}`);
  }

  // Hi·ªÉn th·ªã notification tr√™n UI
  displayNotification(notification) {
    // T√≠ch h·ª£p v·ªõi Ant Design notification ho·∫∑c custom notification
    if (window.antd && window.antd.notification) {
      window.antd.notification[notification.type]({
        message: notification.title,
        description: notification.message + (notification.suggestion ? `\nüí° ${notification.suggestion}` : ''),
        duration: notification.duration / 1000
      });
    } else {
      console.log('üì¢ Notification:', notification);
    }
  }

  // L·∫•y l·ªói g·∫ßn ƒë√¢y
  getRecentErrors(count = 10) {
    const allErrors = [...this.errors, ...this.apiErrors, ...this.consoleErrors];
    return allErrors
      .sort((a, b) => new Date(b.timestamp) - new Date(a.timestamp))
      .slice(0, count);
  }

  // L·∫•y th·ªëng k√™ l·ªói
  getErrorStats() {
    const allErrors = this.getRecentErrors(100);
    const stats = {
      total: allErrors.length,
      byType: {},
      byPage: {},
      recent24h: 0
    };
    
    const now = new Date();
    const yesterday = new Date(now.getTime() - 24 * 60 * 60 * 1000);
    
    allErrors.forEach(error => {
      // Count by type
      stats.byType[error.type] = (stats.byType[error.type] || 0) + 1;
      
      // Count by page
      const page = error.url || 'unknown';
      stats.byPage[page] = (stats.byPage[page] || 0) + 1;
      
      // Count recent 24h
      if (new Date(error.timestamp) > yesterday) {
        stats.recent24h++;
      }
    });
    
    return stats;
  }

  // API ƒë·ªÉ disable/enable auto-fix
  setAutoFix(enabled) {
    this.autoFixEnabled = enabled;
    console.log(`ü§ñ AI Auto-Fix: ${enabled ? 'Enabled' : 'Disabled'}`);
  }

  // Hi·ªÉn th·ªã th√¥ng b√°o cho user
  showUserMessage(message) {
    try {
      console.log(`üì¢ AI Message: ${message}`);
      
      // S·ª≠ d·ª•ng Ant Design notification n·∫øu c√≥
      if (window.antd && window.antd.message) {
        window.antd.message.warning(message);
      } else {
        // Fallback to browser notification
        alert(`ü§ñ AI: ${message}`);
      }
      
      return true;
    } catch (error) {
      console.error('‚ùå Kh√¥ng th·ªÉ hi·ªÉn th·ªã th√¥ng b√°o:', error);
      return false;
    }
  }

  // Chuy·ªÉn h∆∞·ªõng v·ªÅ trang danh s√°ch
  redirectToList(page) {
    try {
      console.log(`üîÑ AI Redirect to: ${page}`);
      
      if (page && page.startsWith('/')) {
        // Chuy·ªÉn h∆∞·ªõng b·∫±ng window.location
        setTimeout(() => {
          window.location.href = page;
        }, 2000);
      } else {
        // Chuy·ªÉn v·ªÅ trang ch√≠nh n·∫øu kh√¥ng c√≥ page c·ª• th·ªÉ
        setTimeout(() => {
          window.location.href = '/';
