import React, { useEffect, useRef, useState, useCallback } from 'react';
import { Modal, Button, Space, message, Alert, Typography, Divider } from 'antd';
import { CameraOutlined, CloseOutlined, ReloadOutlined, CheckCircleOutlined } from '@ant-design/icons';
import { Html5Qrcode } from 'html5-qrcode';

const { Text } = Typography;

const QRScanner = ({ 
  visible, 
  onClose, 
  onScan, 
  title = "Quét Serial Number",
  placeholder = "Đưa camera vào mã QR hoặc Barcode" 
}) => {
  const videoRef = useRef(null);
  const [isScanning, setIsScanning] = useState(false);
  const [error, setError] = useState(null);
  const [scannerInstance, setScannerInstance] = useState(null);
  const [permissionState, setPermissionState] = useState('unknown');
  const [isClosing, setIsClosing] = useState(false);
  const [cameraDevices, setCameraDevices] = useState([]);
  const [selectedCameraId, setSelectedCameraId] = useState(null);

  // Safe cleanup function
  const stopScanner = useCallback(async () => {
    try {
      if (scannerInstance && scannerInstance.isScanning) {
        await scannerInstance.stop();
      }
      setScannerInstance(null);
      setIsScanning(false);
    } catch (err) {
      setScannerInstance(null);
      setIsScanning(false);
    }
  }, [scannerInstance]);

  // Main effect for lifecycle management
  useEffect(() => {
    let isMounted = true;
    if (visible && !isClosing) {
      initializeScanner();
    } else if (!visible) {
      stopScanner();
    }
    return () => {
      isMounted = false;
      stopScanner();
    };
  }, [visible, isClosing, stopScanner]);

  // Initialize scanner
  const initializeScanner = async () => {
    try {
      setError(null);
      await checkCameraPermission();
      await getCameraDevices();
      await startScanner();
    } catch (err) {
      handleCameraError(err);
    }
  };

  // Check camera permission
  const checkCameraPermission = async () => {
    if (!navigator.mediaDevices || !navigator.mediaDevices.getUserMedia) {
      throw new Error('Trình duyệt không hỗ trợ truy cập camera');
    }
    if (navigator.permissions) {
      try {
        const permission = await navigator.permissions.query({ name: 'camera' });
        setPermissionState(permission.state);
        permission.onchange = () => setPermissionState(permission.state);
      } catch {}
    }
  };

  // Get camera devices
  const getCameraDevices = async () => {
    try {
      const devices = await navigator.mediaDevices.enumerateDevices();
      const videoDevices = devices.filter(device => device.kind === 'videoinput');
      setCameraDevices(videoDevices);
      const backCamera = videoDevices.find(device =>
        device.label.toLowerCase().includes('back') ||
        device.label.toLowerCase().includes('rear') ||
        device.label.toLowerCase().includes('environment')
      );
      if (backCamera) {
        setSelectedCameraId(backCamera.deviceId);
      } else if (videoDevices.length > 0) {
        setSelectedCameraId(videoDevices[0].deviceId);
      }
    } catch (err) {
      // ignore
    }
  };

  // Request camera permission
  const requestCameraPermission = async () => {
    try {
      setError(null);
      const stream = await navigator.mediaDevices.getUserMedia({ 
        video: { facingMode: 'environment' } 
      });
      stream.getTracks().forEach(track => track.stop());
      setPermissionState('granted');
      message.success('✅ Đã cấp quyền truy cập camera!');
      await startScanner();
    } catch (err) {
      setPermissionState('denied');
      handleCameraError(err);
    }
  };

  // Handle camera errors
  const handleCameraError = (err) => {
    if (err.name === 'NotAllowedError' || err.name === 'PermissionDeniedError') {
      setError('Quyền truy cập camera bị từ chối. Vui lòng cho phép truy cập camera trong cài đặt trình duyệt.');
      setPermissionState('denied');
    } else if (err.name === 'NotFoundError' || err.name === 'DevicesNotFoundError') {
      setError('Không tìm thấy camera. Vui lòng kiểm tra kết nối camera.');
    } else if (err.name === 'NotSupportedError') {
      setError('Trình duyệt không hỗ trợ truy cập camera.');
    } else if (err.name === 'NotReadableError') {
      setError('Camera đang được sử dụng bởi ứng dụng khác.');
    } else {
      setError(`Lỗi camera: ${err.message || 'Không thể truy cập camera'}`);
    }
  };

  // Start scanner with only QR code (default)
  const startScanner = async () => {
    try {
      if (scannerInstance) {
        await stopScanner();
        await new Promise(resolve => setTimeout(resolve, 100));
      }
      const videoElement = videoRef.current;
      if (!videoElement) {
        throw new Error('Video element not found');
      }
      const html5QrCode = new Html5Qrcode("qr-reader");
      const config = {
        fps: 10,
        qrbox: { width: 250, height: 250 },
        aspectRatio: 1.0,
        disableFlip: false,
        videoConstraints: {
          facingMode: 'environment',
          deviceId: selectedCameraId ? { exact: selectedCameraId } : undefined
        }
      };
      await html5QrCode.start(
        { facingMode: 'environment' },
        config,
        async (decodedText) => {
          message.success(`📱 Đã quét thành công: ${decodedText}`);
          onScan(decodedText);
          await handleClose();
        },
        (error) => {
          if (error && !error.includes('NotFoundException')) {
            console.warn(`QR scan error: ${error}`);
          }
        }
      );
      // Try to play video (catch AbortError)
      try {
        if (videoElement && videoElement.play) {
          await videoElement.play();
        }
      } catch (e) {
        // Ignore AbortError
      }
      setScannerInstance(html5QrCode);
      setIsScanning(true);
      setPermissionState('granted');
    } catch (err) {
      handleCameraError(err);
      setIsScanning(false);
    }
  };

  // Handle close with proper cleanup
  const handleClose = async () => {
    setIsClosing(true);
    try {
      await stopScanner();
      setError(null);
      setPermissionState('unknown');
      setIsClosing(false);
      onClose();
    } catch (err) {
      setIsClosing(false);
      onClose();
    }
  };

  // Handle restart
  const handleRestart = async () => {
    setError(null);
    await stopScanner();
    setTimeout(() => {
      initializeScanner();
    }, 500);
  };

  // Render permission request UI
  const renderPermissionRequest = () => (
    <div style={{ textAlign: 'center', padding: '40px 20px' }}>
      <div style={{ fontSize: '64px', marginBottom: '20px' }}>📱</div>
      <div style={{ fontSize: '18px', fontWeight: 'bold', marginBottom: '12px' }}>
        Cần quyền truy cập Camera
      </div>
      <div style={{ fontSize: '14px', color: '#666', marginBottom: '24px', lineHeight: '1.5' }}>
        Để quét mã QR/Barcode, ứng dụng cần quyền truy cập camera của thiết bị.
        <br />
        Vui lòng bấm "Cho phép" khi trình duyệt hỏi.
      </div>
      <Button 
        type="primary" 
        size="large"
        icon={<CameraOutlined />}
        onClick={requestCameraPermission}
        style={{ 
          background: '#52c41a',
          borderColor: '#52c41a',
          height: '48px',
          fontSize: '16px',
          fontWeight: 'bold'
        }}
        disabled={isClosing}
      >
        🔓 Cấp quyền Camera
      </Button>
      <Divider />
      <div style={{ fontSize: '12px', color: '#999', textAlign: 'left' }}>
        <div style={{ marginBottom: '8px', fontWeight: 'bold' }}>💡 Lưu ý:</div>
        <div>• Trên <strong>iPhone/iPad</strong>: Bấm "Cho phép" khi Safari hỏi</div>
        <div>• Trên <strong>Android</strong>: Bấm "Allow" khi Chrome hỏi</div>
        <div>• Nếu đã từ chối: Vào Settings → Camera → Cho phép trang web này</div>
      </div>
    </div>
  );

  return (
    <Modal
      title={
        <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
          <CameraOutlined style={{ color: '#1890ff' }} />
          <span>{title}</span>
          {permissionState === 'granted' && (
            <CheckCircleOutlined style={{ color: '#52c41a', marginLeft: '8px' }} />
          )}
        </div>
      }
      open={visible}
      onCancel={handleClose}
      width={Math.min(600, window.innerWidth - 32)}
      footer={
        <Space>
          <Button onClick={handleRestart} icon={<ReloadOutlined />} disabled={isClosing}>
            Thử lại
          </Button>
          <Button onClick={handleClose} icon={<CloseOutlined />} disabled={isClosing}>
            Đóng
          </Button>
        </Space>
      }
      centered
      style={{ top: 20 }}
      confirmLoading={isClosing}
      maskClosable={!isClosing}
      closable={!isClosing}
      forceRender={true}
    >
      <div style={{ textAlign: 'center', padding: '20px 0' }}>
        {/* Permission request UI */}
        {(permissionState === 'denied' || permissionState === 'prompt' || permissionState === 'unknown') && !scannerInstance && (
          renderPermissionRequest()
        )}
        {/* Error display */}
        {error && (
          <Alert
            message="Lỗi Camera"
            description={
              <div>
                <div>{error}</div>
                <div style={{ marginTop: '12px', fontSize: '12px' }}>
                  <strong>Cách khắc phục:</strong>
                  <br />• Đảm bảo trang web được truy cập qua HTTPS
                  <br />• Kiểm tra cài đặt quyền camera trong trình duyệt
                  <br />• Thử tải lại trang và cấp quyền lại
                </div>
              </div>
            }
            type="error"
            showIcon
            style={{ marginBottom: 16, textAlign: 'left' }}
            action={
              <Button size="small" onClick={requestCameraPermission} disabled={isClosing}>
                Thử lại
              </Button>
            }
          />
        )}
        {/* Success message when camera is ready */}
        {permissionState === 'granted' && !error && (
          <Alert
            message="Camera đã sẵn sàng"
            description={placeholder}
            type="success"
            showIcon
            style={{ marginBottom: 16 }}
          />
        )}
        {/* Scanner container with controlled video element */}
        {permissionState === 'granted' && !error && (
          <div 
            id="qr-reader"
            style={{ 
              width: '100%',
              maxWidth: '500px',
              margin: '0 auto',
              border: '2px dashed #52c41a',
              borderRadius: '8px',
              minHeight: '300px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              position: 'relative'
            }}
          >
            {isScanning && visible && !isClosing && (
              <video
                ref={videoRef}
                style={{
                  width: '100%',
                  height: '100%',
                  objectFit: 'cover',
                  borderRadius: '6px',
                  display: 'block'
                }}
                autoPlay
                playsInline
                muted
              />
            )}
            {(!isScanning || isClosing) && (
              <div style={{ textAlign: 'center', color: '#999' }}>
                <CameraOutlined style={{ fontSize: '48px', marginBottom: '16px' }} />
                <div>Đang khởi tạo camera...</div>
              </div>
            )}
          </div>
        )}
        <div style={{ marginTop: 16, fontSize: '12px', color: '#666' }}>
          <Text type="secondary">
            💡 Hỗ trợ quét: QR Code
          </Text>
        </div>
      </div>
    </Modal>
  );
};

export default QRScanner;
