import React, { useState, useEffect } from 'react';
import {
  Card, Table, Button, Input, Space, Modal, Form, Select, message,
  Row, Col, Statistic, Tag, Tooltip, DatePicker, Typography, Alert,
  Tabs, Badge, Descriptions, Divider, Rate, InputNumber, Drawer,
  Timeline, Empty, Spin, AutoComplete
} from 'antd';
import {
  PlusOutlined, SearchOutlined, EditOutlined, EyeOutlined,
  ToolOutlined, ExclamationCircleOutlined, CheckCircleOutlined,
  ClockCircleOutlined, DeleteOutlined, FileTextOutlined,
  HistoryOutlined, InfoCircleOutlined, BarcodeOutlined,
  ShopOutlined, CalendarOutlined, WarningOutlined,
  SafetyOutlined, EnvironmentOutlined, ThunderboltOutlined,
  RobotOutlined, ScanOutlined
} from '@ant-design/icons';
import { warrantyAPI, customersAPI, productsAPI, usersAPI, serialsAPI, formatCurrency, formatDate } from '../services/api';
import LoadingSpinner, { PageLoading, CardLoading } from '../components/LoadingSpinner';
import useAPI from '../hooks/useAPI';
import moment from 'moment';

const { Search } = Input;
const { Option } = Select;
const { TabPane } = Tabs;
const { Text, Title } = Typography;

const WarrantyPage = () => {
  const [claims, setClaims] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [products, setProducts] = useState([]);
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [isDetailModalVisible, setIsDetailModalVisible] = useState(false);
  const [selectedClaim, setSelectedClaim] = useState(null);
  const [searchText, setSearchText] = useState('');
  const [filterStatus, setFilterStatus] = useState('all');
  const [filterPriority, setFilterPriority] = useState('all');
  const [stats, setStats] = useState({});
  const [form] = Form.useForm();

  // ðŸ” NEW: Serial Search State
  const [serialSearchResults, setSerialSearchResults] = useState([]);
  const [serialSearchLoading, setSerialSearchLoading] = useState(false);
  const [serialSearchText, setSerialSearchText] = useState('');
  const [selectedSerial, setSelectedSerial] = useState(null);
  const [serialDetailDrawer, setSerialDetailDrawer] = useState(false);
  const [activeTab, setActiveTab] = useState('serials');

  // ðŸŽ¯ NEW: Quick Search & Auto-fill State
  const [quickSearchValue, setQuickSearchValue] = useState('');
  const [quickSearchLoading, setQuickSearchLoading] = useState(false);
  const [autoFillLoading, setAutoFillLoading] = useState(false);
  const [serialSuggestions, setSerialSuggestions] = useState([]);

  // ðŸ’¼ NEW: Claims Management State - moved from duplicate

  const claimStatuses = [
    { value: 'pending', label: 'Chá» duyá»‡t', color: 'orange' },
    { value: 'approved', label: 'ÄÃ£ duyá»‡t', color: 'blue' },
    { value: 'rejected', label: 'Tá»« chá»‘i', color: 'red' },
    { value: 'in_progress', label: 'Äang xá»­ lÃ½', color: 'purple' },
    { value: 'completed', label: 'HoÃ n thÃ nh', color: 'green' },
    { value: 'cancelled', label: 'ÄÃ£ há»§y', color: 'gray' },
  ];

  const claimTypes = [
    { value: 'repair', label: 'Sá»­a chá»¯a' },
    { value: 'replace', label: 'Thay tháº¿' },
    { value: 'refund', label: 'HoÃ n tiá»n' },
  ];

  const priorities = [
    { value: 'low', label: 'Tháº¥p', color: 'green' },
    { value: 'normal', label: 'BÃ¬nh thÆ°á»ng', color: 'blue' },
    { value: 'high', label: 'Cao', color: 'orange' },
    { value: 'urgent', label: 'Kháº©n cáº¥p', color: 'red' },
  ];

  useEffect(() => {
    loadData();
    loadStats();
  }, []);

  useEffect(() => {
    if (activeTab === 'claims') {
      fetchClaims();
    }
  }, [searchText, filterStatus, filterPriority, activeTab]);

  const loadData = async () => {
    try {
      const [customersRes, productsRes, usersRes] = await Promise.all([
        customersAPI.getAll(),
        productsAPI.getAll(),
        usersAPI.getAll()
      ]);
      
      if (customersRes.data.success) setCustomers(customersRes.data.data);
      if (productsRes.data.success) setProducts(productsRes.data.data);
      if (usersRes.data.success) setUsers(usersRes.data.data);
    } catch (error) {
      console.error('Error loading data:', error);
    }
  };

  const statsAPI = useAPI({
    showErrorMessage: false,
    onError: (error) => {
      console.warn('Warranty stats failed, using fallback data');
      setStats({
        total_claims: 0,
        pending_claims: 0,
        in_progress_claims: 0,
        completed_claims: 0,
        avg_resolution_days: 0
      });
    }
  });

  const loadStats = async () => {
    try {
      await statsAPI.executeAPI(() => warrantyAPI.getStats());
      if (statsAPI.data && statsAPI.data.success) {
        setStats(statsAPI.data.data);
      }
    } catch (error) {
      console.log('Stats API call completed with fallback data');
    }
  };

  const fetchClaims = async () => {
    try {
      setLoading(true);
      const params = {
        serial_number: searchText,
        status: filterStatus !== 'all' ? filterStatus : undefined,
        priority: filterPriority !== 'all' ? filterPriority : undefined,
      };
      
      const response = await warrantyAPI.getClaims(params);
      if (response.data.success) {
        setClaims(response.data.data);
      }
    } catch (error) {
      console.error('Error fetching claims:', error);
    } finally {
      setLoading(false);
    }
  };

  // ðŸ” NEW: Serial Search Functions
  const handleSerialSearch = async (query) => {
    if (!query || query.trim().length < 2) {
      setSerialSearchResults([]);
      return;
    }
    
    try {
      setSerialSearchLoading(true);
      const response = await serialsAPI.search(query.trim());
      
      if (response.data.success) {
        setSerialSearchResults(response.data.data);
        message.success(`TÃ¬m tháº¥y ${response.data.data.length} káº¿t quáº£`);
      } else {
        setSerialSearchResults([]);
        message.info('KhÃ´ng tÃ¬m tháº¥y serial number nÃ o');
      }
    } catch (error) {
      message.error('Lá»—i khi tÃ¬m kiáº¿m serial number');
      setSerialSearchResults([]);
    } finally {
      setSerialSearchLoading(false);
    }
  };

  // ðŸŽ¯ NEW: Quick Search from Header
  const handleQuickSearch = async (value) => {
    if (!value?.trim()) return;
    
    try {
      setQuickSearchLoading(true);
      const response = await serialsAPI.search(value.trim());
      
      if (response.data.success && response.data.data.length > 0) {
        // Switch to serials tab and show results
        setActiveTab('serials');
        setSerialSearchResults(response.data.data);
        setSerialSearchText(value.trim());
        message.success(`ðŸŽ¯ TÃ¬m tháº¥y ${response.data.data.length} káº¿t quáº£ cho "${value}"`);
      } else {
        message.warning(`KhÃ´ng tÃ¬m tháº¥y Serial Number "${value}"`);
      }
    } catch (error) {
      message.error('Lá»—i khi tÃ¬m kiáº¿m nhanh');
    } finally {
      setQuickSearchLoading(false);
      setQuickSearchValue('');
    }
  };

  // ðŸ¤– NEW: Auto-fill Form when entering Serial Number
  const handleSerialAutoFill = async (serialNumber) => {
    if (!serialNumber?.trim() || serialNumber.length < 3) return;
    
    try {
      setAutoFillLoading(true);
      const response = await serialsAPI.getWarrantyInfo(serialNumber.trim());
      
      if (response.data.success) {
        const serialInfo = response.data.data;
        
        // Auto-fill form fields
        form.setFieldsValue({
          serial_number: serialInfo.serial_number,
          product_id: serialInfo.product_id || null,
          customer_id: serialInfo.customer_id || null,
          warranty_start_date: serialInfo.warranty_start_date ? moment(serialInfo.warranty_start_date) : null,
          warranty_end_date: serialInfo.warranty_end_date ? moment(serialInfo.warranty_end_date) : null,
          issue_description: `YÃªu cáº§u báº£o hÃ nh cho ${serialInfo.product_name || 'sáº£n pháº©m'}`,
          claim_type: 'repair',
          status: 'pending',
          priority: 'normal'
        });
        
        message.success(`ðŸ¤– ÄÃ£ tá»± Ä‘á»™ng Ä‘iá»n thÃ´ng tin cho Serial: ${serialNumber}`);
      } else {
        message.warning(`Serial "${serialNumber}" khÃ´ng tÃ¬m tháº¥y thÃ´ng tin. Vui lÃ²ng kiá»ƒm tra láº¡i.`);
      }
    } catch (error) {
      message.error('KhÃ´ng thá»ƒ tá»± Ä‘á»™ng Ä‘iá»n thÃ´ng tin serial');
    } finally {
      setAutoFillLoading(false);
    }
  };

  // ðŸ’¡ NEW: Fetch Serial Suggestions for AutoComplete
  const fetchSerialSuggestions = async (searchText) => {
    if (!searchText || searchText.length < 2) {
      setSerialSuggestions([]);
      return;
    }
    
    try {
      const response = await serialsAPI.search(searchText, { limit: 10 });
      if (response.data.success) {
        const suggestions = response.data.data.map(item => ({
          value: item.serial_number,
          label: (
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <span style={{ fontWeight: 'bold', color: '#1890ff' }}>{item.serial_number}</span>
              <span style={{ fontSize: '12px', color: '#666' }}>{item.product_name}</span>
            </div>
          )
        }));
        setSerialSuggestions(suggestions);
      }
    } catch (error) {
      setSerialSuggestions([]);
    }
  };

  // ðŸŽ¯ Helper Functions  
  const showModal = (claim = null) => {
    setSelectedClaim(claim);
    setIsModalVisible(true);
    if (claim) {
      form.setFieldsValue({
        ...claim,
        warranty_start_date: claim.warranty_start_date ? moment(claim.warranty_start_date) : null,
        warranty_end_date: claim.warranty_end_date ? moment(claim.warranty_end_date) : null,
        expected_completion_date: claim.expected_completion_date ? moment(claim.expected_completion_date) : null,
        actual_completion_date: claim.actual_completion_date ? moment(claim.actual_completion_date) : null,
        sent_to_supplier_date: claim.sent_to_supplier_date ? moment(claim.sent_to_supplier_date) : null,
        received_from_supplier_date: claim.received_from_supplier_date ? moment(claim.received_from_supplier_date) : null,
      });
    } else {
      form.resetFields();
    }
  };

  const handleSubmit = async (values) => {
    try {
      const data = {
        ...values,
        warranty_start_date: values.warranty_start_date?.format('YYYY-MM-DD'),
        warranty_end_date: values.warranty_end_date?.format('YYYY-MM-DD'),
        expected_completion_date: values.expected_completion_date?.format('YYYY-MM-DD'),
        actual_completion_date: values.actual_completion_date?.format('YYYY-MM-DD'),
        sent_to_supplier_date: values.sent_to_supplier_date?.format('YYYY-MM-DD'),
        received_from_supplier_date: values.received_from_supplier_date?.format('YYYY-MM-DD'),
      };

      if (selectedClaim) {
        await warrantyAPI.updateClaim(selectedClaim.id, data);
        message.success('ðŸŽ‰ Cáº­p nháº­t yÃªu cáº§u báº£o hÃ nh thÃ nh cÃ´ng');
      } else {
        await warrantyAPI.createClaim(data);
        message.success('ðŸŽ‰ Táº¡o yÃªu cáº§u báº£o hÃ nh thÃ nh cÃ´ng');
      }
      
      setIsModalVisible(false);
      // Refresh data if needed
      loadStats();
    } catch (error) {
      message.error('âŒ Lá»—i khi lÆ°u yÃªu cáº§u báº£o hÃ nh');
    }
  };

  const showSerialDetail = async (serial) => {
    try {
      const response = await serialsAPI.getWarrantyInfo(serial.serial_number);
      if (response.data.success) {
        setSelectedSerial(response.data.data);
        setSerialDetailDrawer(true);
      }
    } catch (error) {
      message.error('Lá»—i khi láº¥y thÃ´ng tin chi tiáº¿t');
    }
  };

  const getStatusTag = (status) => {
    const statusMap = {
      'available': { color: 'green', text: 'CÃ³ sáºµn' },
      'sold': { color: 'blue', text: 'ÄÃ£ bÃ¡n' },
      'damaged': { color: 'red', text: 'Há»ng' },
      'reserved': { color: 'orange', text: 'ÄÃ£ Ä‘áº·t' }
    };
    
    const statusInfo = statusMap[status] || { color: 'default', text: status };
    return <Tag color={statusInfo.color}>{statusInfo.text}</Tag>;
  };

  const getConditionTag = (condition) => {
    const conditionMap = {
      'new': { color: 'green', text: 'Má»›i' },
      'like_new': { color: 'blue', text: 'NhÆ° má»›i' },
      'good': { color: 'cyan', text: 'Tá»‘t' },
      'fair': { color: 'orange', text: 'KhÃ¡' },
      'poor': { color: 'red', text: 'KÃ©m' }
    };
    
    const conditionInfo = conditionMap[condition] || { color: 'default', text: condition };
    return <Tag color={conditionInfo.color}>{conditionInfo.text}</Tag>;
  };

  const getWarrantyStatusTag = (warranty) => {
    if (!warranty || warranty.status === 'N/A') {
      return <Tag color="default">N/A</Tag>;
    }
    
    const statusMap = {
      'CÃ²n hiá»‡u lá»±c': { color: 'green', icon: <CheckCircleOutlined /> },
      'Sáº¯p háº¿t háº¡n': { color: 'orange', icon: <WarningOutlined /> },
      'Háº¿t háº¡n': { color: 'red', icon: <ExclamationCircleOutlined /> }
    };
    
    const statusInfo = statusMap[warranty.status] || { color: 'default', icon: null };
    
    return (
      <Tag color={statusInfo.color} icon={statusInfo.icon}>
        {warranty.status}
      </Tag>
    );
  };

  // Serial Search Table Columns
  const serialColumns = [
    {
      title: 'Serial Number',
      dataIndex: 'serial_number',
      key: 'serial_number',
      width: 150,
      fixed: 'left',
      render: (text) => (
        <Text copyable strong style={{ color: '#1890ff' }}>
          {text}
        </Text>
      )
    },
    {
      title: 'Tráº¡ng thÃ¡i',
      dataIndex: 'status_display',
      key: 'status',
      width: 100,
      render: (text, record) => getStatusTag(record.status)
    },
    {
      title: 'TÃ¬nh tráº¡ng',
      dataIndex: 'condition_display',
      key: 'condition',
      width: 100,
      render: (text, record) => getConditionTag(record.condition_grade)
    },
    {
      title: 'NhÃ  cung cáº¥p',
      key: 'supplier',
      width: 200,
      render: (_, record) => (
        <div>
          <div style={{ fontWeight: 'bold' }}>
            {record.supplier_display?.name || 'N/A'}
          </div>
          <div style={{ fontSize: '12px', color: '#666' }}>
            {record.supplier_display?.code || ''}
          </div>
        </div>
      )
    },
    {
      title: 'Thá»i gian',
      key: 'time',
      width: 180,
      render: (_, record) => (
        <div>
          <div style={{ fontSize: '12px', fontWeight: 'bold' }}>
            Nháº­p: {record.import_time_display}
          </div>
          {record.sale_info && (
            <div style={{ fontSize: '12px', color: '#52c41a' }}>
              BÃ¡n: {record.sale_info.date}
            </div>
          )}
        </div>
      )
    },
    {
      title: 'Vá»‹ trÃ­',
      dataIndex: 'location',
      key: 'location',
      width: 80,
      render: (location) => (
        <Tag icon={<EnvironmentOutlined />}>
          {location || 'N/A'}
        </Tag>
      )
    },
    {
      title: 'Báº£o hÃ nh',
      key: 'warranty',
      width: 200,
      render: (_, record) => {
        const warranty = record.warranty_display;
        return (
          <div>
            <div style={{ fontSize: '12px', fontWeight: 'bold' }}>
              {warranty.period}
            </div>
            {warranty.start_date !== 'N/A' && (
              <>
                <div style={{ fontSize: '11px', color: '#666' }}>
                  Tá»«: {warranty.start_date}
                </div>
                <div style={{ fontSize: '11px', color: '#666' }}>
                  Äáº¿n: {warranty.end_date}
                </div>
                <div style={{ marginTop: '4px' }}>
                  {getWarrantyStatusTag(warranty)}
                </div>
              </>
            )}
            <div style={{ fontSize: '11px', color: '#1890ff', marginTop: '2px' }}>
              BH: {warranty.provider}
            </div>
          </div>
        );
      }
    },
    {
      title: 'Thao tÃ¡c',
      key: 'actions',
      width: 100,
      fixed: 'right',
      render: (_, record) => (
        <Space>
          <Tooltip title="Xem chi tiáº¿t">
            <Button 
              size="small" 
              icon={<EyeOutlined />} 
              onClick={() => showSerialDetail(record)}
            />
          </Tooltip>
          <Tooltip title="Lá»‹ch sá»­">
            <Button 
              size="small" 
              icon={<HistoryOutlined />}
              onClick={() => {
                message.info('TÃ­nh nÄƒng lá»‹ch sá»­ sáº½ Ä‘Æ°á»£c phÃ¡t triá»ƒn');
              }}
            />
          </Tooltip>
        </Space>
      )
    }
  ];

  return (
    <div style={{ padding: '24px' }}>
      {/* Enhanced Header with Quick Search */}
      <div style={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center',
        marginBottom: '24px',
        padding: '16px 0',
        borderBottom: '2px solid #f0f0f0'
      }}>
        <Title level={2} style={{ margin: 0, display: 'flex', alignItems: 'center' }}>
          <SafetyOutlined style={{ marginRight: '12px', color: '#52c41a', fontSize: '28px' }} />
          <span style={{ background: 'linear-gradient(135deg, #52c41a, #1890ff)', 
                       WebkitBackgroundClip: 'text', 
                       WebkitTextFillColor: 'transparent' }}>
            Quáº£n lÃ½ Báº£o hÃ nh & Serial Number
          </span>
        </Title>
        
        {/* ðŸ” Quick Search Box */}
        <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
          <Tag icon={<RobotOutlined />} color="green">AI Smart Search</Tag>
          <AutoComplete
            style={{ width: 350 }}
            options={serialSuggestions}
            value={quickSearchValue}
            onChange={setQuickSearchValue}
            onSearch={fetchSerialSuggestions}
            onSelect={handleQuickSearch}
            placeholder="ðŸ” Nháº­p Serial Number Ä‘á»ƒ tÃ¬m kiáº¿m nhanh..."
            size="large"
            allowClear
          >
            <Input
              prefix={<ScanOutlined style={{ color: '#1890ff' }} />}
              suffix={
                <Button 
                  type="primary" 
                  icon={<ThunderboltOutlined />}
                  loading={quickSearchLoading}
                  onClick={() => handleQuickSearch(quickSearchValue)}
                  style={{ border: 'none', boxShadow: 'none' }}
                  size="small"
                >
                  Search
                </Button>
              }
              onPressEnter={() => handleQuickSearch(quickSearchValue)}
              style={{ 
                borderRadius: '8px',
                boxShadow: '0 2px 8px rgba(0,0,0,0.1)'
              }}
            />
          </AutoComplete>
        </div>
      </div>
      
      {/* Stats Cards */}
      <Row gutter={[16, 16]} style={{ marginBottom: 24 }}>
        <Col span={6}>
          <Card>
            <Statistic
              title="Tá»•ng yÃªu cáº§u"
              value={stats.total_claims || 0}
              prefix={<FileTextOutlined />}
              valueStyle={{ color: '#1890ff' }}
            />
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <Statistic
              title="Chá» duyá»‡t"
              value={stats.pending_claims || 0}
              prefix={<ClockCircleOutlined />}
              valueStyle={{ color: '#faad14' }}
            />
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <Statistic
              title="Äang xá»­ lÃ½"
              value={stats.in_progress_claims || 0}
              prefix={<ToolOutlined />}
              valueStyle={{ color: '#722ed1' }}
            />
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <Statistic
              title="HoÃ n thÃ nh"
              value={stats.completed_claims || 0}
              prefix={<CheckCircleOutlined />}
              valueStyle={{ color: '#52c41a' }}
            />
          </Card>
        </Col>
      </Row>

      {/* Main Content with Tabs */}
      <Card>
        <Tabs 
          activeKey={activeTab} 
          onChange={setActiveTab}
          size="large"
          style={{ marginTop: '16px' }}
          items={[
            {
              key: 'serials',
              label: (
                <span>
                  <BarcodeOutlined />
                  TÃ¬m kiáº¿m Serial Number
                  <Badge count={serialSearchResults.length} style={{ marginLeft: '8px' }} />
                </span>
              ),
              children: (
                <div>
                  {/* Serial Search Section */}
                  <Row gutter={[16, 16]} style={{ marginBottom: 16 }}>
                    <Col span={12}>
                      <Search
                        placeholder="Nháº­p Serial Number, tÃªn sáº£n pháº©m hoáº·c nhÃ  cung cáº¥p..."
                        value={serialSearchText}
                        onChange={(e) => setSerialSearchText(e.target.value)}
                        onSearch={handleSerialSearch}
                        enterButton={
                          <Button type="primary" icon={<SearchOutlined />}>
                            TÃ¬m kiáº¿m
                          </Button>
                        }
                        size="large"
                        allowClear
                        loading={serialSearchLoading}
                      />
                    </Col>
                    <Col span={12}>
                      <Alert
                        message="ðŸ’¡ TÃ¬m kiáº¿m nÃ¢ng cao"
                        description="Báº¡n cÃ³ thá»ƒ tÃ¬m theo Serial Number, tÃªn sáº£n pháº©m, hoáº·c tÃªn nhÃ  cung cáº¥p. Káº¿t quáº£ sáº½ hiá»ƒn thá»‹ Ä‘áº§y Ä‘á»§ thÃ´ng tin báº£o hÃ nh vÃ  tráº¡ng thÃ¡i."
                        type="info"
                        showIcon
                        style={{ height: '100%' }}
                      />
                    </Col>
                  </Row>

                  {/* Serial Results Table */}
                  {serialSearchResults.length > 0 ? (
                    <Table
                      columns={serialColumns}
                      dataSource={serialSearchResults}
                      rowKey="id"
                      loading={serialSearchLoading}
                      pagination={{
                        total: serialSearchResults.length,
                        pageSize: 10,
                        showSizeChanger: true,
                        showTotal: (total, range) => 
                          `${range[0]}-${range[1]} cá»§a ${total} serial number`
                      }}
                      scroll={{ x: 1400 }}
                      size="middle"
                    />
                  ) : (
                    <Empty
                      image={Empty.PRESENTED_IMAGE_SIMPLE}
                      description={
                        <span>
                          {serialSearchText ? 
                            'KhÃ´ng tÃ¬m tháº¥y káº¿t quáº£ nÃ o' : 
                            'Nháº­p tá»« khÃ³a Ä‘á»ƒ tÃ¬m kiáº¿m Serial Number'
                          }
                        </span>
                      }
                      extra={
                        <Button 
                          type="primary" 
                          icon={<PlusOutlined />}
                          onClick={() => showModal()}
                          style={{ 
                            background: 'linear-gradient(135deg, #52c41a, #1890ff)',
                            border: 'none',
                            borderRadius: '8px'
                          }}
                        >
                          Táº¡o yÃªu cáº§u báº£o hÃ nh
                        </Button>
                      }
                    />
                  )}

                  {/* Quick Action Buttons */}
                  <div style={{ 
