import React, { useState, useEffect } from 'react';
import {
  Card,
  Table,
  Button,
  Input,
  Space,
  Modal,
  Form,
  message,
  Row,
  Col,
  Statistic,
  Tag,
  Tooltip,
  Popconfirm,
  Drawer,
  Typography,
  Avatar
} from 'antd';
import {
  PlusOutlined,
  SearchOutlined,
  EditOutlined,
  DeleteOutlined,
  ShopOutlined,
  PhoneOutlined,
  MailOutlined,
  HomeOutlined,
  CreditCardOutlined,
  EyeOutlined,
  BankOutlined,
  UserOutlined,
  ContactsOutlined
} from '@ant-design/icons';
import { suppliersAPI, formatCurrency, formatDate } from '../services/api';

const { Search } = Input;
const { Text } = Typography;

const SuppliersPage = () => {
  const [suppliers, setSuppliers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [isDetailDrawerVisible, setIsDetailDrawerVisible] = useState(false);
  const [editingSupplier, setEditingSupplier] = useState(null);
  const [selectedSupplier, setSelectedSupplier] = useState(null);
  const [searchText, setSearchText] = useState('');
  const [stats, setStats] = useState({
    total: 0,
    totalDebt: 0,
    totalCreditLimit: 0
  });
  const [form] = Form.useForm();

  useEffect(() => {
    fetchSuppliers();
  }, [searchText]);

  const fetchSuppliers = async () => {
    try {
      setLoading(true);
      const response = await suppliersAPI.getAll({
        search: searchText
      });
      
      if (response.data.success) {
        setSuppliers(response.data.data);
        calculateStats(response.data.data);
      }
    } catch (error) {
      message.error('Lỗi khi tải danh sách nhà cung cấp');
      console.error('Error fetching suppliers:', error);
    } finally {
      setLoading(false);
    }
  };

  const calculateStats = (supplierData) => {
    const stats = {
      total: supplierData.length,
      totalDebt: supplierData.reduce((sum, s) => sum + (s.total_debt || 0), 0),
      totalCreditLimit: supplierData.reduce((sum, s) => sum + (s.credit_limit || 0), 0)
    };
    setStats(stats);
  };

  const showModal = (supplier = null) => {
    setEditingSupplier(supplier);
    setIsModalVisible(true);
    if (supplier) {
      form.setFieldsValue(supplier);
    } else {
      form.resetFields();
    }
  };

  const handleCancel = () => {
    setIsModalVisible(false);
    setEditingSupplier(null);
    form.resetFields();
  };

  const handleSubmit = async (values) => {
    try {
      if (editingSupplier) {
        await suppliersAPI.update(editingSupplier.id, values);
        message.success('Cập nhật nhà cung cấp thành công');
      } else {
        await suppliersAPI.create(values);
        message.success('Tạo nhà cung cấp thành công');
      }
      
      handleCancel();
      fetchSuppliers();
    } catch (error) {
      message.error(`Lỗi khi ${editingSupplier ? 'cập nhật' : 'tạo'} nhà cung cấp`);
      console.error('Error saving supplier:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await suppliersAPI.delete(id);
      message.success('Xóa nhà cung cấp thành công');
      fetchSuppliers();
    } catch (error) {
      message.error('Lỗi khi xóa nhà cung cấp');
      console.error('Error deleting supplier:', error);
    }
  };

  const showSupplierDetail = async (supplier) => {
    try {
      const response = await suppliersAPI.getById(supplier.id);
      if (response.data.success) {
        setSelectedSupplier(response.data.data);
        setIsDetailDrawerVisible(true);
      }
    } catch (error) {
      message.error('Lỗi khi tải thông tin nhà cung cấp');
    }
  };

  const columns = [
    {
      title: 'Mã NCC',
      dataIndex: 'code',
      key: 'code',
      width: 80,
      fixed: 'left',
      render: (text) => <Text strong style={{ color: '#1890ff' }}>{text}</Text>
    },
    {
      title: 'Tên nhà cung cấp',
      dataIndex: 'name',
      key: 'name',
      width: 200,
      fixed: 'left',
      render: (text) => (
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <Avatar 
            size="small" 
            icon={<ShopOutlined />} 
            style={{ 
              backgroundColor: '#f0f0f0', 
              color: '#1890ff',
              marginRight: 8 
            }} 
          />
          <Text strong style={{ fontSize: '13px' }}>{text}</Text>
        </div>
      )
    },
    {
      title: 'Người liên hệ',
      dataIndex: 'contact_person',
      key: 'contact_person',
      width: 140,
      render: (text) => (
        <div style={{ fontSize: '12px' }}>
          {text ? (
            <div style={{ display: 'flex', alignItems: 'center' }}>
              <UserOutlined style={{ marginRight: 4, color: '#666' }} />
              <Text ellipsis style={{ maxWidth: '100px' }}>{text}</Text>
            </div>
          ) : (
            <Text type="secondary" style={{ fontSize: '11px' }}>Chưa có</Text>
          )}
        </div>
      )
    },
    {
      title: 'Liên hệ',
      key: 'contact_info',
      width: 160,
      render: (_, record) => (
        <div style={{ fontSize: '11px', lineHeight: '1.2' }}>
          {record.phone && (
            <div style={{ 
              display: 'flex', 
              alignItems: 'center', 
              marginBottom: 2,
              color: '#666' 
            }}>
              <PhoneOutlined style={{ marginRight: 4, fontSize: '10px' }} />
              <Text ellipsis style={{ maxWidth: '110px', fontSize: '11px' }}>
                {record.phone}
              </Text>
            </div>
          )}
          {record.email && (
            <div style={{ 
              display: 'flex', 
              alignItems: 'center',
              color: '#666' 
            }}>
              <MailOutlined style={{ marginRight: 4, fontSize: '10px' }} />
              <Text ellipsis style={{ maxWidth: '110px', fontSize: '11px' }}>
                {record.email}
              </Text>
            </div>
          )}
          {!record.phone && !record.email && (
            <Text type="secondary" style={{ fontSize: '11px' }}>Chưa có</Text>
          )}
        </div>
      )
    },
    {
      title: 'Địa chỉ',
      key: 'address_info',
      width: 180,
      render: (_, record) => (
        <div style={{ fontSize: '11px', lineHeight: '1.3' }}>
          {record.address && (
            <div style={{ marginBottom: 2, color: '#333' }}>
              <Text ellipsis style={{ maxWidth: '150px', fontSize: '11px' }}>
                {record.address}
              </Text>
            </div>
          )}
          {record.city && (
            <div style={{ color: '#666' }}>
              <HomeOutlined style={{ marginRight: 4, fontSize: '10px' }} />
              <Text style={{ fontSize: '11px' }}>{record.city}</Text>
            </div>
          )}
          {!record.address && !record.city && (
            <Text type="secondary" style={{ fontSize: '11px' }}>Chưa có</Text>
          )}
        </div>
      )
    },
    {
      title: 'Công nợ',
      dataIndex: 'total_debt',
      key: 'total_debt',
      width: 100,
      align: 'right',
      render: (debt) => (
        <div style={{ textAlign: 'right' }}>
          <Text 
            strong 
            style={{ 
              color: debt > 0 ? '#ff4d4f' : '#52c41a',
              fontSize: '12px'
            }}
          >
            {formatCurrency(debt || 0)}
          </Text>
        </div>
      ),
      sorter: (a, b) => (a.total_debt || 0) - (b.total_debt || 0)
    },
    {
      title: 'Hạn mức',
      dataIndex: 'credit_limit',
      key: 'credit_limit',
      width: 100,
      align: 'right',
      render: (limit) => (
        <div style={{ textAlign: 'right' }}>
          <Text style={{ color: '#1890ff', fontSize: '12px' }}>
            {formatCurrency(limit || 0)}
          </Text>
        </div>
      )
    },
    {
      title: 'Thanh toán',
      dataIndex: 'payment_terms',
      key: 'payment_terms',
      width: 100,
      render: (terms) => (
        <Tag 
          color="blue" 
          style={{ 
            fontSize: '11px',
            margin: 0,
            padding: '2px 6px',
            lineHeight: '1.2'
          }}
        >
          {terms || 'Không xác định'}
        </Tag>
      )
    },
    {
      title: 'Thao tác',
      key: 'actions',
      width: 120,
      fixed: 'right',
      render: (_, record) => (
        <Space size="small">
          <Tooltip title="Xem chi tiết">
            <Button
              type="text"
              size="small"
              icon={<EyeOutlined />}
              onClick={() => showSupplierDetail(record)}
              style={{ padding: '2px 4px' }}
            />
          </Tooltip>
          <Tooltip title="Chỉnh sửa">
            <Button
              type="text"
              size="small"
              icon={<EditOutlined />}
              onClick={() => showModal(record)}
              style={{ padding: '2px 4px', color: '#1890ff' }}
            />
          </Tooltip>
          <Popconfirm
            title="Bạn có chắc chắn muốn xóa nhà cung cấp này?"
            onConfirm={() => handleDelete(record.id)}
            okText="Có"
            cancelText="Không"
            placement="topRight"
          >
            <Tooltip title="Xóa">
              <Button
                type="text"
                danger
                size="small"
                icon={<DeleteOutlined />}
                style={{ padding: '2px 4px' }}
              />
            </Tooltip>
          </Popconfirm>
        </Space>
      )
    }
  ];

  return (
    <div style={{ padding: '20px' }}>
      {/* Header thống kê - Responsive */}
      <Row gutter={[16, 16]} style={{ marginBottom: 20 }}>
        <Col xs={24} sm={8}>
          <Card size="small" style={{ height: '100%' }}>
            <Statistic
              title="Tổng nhà cung cấp"
              value={stats.total}
              prefix={<ShopOutlined />}
              valueStyle={{ color: '#1890ff', fontSize: '20px' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={8}>
          <Card size="small" style={{ height: '100%' }}>
            <Statistic
              title="Tổng công nợ"
              value={stats.totalDebt}
              formatter={value => formatCurrency(value)}
              prefix={<CreditCardOutlined />}
              valueStyle={{ color: '#ff4d4f', fontSize: '20px' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={8}>
          <Card size="small" style={{ height: '100%' }}>
            <Statistic
              title="Tổng hạn mức"
              value={stats.totalCreditLimit}
              formatter={value => formatCurrency(value)}
              prefix={<BankOutlined />}
              valueStyle={{ color: '#52c41a', fontSize: '20px' }}
            />
          </Card>
        </Col>
      </Row>

      {/* Main Content */}
      <Card 
        title={
          <div style={{ display: 'flex', alignItems: 'center' }}>
            <ContactsOutlined style={{ marginRight: 8 }} />
            Quản lý nhà cung cấp
          </div>
        }
        size="small"
      >
        {/* Filters - Responsive */}
        <Row gutter={[16, 16]} style={{ marginBottom: 16 }}>
          <Col xs={24} sm={16} md={18}>
            <Search
              placeholder="Tìm theo tên, mã NCC, người liên hệ..."
              value={searchText}
              onChange={(e) => setSearchText(e.target.value)}
              onSearch={fetchSuppliers}
              enterButton={<SearchOutlined />}
              allowClear
              size="middle"
            />
          </Col>
          <Col xs={24} sm={8} md={6} style={{ textAlign: 'right' }}>
            <Button
              type="primary"
              icon={<PlusOutlined />}
              onClick={() => showModal()}
              size="middle"
              block
            >
              Thêm nhà cung cấp
            </Button>
          </Col>
        </Row>

        {/* Table với responsive scroll */}
        <Table
          columns={columns}
          dataSource={suppliers}
          rowKey="id"
          loading={loading}
          size="small"
          pagination={{
            total: suppliers.length,
            pageSize: 15,
            showSizeChanger: true,
            showQuickJumper: true,
            showTotal: (total, range) => 
              `${range[0]}-${range[1]} của ${total} nhà cung cấp`,
            responsive: true,
            size: 'small'
          }}
          scroll={{ 
            x: 1100,
            y: 'calc(100vh - 450px)'
          }}
          style={{ 
            backgroundColor: '#fafafa',
            borderRadius: '6px'
          }}
        />
      </Card>

      {/* Modal thêm/sửa nhà cung cấp */}
      <Modal
        title={
          <div style={{ display: 'flex', alignItems: 'center' }}>
            <ShopOutlined style={{ marginRight: 8 }} />
            {editingSupplier ? 'Cập nhật nhà cung cấp' : 'Thêm nhà cung cấp mới'}
          </div>
        }
        open={isModalVisible}
        onCancel={handleCancel}
        footer={null}
        width={800}
        style={{ top: 20 }}
      >
        <Form
          form={form}
          layout="vertical"
          onFinish={handleSubmit}
          autoComplete="off"
          size="middle"
        >
          <Row gutter={16}>
            <Col span={12}>
              <Form.Item
                label="Tên nhà cung cấp"
                name="name"
                rules={[{ required: true, message: 'Vui lòng nhập tên nhà cung cấp' }]}
              >
                <Input prefix={<ShopOutlined />} placeholder="Nhập tên nhà cung cấp" />
              </Form.Item>
            </Col>
            <Col span={12}>
              <Form.Item
                label="Người liên hệ"
                name="contact_person"
              >
                <Input prefix={<UserOutlined />} placeholder="Nhập tên người liên hệ" />
              </Form.Item>
            </Col>
          </Row>

          <Row gutter={16}>
            <Col span={12}>
              <Form.Item
                label="Số điện thoại"
                name="phone"
              >
                <Input prefix={<PhoneOutlined />} placeholder="Nhập số điện thoại" />
              </Form.Item>
            </Col>
            <Col span={12}>
              <Form.Item
                label="Email"
                name="email"
                rules={[{ type: 'email', message: 'Email không hợp lệ' }]}
              >
                <Input prefix={<MailOutlined />} placeholder="Nhập email" />
              </Form.Item>
            </Col>
          </Row>

          <Row gutter={16}>
            <Col span={12}>
              <Form.Item
                label="Địa chỉ"
                name="address"
              >
                <Input prefix={<HomeOutlined />} placeholder="Nhập địa chỉ" />
              </Form.Item>
            </Col>
            <Col span={12}>
              <Form.Item
                label="Thành phố"
                name="city"
              >
                <Input placeholder="Nhập thành phố" />
              </Form.Item>
            </Col>
          </Row>

          <Row gutter={16}>
            <Col span={12}>
              <Form.Item
                label="Mã số thuế"
                name="tax_code"
              >
                <Input placeholder="Nhập mã số thuế" />
              </Form.Item>
            </Col>
            <Col span={12}>
              <Form.Item
                label="Điều khoản thanh toán"
                name="payment_terms"
                initialValue="30 ngày"
              >
                <Input placeholder="VD: 30 ngày, Thanh toán ngay..." />
              </Form.Item>
            </Col>
          </Row>

          <Row gutter={16}>
            <Col span={12}>
              <Form.Item
                label="Hạn mức tín dụng (VNĐ)"
                name="credit_limit"
                initialValue={0}
              >
                <Input 
                  type="number" 
                  min={0} 
                  placeholder="0"
                  prefix={<CreditCardOutlined />}
                />
              </Form.Item>
            </Col>
            <Col span={12}>
              <Form.Item
                label="Công nợ hiện tại (VNĐ)"
                name="total_debt"
                initialValue={0}
              >
                <Input 
                  type="number" 
                  min={0} 
                  placeholder="0"
                  prefix={<BankOutlined />}
                />
              </Form.Item>
            </Col>
          </Row>

          <Form.Item
            label="Ghi chú"
            name="notes"
          >
            <Input.TextArea rows={3} placeholder="Nhập ghi chú" />
          </Form.Item>

          <Form.Item style={{ textAlign: 'right', marginBottom: 0 }}>
            <Space>
              <Button onClick={handleCancel}>
                Hủy
              </Button>
              <Button type="primary" htmlType="submit">
                {editingSupplier ? 'Cập nhật' : 'Thêm mới'}
              </Button>
            </Space>
          </Form.Item>
        </Form>
      </Modal>

      {/* Drawer chi tiết nhà cung cấp */}
      <Drawer
        title={
          <div style={{ display: 'flex', alignItems: 'center' }}>
            <EyeOutlined style={{ marginRight: 8 }} />
            Thông tin chi tiết nhà cung cấp
          </div>
        }
        placement="right"
        onClose={() => setIsDetailDrawerVisible(false)}
        open={isDetailDrawerVisible}
        width={500}
      >
        {selectedSupplier && (
          <div>
            <Card title="Thông tin cơ bản" size="small" style={{ marginBottom: 16 }}>
              <div style={{ lineHeight: '1.8' }}>
                <p><Text strong>Mã nhà cung cấp:</Text> {selectedSupplier.code}</p>
                <p><Text strong>Tên:</Text> {selectedSupplier.name}</p>
                <p><Text strong>Người liên hệ:</Text> {selectedSupplier.contact_person || 'Chưa có'}</p>
                <p><Text strong>Điện thoại:</Text> {selectedSupplier.phone || 'Chưa có'}</p>
                <p><Text strong>Email:</Text> {selectedSupplier.email || 'Chưa có'}</p>
                <p><Text strong>Mã số thuế:</Text> {selectedSupplier.tax_code || 'Chưa có'}</p>
              </div>
            </Card>

            <Card title="Thông tin tài chính" size="small" style={{ marginBottom: 16 }}>
              <Row gutter={16}>
                <Col span={12}>
                  <Statistic
                    title="Công nợ hiện tại"
                    value={selectedSupplier.total_debt || 0}
                    formatter={value => formatCurrency(value)}
                    valueStyle={{ 
                      color: selectedSupplier.total_debt > 0 ? '#ff4d4f' : '#52c41a',
                      fontSize: '18px'
                    }}
                  />
                </Col>
                <Col span={12}>
                  <Statistic
                    title="Hạn mức tín dụng"
                    value={selectedSupplier.credit_limit || 0}
                    formatter={value => formatCurrency(value)}
                    valueStyle={{ color: '#1890ff', fontSize: '18px' }}
                  />
                </Col>
              </Row>
              <p style={{ marginTop: 16 }}>
                <Text strong>Điều khoản thanh toán:</Text> {selectedSupplier.payment_terms || 'Không xác định'}
              </p>
            </Card>

            {(selectedSupplier.address || selectedSupplier.city) && (
              <Card title="Địa chỉ" size="small" style={{ marginBottom: 16 }}>
                {selectedSupplier.address && <p>{selectedSupplier.address}</p>}
                {selectedSupplier.city && <p><Text strong>Thành phố:</Text> {selectedSupplier.city}</p>}
              </Card>
            )}

            {selectedSupplier.notes && (
              <Card title="Ghi chú" size="small">
                <p>{selectedSupplier.notes}</p>
              </Card>
            )}
          </div>
        )}
      </Drawer>
    </div>
  );
};

export default SuppliersPage;
