// Comprehensive Website Analysis Tool - AI Powered
// Ph√¢n t√≠ch to√†n b·ªô website ƒë·ªÉ t√¨m l·ªói ti·ªÅm t√†ng v√† v·∫•n ƒë·ªÅ performance

class WebsiteAnalyzer {
  constructor() {
    this.analysisResults = [];
    this.isAnalyzing = false;
    this.performanceMetrics = {};
    this.codeQualityIssues = [];
    this.securityConcerns = [];
    this.uiUxProblems = [];
  }

  // ============================================
  // MAIN ANALYSIS ORCHESTRATOR
  // ============================================
  
  async analyzeEntireWebsite() {
    console.log('üîç AI Website Analyzer: Starting comprehensive analysis...');
    
    this.isAnalyzing = true;
    this.analysisResults = [];
    
    try {
      const results = await Promise.allSettled([
        this.analyzePerformance(),
        this.analyzeCodeQuality(),
        this.analyzeAPIs(),
        this.analyzeSecurity(),
        this.analyzeUIUX(),
        this.analyzeAccessibility(),
        this.analyzeConsoleErrors(),
        this.analyzeNetworkRequests(),
        this.analyzeLocalStorageUsage(),
        this.analyzeMemoryUsage()
      ]);
      
      console.log('üìä AI Analysis Complete. Processing results...');
      
      this.processAnalysisResults(results);
      this.generateComprehensiveReport();
      
      return this.getDetailedReport();
      
    } catch (error) {
      console.error('‚ùå Website analysis failed:', error);
      return { error: error.message };
    } finally {
      this.isAnalyzing = false;
    }
  }

  // ============================================
  // PERFORMANCE ANALYSIS
  // ============================================
  
  async analyzePerformance() {
    console.log('‚ö° Analyzing website performance...');
    
    const metrics = {
      pageLoadTime: 0,
      domContentLoaded: 0,
      firstContentfulPaint: 0,
      largestContentfulPaint: 0,
      cumulativeLayoutShift: 0,
      firstInputDelay: 0
    };
    
    // Measure current performance
    if (window.performance) {
      const navigation = performance.getEntriesByType('navigation')[0];
      if (navigation) {
        metrics.pageLoadTime = navigation.loadEventEnd - navigation.loadEventStart;
        metrics.domContentLoaded = navigation.domContentLoadedEventEnd - navigation.domContentLoadedEventStart;
      }
      
      // Web Vitals measurements
      if (window.PerformanceObserver) {
        try {
          // FCP measurement
          const paintEntries = performance.getEntriesByType('paint');
          const fcp = paintEntries.find(entry => entry.name === 'first-contentful-paint');
          if (fcp) metrics.firstContentfulPaint = fcp.startTime;
          
        } catch (error) {
          console.warn('Performance metrics partially unavailable:', error);
        }
      }
    }
    
    // Analyze bundle size
    const scripts = document.querySelectorAll('script[src]');
    let totalBundleSize = 0;
    
    scripts.forEach(script => {
      // Estimate bundle size (this would need actual network analysis for accuracy)
      totalBundleSize += 500; // Rough estimate in KB
    });
    
    const issues = [];
    
    if (metrics.pageLoadTime > 3000) {
      issues.push({
        type: 'performance',
        severity: 'high',
        issue: 'Slow page load time',
        value: `${metrics.pageLoadTime}ms`,
        recommendation: 'Consider code splitting, image optimization, and CDN usage'
      });
    }
    
    if (totalBundleSize > 1000) {
      issues.push({
        type: 'performance',
        severity: 'medium',
        issue: 'Large bundle size',
        value: `~${totalBundleSize}KB`,
        recommendation: 'Implement code splitting and tree shaking'
      });
    }
    
    this.performanceMetrics = metrics;
    return { metrics, issues };
  }

  // ============================================
  // CODE QUALITY ANALYSIS
  // ============================================
  
  async analyzeCodeQuality() {
    console.log('üîç Analyzing code quality patterns...');
    
    const issues = [];
    
    // Check for common anti-patterns in DOM
    const inlineStyles = document.querySelectorAll('[style]');
    if (inlineStyles.length > 10) {
      issues.push({
        type: 'code_quality',
        severity: 'medium',
        issue: 'Too many inline styles',
        count: inlineStyles.length,
        recommendation: 'Move styles to CSS files for better maintainability'
      });
    }
    
    // Check for memory leaks indicators
    const eventListeners = this.countEventListeners();
    if (eventListeners > 100) {
      issues.push({
        type: 'code_quality',
        severity: 'high',
        issue: 'Potential memory leaks',
        count: eventListeners,
        recommendation: 'Review event listener cleanup in useEffect returns'
      });
    }
    
    // Check for unused CSS
    const stylesheets = document.styleSheets;
    let unusedRules = 0;
    
    try {
      for (let stylesheet of stylesheets) {
        try {
          for (let rule of stylesheet.cssRules || stylesheet.rules) {
            if (rule.type === 1) { // STYLE_RULE
              const selector = rule.selectorText;
              if (selector && !document.querySelector(selector)) {
                unusedRules++;
              }
            }
          }
        } catch (e) {
          // Cross-origin stylesheet
          continue;
        }
      }
    } catch (error) {
      console.warn('CSS analysis limited due to cross-origin restrictions');
    }
    
    if (unusedRules > 50) {
      issues.push({
        type: 'code_quality',
        severity: 'medium',
        issue: 'Unused CSS rules',
        count: unusedRules,
        recommendation: 'Remove unused CSS to reduce bundle size'
      });
    }
    
    this.codeQualityIssues = issues;
    return issues;
  }

  // ============================================
  // API ANALYSIS
  // ============================================
  
  async analyzeAPIs() {
    console.log('üåê Analyzing API patterns and potential issues...');
    
    const issues = [];
    const apiEndpoints = [];
    
    // Intercept fetch requests (would need to be set up earlier)
    const originalFetch = window.fetch;
    let apiCallCount = 0;
    let failedCalls = 0;
    
    // Analyze existing API calls from performance entries
    const resourceEntries = performance.getEntriesByType('resource');
    
    resourceEntries.forEach(entry => {
      if (entry.name.includes('/api/')) {
        apiEndpoints.push({
          url: entry.name,
          duration: entry.duration,
          size: entry.transferSize,
          cached: entry.transferSize === 0
        });
        
        apiCallCount++;
        
        // Check for slow APIs
        if (entry.duration > 5000) {
          issues.push({
            type: 'api',
            severity: 'high',
            issue: 'Slow API response',
            url: entry.name,
            duration: `${entry.duration}ms`,
            recommendation: 'Consider caching, pagination, or endpoint optimization'
          });
        }
      }
    });
    
    // Check for too many concurrent API calls
    if (apiCallCount > 20) {
      issues.push({
        type: 'api',
        severity: 'medium',
        issue: 'Too many API calls',
        count: apiCallCount,
        recommendation: 'Implement request batching or reduce unnecessary calls'
      });
    }
    
    return { endpoints: apiEndpoints, issues };
  }

  // ============================================
  // SECURITY ANALYSIS
  // ============================================
  
  async analyzeSecurity() {
    console.log('üîí Analyzing security concerns...');
    
    const issues = [];
    
    // Check for sensitive data in localStorage
    for (let i = 0; i < localStorage.length; i++) {
      const key = localStorage.key(i);
      const value = localStorage.getItem(key);
      
      if (key && (key.includes('token') || key.includes('password') || key.includes('secret'))) {
        issues.push({
          type: 'security',
          severity: 'high',
          issue: 'Sensitive data in localStorage',
          key: key,
          recommendation: 'Use secure storage methods or session storage for sensitive data'
        });
      }
    }
    
    // Check for inline JavaScript
    const inlineScripts = document.querySelectorAll('script:not([src])');
    if (inlineScripts.length > 5) {
      issues.push({
        type: 'security',
        severity: 'medium',
        issue: 'Multiple inline scripts',
        count: inlineScripts.length,
        recommendation: 'Move scripts to external files and implement CSP'
      });
    }
    
    // Check for external links without rel="noopener"
    const externalLinks = document.querySelectorAll('a[href^="http"]:not([rel*="noopener"])');
    if (externalLinks.length > 0) {
      issues.push({
        type: 'security',
        severity: 'low',
        issue: 'External links without rel="noopener"',
        count: externalLinks.length,
        recommendation: 'Add rel="noopener" to external links for security'
      });
    }
    
    this.securityConcerns = issues;
    return issues;
  }

  // ============================================
  // UI/UX ANALYSIS
  // ============================================
  
  async analyzeUIUX() {
    console.log('üé® Analyzing UI/UX patterns...');
    
    const issues = [];
    
    // Check for missing alt texts
    const images = document.querySelectorAll('img:not([alt])');
    if (images.length > 0) {
      issues.push({
        type: 'ui_ux',
        severity: 'medium',
        issue: 'Images without alt text',
        count: images.length,
        recommendation: 'Add descriptive alt text for accessibility'
      });
    }
    
    // Check for very small clickable elements
    const buttons = document.querySelectorAll('button, a, [onclick]');
    let smallElements = 0;
    
    buttons.forEach(element => {
      const rect = element.getBoundingClientRect();
      if (rect.width < 44 || rect.height < 44) {
        smallElements++;
      }
    });
    
    if (smallElements > 0) {
      issues.push({
        type: 'ui_ux',
        severity: 'medium',
        issue: 'Small clickable elements',
        count: smallElements,
        recommendation: 'Ensure clickable elements are at least 44x44px for mobile usability'
      });
    }
    
    this.uiUxProblems = issues;
    return issues;
  }

  // ============================================
  // ACCESSIBILITY ANALYSIS
  // ============================================
  
  async analyzeAccessibility() {
    console.log('‚ôø Analyzing accessibility...');
    
    const issues = [];
    
    // Check for missing headings hierarchy
    const headings = document.querySelectorAll('h1, h2, h3, h4, h5, h6');
    if (headings.length === 0) {
      issues.push({
        type: 'accessibility',
        severity: 'high',
        issue: 'No heading structure',
        recommendation: 'Add proper heading hierarchy for screen readers'
      });
    }
    
    // Check for forms without labels
    const inputs = document.querySelectorAll('input:not([type="hidden"])');
    let unlabeledInputs = 0;
    
    inputs.forEach(input => {
      const label = document.querySelector(`label[for="${input.id}"]`);
      const ariaLabel = input.getAttribute('aria-label');
      
      if (!label && !ariaLabel && !input.closest('label')) {
        unlabeledInputs++;
      }
    });
    
    if (unlabeledInputs > 0) {
      issues.push({
        type: 'accessibility',
        severity: 'high',
        issue: 'Form inputs without labels',
        count: unlabeledInputs,
        recommendation: 'Add proper labels or aria-label attributes'
      });
    }
    
    return issues;
  }

  // ============================================
  // CONSOLE ERRORS ANALYSIS
  // ============================================
  
  async analyzeConsoleErrors() {
    console.log('üìú Analyzing console errors...');
    
    // This would typically be collected over time
    const errorPatterns = [
      { pattern: /Failed to load resource/, severity: 'medium', type: 'Resource loading' },
      { pattern: /Cannot read property.*null/, severity: 'high', type: 'Null reference' },
      { pattern: /React.*Warning/, severity: 'low', type: 'React warning' },
      { pattern: /404.*Not Found/, severity: 'medium', type: '404 Error' }
    ];
    
    return {
      patterns: errorPatterns,
      recommendation: 'Monitor console errors and implement proper error boundaries'
    };
  }

  // ============================================
  // NETWORK REQUESTS ANALYSIS
  // ============================================
  
  async analyzeNetworkRequests() {
    console.log('üåê Analyzing network requests...');
    
    const resources = performance.getEntriesByType('resource');
    const analysis = {
      totalRequests: resources.length,
      totalSize: 0,
      slowRequests: [],
      largeRequests: [],
      failedRequests: []
    };
    
    resources.forEach(resource => {
      analysis.totalSize += resource.transferSize || 0;
      
      if (resource.duration > 3000) {
        analysis.slowRequests.push({
          url: resource.name,
          duration: resource.duration
        });
      }
      
      if (resource.transferSize > 1000000) { // 1MB
        analysis.largeRequests.push({
          url: resource.name,
          size: resource.transferSize
        });
      }
    });
    
    return analysis;
  }

  // ============================================
  // MEMORY USAGE ANALYSIS
  // ============================================
  
  async analyzeMemoryUsage() {
    console.log('üß† Analyzing memory usage...');
    
    const memoryInfo = {};
    
    if (performance.memory) {
      memoryInfo.used = performance.memory.usedJSHeapSize;
      memoryInfo.total = performance.memory.totalJSHeapSize;
      memoryInfo.limit = performance.memory.jsHeapSizeLimit;
      memoryInfo.usage = ((memoryInfo.used / memoryInfo.limit) * 100).toFixed(2);
    }
    
    return memoryInfo;
  }

  // ============================================
  // UTILITY METHODS
  // ============================================
  
  countEventListeners() {
    // This is a simplified estimation
    const elements = document.querySelectorAll('*');
    let count = 0;
    
    elements.forEach(element => {
      const events = Object.getOwnPropertyNames(element).filter(prop => 
        prop.startsWith('on') && typeof element[prop] === 'function'
      );
      count += events.length;
    });
    
    return count;
  }
  
  analyzeLocalStorageUsage() {
    const usage = {
      itemCount: localStorage.length,
      estimatedSize: 0,
      items: []
    };
    
    for (let i = 0; i < localStorage.length; i++) {
      const key = localStorage.key(i);
      const value = localStorage.getItem(key);
      const size = new Blob([value]).size;
      
      usage.estimatedSize += size;
      usage.items.push({ key, size });
    }
    
    return usage;
  }

  processAnalysisResults(results) {
    console.log('üìä Processing analysis results...');
    
    results.forEach((result, index) => {
      if (result.status === 'fulfilled') {
        this.analysisResults.push(result.value);
      } else {
        console.warn(`Analysis ${index} failed:`, result.reason);
      }
    });
  }

  generateComprehensiveReport() {
    console.log('üìù Generating comprehensive report...');
    
    const allIssues = this.analysisResults.flat().filter(item => 
      Array.isArray(item) ? item : item.issues || []
    ).flat();
    
    const summary = {
      totalIssues: allIssues.length,
      criticalIssues: allIssues.filter(issue => issue.severity === 'high').length,
      mediumIssues: allIssues.filter(issue => issue.severity === 'medium').length,
      lowIssues: allIssues.filter(issue => issue.severity === 'low').length,
      categories: {
        performance: allIssues.filter(issue => issue.type === 'performance').length,
        security: allIssues.filter(issue => issue.type === 'security').length,
        accessibility: allIssues.filter(issue => issue.type === 'accessibility').length,
        ui_ux: allIssues.filter(issue => issue.type === 'ui_ux').length,
        code_quality: allIssues.filter(issue => issue.type === 'code_quality').length
      }
    };
    
    console.log('üéØ Analysis Summary:', summary);
    return summary;
  }

  getDetailedReport() {
    return {
      summary: this.generateComprehensiveReport(),
      performance: this.performanceMetrics,
      issues: {
        codeQuality: this.codeQualityIssues,
        security: this.securityConcerns,
        uiUx: this.uiUxProblems
      },
      allResults: this.analysisResults,
      timestamp: new Date().toISOString(),
      isAnalyzing: this.isAnalyzing
    };
  }
}

