import React, { useEffect, useRef, useState, useCallback } from 'react';
import { Modal, Button, Space, message, Alert, Typography, Divider } from 'antd';
import { CameraOutlined, CloseOutlined, ReloadOutlined, ExclamationCircleOutlined, CheckCircleOutlined } from '@ant-design/icons';
import { Html5QrcodeScanner } from 'html5-qrcode';

const { Text } = Typography;

// Define supported formats manually to avoid import issues
const SUPPORTED_FORMATS = {
  QR_CODE: 0,
  AZTEC: 1,
  CODABAR: 2,
  CODE_39: 3,
  CODE_93: 4,
  CODE_128: 5,
  DATA_MATRIX: 6,
  MAXICODE: 7,
  ITF: 8,
  EAN_13: 9,
  EAN_8: 10,
  PDF_417: 11,
  RSS_14: 12,
  RSS_EXPANDED: 13,
  UPC_A: 14,
  UPC_E: 15,
  UPC_EAN_EXTENSION: 16
};

const QRScanner = ({ 
  visible, 
  onClose, 
  onScan, 
  title = "Qu√©t Serial Number",
  placeholder = "ƒê∆∞a camera v√†o m√£ QR ho·∫∑c Barcode" 
}) => {
  const scannerContainerRef = useRef(null);
  const [isScanning, setIsScanning] = useState(false);
  const [error, setError] = useState(null);
  const [scannerInstance, setScannerInstance] = useState(null);
  const [permissionState, setPermissionState] = useState('unknown'); // 'granted', 'denied', 'prompt', 'unknown'
  const [cameraDevices, setCameraDevices] = useState([]);
  const [selectedCameraId, setSelectedCameraId] = useState(null);
  const [isClosing, setIsClosing] = useState(false);

  // üîß FIXED: Proper cleanup with useCallback to prevent memory leaks
  const stopScanner = useCallback(async () => {
    try {
      if (scannerInstance) {
        // Only clear if container still exists in DOM
        const container = scannerContainerRef.current;
        if (container && container.childNodes.length > 0) {
          await scannerInstance.clear();
        }
        setScannerInstance(null);
      }
      setIsScanning(false);
    } catch (err) {
      console.error('Error stopping scanner:', err);
      // Even if clear fails, reset the instance to prevent further errors
      setScannerInstance(null);
      setIsScanning(false);
    }
  }, [scannerInstance]);

  // üîß FIXED: Enhanced useEffect with proper cleanup
  useEffect(() => {
    let mounted = true;
    if (visible && !isClosing) {
      console.log('üöÄ QR Scanner opening...');
      checkCameraPermission();
    } else if (!visible && scannerInstance) {
      console.log('üîÑ QR Scanner closing...');
      stopScanner();
    }

    // üîß FIXED: Cleanup on unmount
    return () => {
      mounted = false;
      if (scannerInstance) {
        console.log('üßπ Cleaning up QR scanner on unmount...');
        stopScanner();
      }
    };
  }, [visible, isClosing, scannerInstance, stopScanner]);

  // üîß FIXED: Check camera permission with better error handling
  const checkCameraPermission = async () => {
    try {
      // Check if getUserMedia is available
      if (!navigator.mediaDevices || !navigator.mediaDevices.getUserMedia) {
        setError('Tr√¨nh duy·ªát kh√¥ng h·ªó tr·ª£ truy c·∫≠p camera. Vui l√≤ng s·ª≠ d·ª•ng tr√¨nh duy·ªát hi·ªán ƒë·∫°i.');
        return;
      }

      // Check permission API if available
      if (navigator.permissions) {
        try {
          const permission = await navigator.permissions.query({ name: 'camera' });
          setPermissionState(permission.state);
          
          permission.onchange = () => {
            setPermissionState(permission.state);
          };
        } catch (err) {
          console.log('Permission API not fully supported');
        }
      }

      // Get camera devices
      await getCameraDevices();
      
      // Try to start scanner
      await startScanner();
      
    } catch (err) {
      console.error('Camera permission check failed:', err);
      handleCameraError(err);
    }
  };

  // Get available camera devices
  const getCameraDevices = async () => {
    try {
      const devices = await navigator.mediaDevices.enumerateDevices();
      const videoDevices = devices.filter(device => device.kind === 'videoinput');
      setCameraDevices(videoDevices);
      
      // Prefer back camera on mobile
      const backCamera = videoDevices.find(device => 
        device.label.toLowerCase().includes('back') || 
        device.label.toLowerCase().includes('rear') ||
        device.label.toLowerCase().includes('environment')
      );
      
      if (backCamera) {
        setSelectedCameraId(backCamera.deviceId);
      } else if (videoDevices.length > 0) {
        setSelectedCameraId(videoDevices[0].deviceId);
      }
    } catch (err) {
      console.error('Error getting camera devices:', err);
    }
  };

  // Request camera permission explicitly
  const requestCameraPermission = async () => {
    try {
      setError(null);
      const stream = await navigator.mediaDevices.getUserMedia({ 
        video: { 
          facingMode: 'environment' // Prefer back camera
        } 
      });
      
      // Stop the stream immediately as we just needed permission
      stream.getTracks().forEach(track => track.stop());
      
      setPermissionState('granted');
      message.success('‚úÖ ƒê√£ c·∫•p quy·ªÅn truy c·∫≠p camera!');
      
      // Now start the scanner
      await startScanner();
      
    } catch (err) {
      console.error('Camera permission denied:', err);
      setPermissionState('denied');
      handleCameraError(err);
    }
  };

  const handleCameraError = (err) => {
    if (err.name === 'NotAllowedError' || err.name === 'PermissionDeniedError') {
      setError('Quy·ªÅn truy c·∫≠p camera b·ªã t·ª´ ch·ªëi. Vui l√≤ng cho ph√©p truy c·∫≠p camera trong c√†i ƒë·∫∑t tr√¨nh duy·ªát.');
      setPermissionState('denied');
    } else if (err.name === 'NotFoundError' || err.name === 'DevicesNotFoundError') {
      setError('Kh√¥ng t√¨m th·∫•y camera. Vui l√≤ng ki·ªÉm tra k·∫øt n·ªëi camera.');
    } else if (err.name === 'NotSupportedError') {
      setError('Tr√¨nh duy·ªát kh√¥ng h·ªó tr·ª£ truy c·∫≠p camera.');
    } else if (err.name === 'NotReadableError') {
      setError('Camera ƒëang ƒë∆∞·ª£c s·ª≠ d·ª•ng b·ªüi ·ª©ng d·ª•ng kh√°c.');
    } else {
      setError(`L·ªói camera: ${err.message || 'Kh√¥ng th·ªÉ truy c·∫≠p camera'}`);
    }
  };

  // üîß FIXED: Enhanced startScanner with better error handling and DOM checks
  const startScanner = async () => {
    try {
      setError(null);
      setIsScanning(true);

      // üîß FIXED: Clear any existing scanner first
      if (scannerInstance) {
        await stopScanner();
        // Wait a bit for cleanup to complete
        await new Promise(resolve => setTimeout(resolve, 100));
      }

      // üîß FIXED: Check if DOM element exists before creating scanner
      const container = scannerContainerRef.current;
      if (!container) {
        console.error('QR reader element not found');
        setError('Kh√¥ng th·ªÉ kh·ªüi t·∫°o scanner. Vui l√≤ng th·ª≠ l·∫°i.');
        setIsScanning(false);
        return;
      }

      // Enhanced configuration for better mobile support
      const config = {
        fps: 10,
        qrbox: function(viewfinderWidth, viewfinderHeight) {
          // Make qrbox responsive
          const minEdgePercentage = 0.7;
          const minEdgeSize = Math.min(viewfinderWidth, viewfinderHeight);
          const qrboxSize = Math.floor(minEdgeSize * minEdgePercentage);
          return {
            width: qrboxSize,
            height: qrboxSize
          };
        },
        aspectRatio: 1.0,
        disableFlip: false,
        // Camera constraints for mobile
        videoConstraints: {
          facingMode: 'environment', // Use back camera
          advanced: [
            { focusMode: 'continuous' },
            { exposureMode: 'continuous' },
            { whiteBalanceMode: 'continuous' }
          ]
        },
        // Support multiple formats using our constants
        formatsToSupport: [
          SUPPORTED_FORMATS.QR_CODE,
          SUPPORTED_FORMATS.CODE_128,
          SUPPORTED_FORMATS.CODE_39,
          SUPPORTED_FORMATS.CODE_93,
          SUPPORTED_FORMATS.EAN_13,
          SUPPORTED_FORMATS.EAN_8,
          SUPPORTED_FORMATS.UPC_A,
          SUPPORTED_FORMATS.UPC_E
        ]
      };

      console.log('üîß Creating new QR scanner instance...');
      const scanner = new Html5QrcodeScanner(
        container,
        config,
        false // verbose logging
      );

      scanner.render(
        (decodedText, decodedResult) => {
          // Success callback
          console.log('‚úÖ QR Code scanned successfully:', decodedText);
          message.success(`üì± ƒê√£ qu√©t th√†nh c√¥ng: ${decodedText}`);
          onScan(decodedText);
          handleClose();
        },
        (error) => {
          // Error callback - only log, don't show to user
          if (error && !error.includes('NotFoundException')) {
            console.warn(`QR scan error: ${error}`);
          }
        }
      );

      setScannerInstance(scanner);
      setPermissionState('granted');
      console.log('‚úÖ QR scanner started successfully');

    } catch (err) {
      console.error('Scanner initialization error:', err);
      handleCameraError(err);
      setIsScanning(false);
    }
  };

  // üîß FIXED: Enhanced handleClose with proper cleanup sequence
  const handleClose = async () => {
    console.log('üîÑ Closing QR scanner...');
    setIsClosing(true);
    
    try {
      // Stop scanner first
      await stopScanner();
      
      // Reset states
      setError(null);
      setPermissionState('unknown');
      setIsClosing(false);
      
      // Close modal
      onClose();
      
      console.log('‚úÖ QR scanner closed successfully');
    } catch (err) {
      console.error('Error during close:', err);
      setIsClosing(false);
      onClose();
    }
  };

  const handleRestart = async () => {
    console.log('üîÑ Restarting QR scanner...');
    setError(null);
    await stopScanner();
    
    // Wait a bit before restarting
    setTimeout(() => {
      checkCameraPermission();
    }, 500);
  };

  // Render permission request UI
  const renderPermissionRequest = () => (
    <div style={{ textAlign: 'center', padding: '40px 20px' }}>
      <div style={{ fontSize: '64px', marginBottom: '20px' }}>üì±</div>
      <div style={{ fontSize: '18px', fontWeight: 'bold', marginBottom: '12px' }}>
        C·∫ßn quy·ªÅn truy c·∫≠p Camera
      </div>
      <div style={{ fontSize: '14px', color: '#666', marginBottom: '24px', lineHeight: '1.5' }}>
        ƒê·ªÉ qu√©t m√£ QR/Barcode, ·ª©ng d·ª•ng c·∫ßn quy·ªÅn truy c·∫≠p camera c·ªßa thi·∫øt b·ªã.
        <br />
        Vui l√≤ng b·∫•m "Cho ph√©p" khi tr√¨nh duy·ªát h·ªèi.
      </div>
      <Button 
        type="primary" 
        size="large"
        icon={<CameraOutlined />}
        onClick={requestCameraPermission}
        style={{ 
          background: '#52c41a',
          borderColor: '#52c41a',
          height: '48px',
          fontSize: '16px',
          fontWeight: 'bold'
        }}
      >
        üîì C·∫•p quy·ªÅn Camera
      </Button>
      
      <Divider />
      
      <div style={{ fontSize: '12px', color: '#999', textAlign: 'left' }}>
        <div style={{ marginBottom: '8px', fontWeight: 'bold' }}>üí° L∆∞u √Ω:</div>
        <div>‚Ä¢ Tr√™n <strong>iPhone/iPad</strong>: B·∫•m "Cho ph√©p" khi Safari h·ªèi</div>
        <div>‚Ä¢ Tr√™n <strong>Android</strong>: B·∫•m "Allow" khi Chrome h·ªèi</div>
        <div>‚Ä¢ N·∫øu ƒë√£ t·ª´ ch·ªëi: V√†o Settings ‚Üí Camera ‚Üí Cho ph√©p trang web n√†y</div>
      </div>
    </div>
  );

  return (
    <Modal
      title={
        <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
          <CameraOutlined style={{ color: '#1890ff' }} />
          <span>{title}</span>
          {permissionState === 'granted' && (
            <CheckCircleOutlined style={{ color: '#52c41a', marginLeft: '8px' }} />
          )}
        </div>
      }
      open={visible}
      onCancel={handleClose}
      width={Math.min(600, window.innerWidth - 32)}
      footer={
        <Space>
          <Button onClick={handleRestart} icon={<ReloadOutlined />} disabled={isClosing}>
            Th·ª≠ l·∫°i
          </Button>
          <Button onClick={handleClose} icon={<CloseOutlined />} disabled={isClosing}>
            ƒê√≥ng
          </Button>
        </Space>
      }
      // üîß FIXED: Removed destroyOnClose to prevent DOM removal conflicts
      centered
      style={{ top: 20 }}
      confirmLoading={isClosing}
    >
      <div style={{ textAlign: 'center', padding: '20px 0' }}>
        {/* Permission request UI */}
        {(permissionState === 'denied' || permissionState === 'prompt' || permissionState === 'unknown') && !scannerInstance && (
          renderPermissionRequest()
        )}

        {/* Error display */}
        {error && (
          <Alert
            message="L·ªói Camera"
            description={
              <div>
                <div>{error}</div>
                <div style={{ marginTop: '12px', fontSize: '12px' }}>
                  <strong>C√°ch kh·∫Øc ph·ª•c:</strong>
                  <br />‚Ä¢ ƒê·∫£m b·∫£o trang web ƒë∆∞·ª£c truy c·∫≠p qua HTTPS
                  <br />‚Ä¢ Ki·ªÉm tra c√†i ƒë·∫∑t quy·ªÅn camera trong tr√¨nh duy·ªát
                  <br />‚Ä¢ Th·ª≠ t·∫£i l·∫°i trang v√† c·∫•p quy·ªÅn l·∫°i
                </div>
              </div>
            }
            type="error"
            showIcon
            style={{ marginBottom: 16, textAlign: 'left' }}
            action={
              <Button size="small" onClick={requestCameraPermission}>
                Th·ª≠ l·∫°i
              </Button>
            }
          />
        )}

        {/* Success message when camera is ready */}
        {permissionState === 'granted' && !error && (
          <Alert
            message="Camera ƒë√£ s·∫µn s√†ng"
            description={placeholder}
            type="success"
            showIcon
            style={{ marginBottom: 16 }}
          />
        )}

        {/* Scanner container */}
        {permissionState === 'granted' && !error && (
          <div 
            ref={scannerContainerRef}
            style={{ 
              width: '100%',
              maxWidth: '500px',
              margin: '0 auto',
              border: '2px dashed #52c41a',
              borderRadius: '8px',
              minHeight: '300px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center'
            }}
          >
            {!scannerInstance && (
              <div style={{ textAlign: 'center', color: '#999' }}>
                <CameraOutlined style={{ fontSize: '48px', marginBottom: '16px' }} />
                <div>ƒêang kh·ªüi t·∫°o camera...</div>
              </div>
            )}
          </div>
        )}

        <div style={{ marginTop: 16, fontSize: '12px', color: '#666' }}>
          <Text type="secondary">
            üí° H·ªó tr·ª£ qu√©t: QR Code, Code 128, Code 39, EAN-13, UPC-A v√† nhi·ªÅu ƒë·ªãnh d·∫°ng kh√°c
          </Text>
        </div>
      </div>
    </Modal>
  );
};

export default QRScanner;
