import React, { useState, useEffect } from 'react';
import {
  Card,
  Table,
  Button,
  Modal,
  Form,
  Input,
  Select,
  DatePicker,
  Space,
  Tag,
  Row,
  Col,
  Statistic,
  Tabs,
  message,
  InputNumber,
  Radio,
} from 'antd';
import {
  PlusOutlined,
  FilterOutlined,
  DownloadOutlined,
  DollarOutlined,
  TrendingUpOutlined,
  TrendingDownOutlined,
} from '@ant-design/icons';
import { financialAPI, reportsAPI } from '../services/api';
import moment from 'moment';

const { Option } = Select;
const { RangePicker } = DatePicker;
const { TabPane } = Tabs;

const FinancialPage = () => {
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [modalVisible, setModalVisible] = useState(false);
  const [form] = Form.useForm();
  const [stats, setStats] = useState({
    totalIncome: 0,
    totalExpense: 0,
    netProfit: 0,
    todayIncome: 0,
    monthIncome: 0,
  });
  const [activeTab, setActiveTab] = useState('transactions');

  const categories = {
    income: [
      'Bán hàng',
      'Dịch vụ',
      'Thu khác',
      'Lãi ngân hàng',
      'Hoàn tiền',
    ],
    expense: [
      'Nhập hàng',
      'Tiền thuê',
      'Điện nước',
      'Lương nhân viên',
      'Marketing',
      'Vận chuyển',
      'Bảo trì sửa chữa',
      'Chi phí khác',
    ],
  };

  useEffect(() => {
    fetchTransactions();
    fetchStats();
  }, []);

  const fetchTransactions = async () => {
    setLoading(true);
    try {
      const response = await financialAPI.getTransactions();
      if (response.data.success) {
        setTransactions(response.data.data);
      }
    } catch (error) {
      message.error('Lỗi khi tải dữ liệu thu chi');
    } finally {
      setLoading(false);
    }
  };

  const fetchStats = async () => {
    try {
      const response = await reportsAPI.getFinancialSummary();
      if (response.data.success) {
        setStats(response.data.data);
      }
    } catch (error) {
      console.error('Error fetching stats:', error);
    }
  };

  const handleAddTransaction = async (values) => {
    try {
      const transactionData = {
        ...values,
        transaction_date: values.transaction_date?.toISOString() || new Date().toISOString(),
        user_id: 1, // Current user ID
      };

      const response = await financialAPI.create(transactionData);
      if (response.data.success) {
        message.success('Thêm giao dịch thành công');
        setModalVisible(false);
        form.resetFields();
        fetchTransactions();
        fetchStats();
      }
    } catch (error) {
      message.error('Lỗi khi thêm giao dịch');
    }
  };

  const columns = [
    {
      title: 'Ngày',
      dataIndex: 'transaction_date',
      key: 'transaction_date',
      render: (date) => moment(date).format('DD/MM/YYYY HH:mm'),
      sorter: (a, b) => moment(a.transaction_date).unix() - moment(b.transaction_date).unix(),
    },
    {
      title: 'Loại',
      dataIndex: 'type',
      key: 'type',
      render: (type) => (
        <Tag color={type === 'income' ? 'green' : 'red'}>
          {type === 'income' ? 'Thu' : 'Chi'}
        </Tag>
      ),
      filters: [
        { text: 'Thu', value: 'income' },
        { text: 'Chi', value: 'expense' },
      ],
      onFilter: (value, record) => record.type === value,
    },
    {
      title: 'Danh mục',
      dataIndex: 'category',
      key: 'category',
    },
    {
      title: 'Số tiền',
      dataIndex: 'amount',
      key: 'amount',
      render: (amount, record) => (
        <span style={{ color: record.type === 'income' ? '#52c41a' : '#ff4d4f' }}>
          {record.type === 'income' ? '+' : '-'}{amount.toLocaleString('vi-VN')} ₫
        </span>
      ),
      sorter: (a, b) => a.amount - b.amount,
    },
    {
      title: 'Mô tả',
      dataIndex: 'description',
      key: 'description',
      ellipsis: true,
    },
    {
      title: 'Phương thức TT',
      dataIndex: 'payment_method',
      key: 'payment_method',
      render: (method) => {
        const methodMap = {
          cash: 'Tiền mặt',
          card: 'Thẻ',
          transfer: 'Chuyển khoản',
          ewallet: 'Ví điện tử',
        };
        return methodMap[method] || method;
      },
    },
    {
      title: 'Người tạo',
      dataIndex: 'user_name',
      key: 'user_name',
      render: (name) => name || 'Chưa xác định',
    },
  ];

  const renderTransactionsTab = () => (
    <div>
      <Row gutter={16} style={{ marginBottom: 16 }}>
        <Col span={6}>
          <Card>
            <Statistic
              title="Tổng thu"
              value={stats.totalIncome}
              precision={0}
              valueStyle={{ color: '#3f8600' }}
              prefix={<TrendingUpOutlined />}
              suffix="₫"
            />
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <Statistic
              title="Tổng chi"
              value={stats.totalExpense}
              precision={0}
              valueStyle={{ color: '#cf1322' }}
              prefix={<TrendingDownOutlined />}
              suffix="₫"
            />
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <Statistic
              title="Lợi nhuận"
              value={stats.netProfit}
              precision={0}
              valueStyle={{ color: stats.netProfit >= 0 ? '#3f8600' : '#cf1322' }}
              prefix={<DollarOutlined />}
              suffix="₫"
            />
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <Statistic
              title="Thu hôm nay"
              value={stats.todayIncome}
              precision={0}
              valueStyle={{ color: '#1890ff' }}
              suffix="₫"
            />
          </Card>
        </Col>
      </Row>

      <Card>
        <div style={{ marginBottom: 16, display: 'flex', justifyContent: 'space-between' }}>
          <Space>
            <Button
              type="primary"
              icon={<PlusOutlined />}
              onClick={() => setModalVisible(true)}
            >
              Thêm giao dịch
            </Button>
            <Button icon={<FilterOutlined />}>
              Lọc
            </Button>
          </Space>
          <Button icon={<DownloadOutlined />}>
            Xuất báo cáo
          </Button>
        </div>
        
        <Table
          columns={columns}
          dataSource={transactions}
          rowKey="id"
          loading={loading}
          pagination={{
            pageSize: 20,
            showSizeChanger: true,
            showQuickJumper: true,
            showTotal: (total, range) =>
              `${range[0]}-${range[1]} của ${total} giao dịch`,
          }}
        />
      </Card>
    </div>
  );

  return (
    <div style={{ padding: '24px' }}>
      <div style={{ marginBottom: 24 }}>
        <h2>Quản lý tài chính</h2>
        <p>Theo dõi thu chi, lợi nhuận và tài chính doanh nghiệp</p>
      </div>

      <Tabs activeKey={activeTab} onChange={setActiveTab}>
        <TabPane tab="Giao dịch thu chi" key="transactions">
          {renderTransactionsTab()}
        </TabPane>
        <TabPane tab="Báo cáo tài chính" key="reports">
          <Card>
            <h3>Báo cáo tài chính chi tiết</h3>
            <p>Tính năng này sẽ được phát triển...</p>
          </Card>
        </TabPane>
        <TabPane tab="Ngân sách" key="budget">
          <Card>
            <h3>Quản lý ngân sách</h3>
            <p>Tính năng này sẽ được phát triển...</p>
          </Card>
        </TabPane>
      </Tabs>

      <Modal
        title="Thêm giao dịch thu chi"
        visible={modalVisible}
        onCancel={() => {
          setModalVisible(false);
          form.resetFields();
        }}
        footer={null}
        width={600}
      >
        <Form
          form={form}
          layout="vertical"
          onFinish={handleAddTransaction}
          initialValues={{
            type: 'expense',
            payment_method: 'cash',
            transaction_date: moment(),
          }}
        >
          <Row gutter={16}>
            <Col span={12}>
              <Form.Item
                name="type"
                label="Loại giao dịch"
                rules={[{ required: true, message: 'Vui lòng chọn loại giao dịch' }]}
              >
                <Radio.Group>
                  <Radio.Button value="income">Thu</Radio.Button>
                  <Radio.Button value="expense">Chi</Radio.Button>
                </Radio.Group>
              </Form.Item>
            </Col>
            <Col span={12}>
              <Form.Item
                name="amount"
                label="Số tiền"
                rules={[{ required: true, message: 'Vui lòng nhập số tiền' }]}
              >
                <InputNumber
                  style={{ width: '100%' }}
                  placeholder="Nhập số tiền"
                  formatter={(value) => `${value}`.replace(/\B(?=(\d{3})+(?!\d))/g, ',')}
                  parser={(value) => value.replace(/\$\s?|(,*)/g, '')}
                  min={0}
                />
              </Form.Item>
            </Col>
          </Row>

          <Form.Item shouldUpdate={(prevValues, currentValues) => prevValues.type !== currentValues.type}>
            {({ getFieldValue }) => {
              const transactionType = getFieldValue('type');
              return (
                <Form.Item
                  name="category"
                  label="Danh mục"
                  rules={[{ required: true, message: 'Vui lòng chọn danh mục' }]}
                >
                  <Select placeholder="Chọn danh mục">
                    {categories[transactionType]?.map(category => (
                      <Option key={category} value={category}>
                        {category}
                      </Option>
                    ))}
                  </Select>
                </Form.Item>
              );
            }}
          </Form.Item>

          <Form.Item
            name="description"
            label="Mô tả"
            rules={[{ required: true, message: 'Vui lòng nhập mô tả' }]}
          >
            <Input.TextArea rows={3} placeholder="Mô tả chi tiết giao dịch" />
          </Form.Item>

          <Row gutter={16}>
            <Col span={12}>
              <Form.Item
                name="payment_method"
                label="Phương thức thanh toán"
                rules={[{ required: true, message: 'Vui lòng chọn phương thức' }]}
              >
                <Select>
                  <Option value="cash">Tiền mặt</Option>
                  <Option value="card">Thẻ</Option>
                  <Option value="transfer">Chuyển khoản</Option>
                  <Option value="ewallet">Ví điện tử</Option>
                </Select>
              </Form.Item>
            </Col>
            <Col span={12}>
              <Form.Item
                name="transaction_date"
                label="Ngày giao dịch"
                rules={[{ required: true, message: 'Vui lòng chọn ngày' }]}
              >
                <DatePicker
                  style={{ width: '100%' }}
                  showTime
                  format="DD/MM/YYYY HH:mm"
                />
              </Form.Item>
            </Col>
          </Row>

          <Form.Item name="receipt_number" label="Số hóa đơn/chứng từ">
            <Input placeholder="Số hóa đơn hoặc chứng từ (tùy chọn)" />
          </Form.Item>

          <div style={{ textAlign: 'right' }}>
            <Space>
              <Button onClick={() => setModalVisible(false)}>
                Hủy
              </Button>
              <Button type="primary" htmlType="submit">
                Thêm giao dịch
              </Button>
            </Space>
          </div>
        </Form>
      </Modal>
    </div>
  );
};

export default FinancialPage;
